(385, 'Given a string consisting of whitespace and groups of matched parentheses, write a Python program to split it into groups of perfectly matched parentheses without any whitespace.', 'given-a-string-consisting-of-whitespace-and-groups-of-matched-parentheses-write-a-python-program-to-split-it-into-groups-of-perfectly-matched-parentheses-without-any-whitespace', '<p>def test(combined):</p><p>&nbsp; &nbsp; ls = []</p><p>&nbsp; &nbsp; s2 = \"\"</p><p>&nbsp; &nbsp; for s in combined.replace(\' \', \'\'):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s2 += s</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if s2.count(\"(\") == s2.count(\")\"):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ls.append(s2)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s2 = \"\"</p><p>&nbsp; &nbsp; return ls</p><p><br></p><p>combined = \'( ()) ((()()())) (()) ()\'</p><p>print(\"Parentheses string:\")</p><p>print(combined)</p><p>print(\"Separate parentheses groups of the said string:\")</p><p>print(test(combined))</p><p><br></p><p>combined = \'() (( ( )() (&nbsp; )) ) ( ())\'</p><p>print(\"\\nParentheses string:\")</p><p>print(combined)</p><p>print(\"Separate parentheses groups of the said string:\")</p><p>print(test(combined))</p><div><br></div>', 'def test(combined):\r\n    ls = []\r\n    s2 = \"\"\r\n    for s in combined.replace(\' \', \'\'):\r\n        s2 += s\r\n        if s2.count(\"(\") == s2.count(\")\"):\r\n            ls.append(s2)\r\n            s2 = \"\"\r\n    return ls\r\n\r\ncombined = \'( ()) ((()()())) (()) ()\'\r\nprint(\"Parentheses string:\")\r\nprint(combined)\r\nprint(\"Separate parentheses groups of the said string:\")\r\nprint(test(combined))\r\n\r\ncombined = \'() (( ( )() (  )) ) ( ())\'\r\nprint(\"\\nParentheses string:\")\r\nprint(combined)\r\nprint(\"Separate parentheses groups of the said string:\")\r\nprint(test(combined))', 2, 0, '2023-09-02 15:11:12', '2023-09-02 09:11:12', '2023-09-02 09:11:25', 1),
(386, 'Write a Python program to find the indexes of numbers in a given list below a given threshold.', 'write-a-python-program-to-find-the-indexes-of-numbers-in-a-given-list-below-a-given-threshold', '<p>def test(nums, thresh):<br></p><p>&nbsp; &nbsp; return [i for i, n in enumerate(nums) if n &lt; thresh]</p><p><br></p><p>nums=[0, 12, 45, 3, 4923, 322, 105, 29, 15, 39, 55]</p><p>thresh = 100</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"Threshold: \",thresh)</p><p>print(\"Check the indexes of numbers of the said list below the given threshold:\")</p><p>print(test(nums, thresh))</p><p><br></p><p>nums=[0, 12, 4, 3, 49, 9, 1, 5, 3]</p><p>thresh = 10</p><p>print(\"\\nOriginal list:\")</p><p>print(nums)</p><p>print(\"Threshold: \",thresh)</p><p>print(\"Check the indexes of numbers of the said list below the given threshold:\")</p><p>print(test(nums, thresh))</p><div><br></div>', 'def test(nums, thresh):\r\n    return [i for i, n in enumerate(nums) if n < thresh]\r\n\r\nnums=[0, 12, 45, 3, 4923, 322, 105, 29, 15, 39, 55]\r\nthresh = 100\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"Threshold: \",thresh)\r\nprint(\"Check the indexes of numbers of the said list below the given threshold:\")\r\nprint(test(nums, thresh))\r\n\r\nnums=[0, 12, 4, 3, 49, 9, 1, 5, 3]\r\nthresh = 10\r\nprint(\"\\nOriginal list:\")\r\nprint(nums)\r\nprint(\"Threshold: \",thresh)\r\nprint(\"Check the indexes of numbers of the said list below the given threshold:\")\r\nprint(test(nums, thresh))', 4, 0, '2023-09-04 22:49:55', '2023-09-04 16:49:55', '2023-09-04 17:13:02', 1),
(387, 'Write a Python program to check whether the given strings are palindromes or not. Return True otherwise False.', 'write-a-python-program-to-check-whether-the-given-strings-are-palindromes-or-not-return-true-otherwise-false', '<p>def test(strs):</p><p>&nbsp; &nbsp; return [s == s[::-1] for s in strs]</p><p><br></p><p>strs = [\'palindrome\', \'madamimadam\', \'\', \'foo\', \'eyes\']</p><p>print(\"Original strings:\")</p><p>print(strs)</p><p>print(\"\\nTest whether the given strings are palindromes or not:\")</p><p>print(test(strs))</p>', 'def test(strs):\r\n    return [s == s[::-1] for s in strs]\r\n\r\nstrs = [\'palindrome\', \'madamimadam\', \'\', \'foo\', \'eyes\']\r\nprint(\"Original strings:\")\r\nprint(strs)\r\nprint(\"\\nTest whether the given strings are palindromes or not:\")\r\nprint(test(strs))', 0, 0, '2023-09-04 22:54:18', '2023-09-04 16:54:18', '2023-09-04 16:54:18', 1),
(388, 'Write a Python program to find strings in a given list starting with a given prefix.', 'write-a-python-program-to-find-strings-in-a-given-list-starting-with-a-given-prefix', '<p>def test(strs, prefix):<br></p><p>&nbsp; &nbsp; &nbsp;return [s for s in strs if s.startswith(prefix)]</p><p><br></p><p>strs =&nbsp; [\'cat\', \'car\', \'fear\', \'center\']</p><p>prefix = \"ca\"</p><p>print(\"Original strings:\")</p><p>print(strs)</p><p>print(\"Starting prefix:\", prefix)</p><p>print(\"Strings in the said list starting with a given prefix:\")</p><p>print(test(strs, prefix))</p><p><br></p><p>strs =&nbsp; [\'cat\', \'dog\', \'shatter\', \'donut\', \'at\', \'todo\']</p><p>prefix = \"do\"</p><p>print(\"\\nOriginal strings:\")</p><p>print(strs)</p><p>print(\"Starting prefix:\", prefix)</p><p>print(\"Strings in the said list starting with a given prefix:\")</p><p>print(test(strs, prefix))</p><div><br></div>', 'def test(strs, prefix):\r\n     return [s for s in strs if s.startswith(prefix)]\r\n\r\nstrs =  [\'cat\', \'car\', \'fear\', \'center\']\r\nprefix = \"ca\"\r\nprint(\"Original strings:\")\r\nprint(strs)\r\nprint(\"Starting prefix:\", prefix)\r\nprint(\"Strings in the said list starting with a given prefix:\")\r\nprint(test(strs, prefix))\r\n\r\nstrs =  [\'cat\', \'dog\', \'shatter\', \'donut\', \'at\', \'todo\']\r\nprefix = \"do\"\r\nprint(\"\\nOriginal strings:\")\r\nprint(strs)\r\nprint(\"Starting prefix:\", prefix)\r\nprint(\"Strings in the said list starting with a given prefix:\")\r\nprint(test(strs, prefix))', 0, -1, '2023-09-04 22:58:17', '2023-09-04 16:58:17', '2023-09-04 16:58:17', 1),
(389, 'Write a Python program to find the length of a given list of non-empty strings.', 'write-a-python-program-to-find-the-length-of-a-given-list-of-non-empty-strings', '<p>def test(strs):</p><p>&nbsp; &nbsp; return [*map(len, strs)]</p><p><br></p><p>strs =&nbsp; [\'cat\', \'car\', \'fear\', \'center\']</p><p>print(\"Original strings:\")</p><p>print(strs)</p><p>print(\"Lengths of the said list of non-empty strings:\")</p><p>print(test(strs))</p><p><br></p><p>strs =&nbsp; [\'cat\', \'dog\', \'shatter\', \'donut\', \'at\', \'todo\', \'\']</p><p>print(\"\\nOriginal strings:\")</p><p>print(strs)</p><p>print(\"Lengths of the said list of non-empty strings:\")</p><p>print(test(strs))</p><div><br></div>', 'def test(strs):\r\n    return [*map(len, strs)]\r\n\r\nstrs =  [\'cat\', \'car\', \'fear\', \'center\']\r\nprint(\"Original strings:\")\r\nprint(strs)\r\nprint(\"Lengths of the said list of non-empty strings:\")\r\nprint(test(strs))\r\n\r\nstrs =  [\'cat\', \'dog\', \'shatter\', \'donut\', \'at\', \'todo\', \'\']\r\nprint(\"\\nOriginal strings:\")\r\nprint(strs)\r\nprint(\"Lengths of the said list of non-empty strings:\")\r\nprint(test(strs))', 0, 0, '2023-09-04 23:01:20', '2023-09-04 17:01:20', '2023-09-04 17:01:20', 1),
(390, 'Write a Python program to find the longest string in a given list of strings.', 'write-a-python-program-to-find-the-longest-string-in-a-given-list-of-strings', '<p>def test(words):</p><p>&nbsp; &nbsp; return&nbsp; max(words, key=len)</p><p><br></p><p>strs =&nbsp; [\'cat\', \'car\', \'fear\', \'center\']</p><p>print(\"Original strings:\")</p><p>print(strs)</p><p>print(\"Longest string of the said list of strings:\")</p><p>print(test(strs))</p><p><br></p><p>strs =&nbsp; [\'cat\', \'dog\', \'shatter\', \'donut\', \'at\', \'todo\', \'\']</p><p>print(\"\\nOriginal strings:\")</p><p>print(strs)</p><p>print(\"Longest string of the said list of strings:\")</p><p>print(test(strs))</p><div><br></div>', 'def test(words):\r\n    return  max(words, key=len)\r\n\r\nstrs =  [\'cat\', \'car\', \'fear\', \'center\']\r\nprint(\"Original strings:\")\r\nprint(strs)\r\nprint(\"Longest string of the said list of strings:\")\r\nprint(test(strs))\r\n\r\nstrs =  [\'cat\', \'dog\', \'shatter\', \'donut\', \'at\', \'todo\', \'\']\r\nprint(\"\\nOriginal strings:\")\r\nprint(strs)\r\nprint(\"Longest string of the said list of strings:\")\r\nprint(test(strs))', 0, 0, '2023-09-04 23:05:26', '2023-09-04 17:05:26', '2023-09-04 17:05:26', 1),
