(418, 'Write a Python program to find all words in a given string with n consonants.', 'write-a-python-program-to-find-all-words-in-a-given-string-with-n-consonants', '<p>def test(strs, n):</p><p>&nbsp; &nbsp; return [w for w in strs.split() if sum([c not in \"aeiou\" for c in w.lower()]) == n]</p><p><br></p><p>strs = \"this is our time\"</p><p>print(\"Original string:\",strs)</p><p><br></p><p>n = 3</p><p>print(\"Number of consonants:\",n)</p><p>print(\"Words in the said string with\",n,\"consonants:\")</p><p>print(test(strs, n))</p><p><br></p><p>n = 2</p><p>print(\"\\nNumber of consonants:\",n)</p><p>print(\"Words in the said string with\",n,\"consonants:\")</p><p>print(test(strs, n))</p><p><br></p><p>n = 1</p><p>print(\"\\nNumber of consonants:\",n)</p><p>print(\"Words in the said string with\",n,\"consonants:\")</p><p>print(test(strs, n))</p><div><br></div>', 'def test(strs, n):\r\n    return [w for w in strs.split() if sum([c not in \"aeiou\" for c in w.lower()]) == n]\r\n\r\nstrs = \"this is our time\"\r\nprint(\"Original string:\",strs)\r\n\r\nn = 3\r\nprint(\"Number of consonants:\",n)\r\nprint(\"Words in the said string with\",n,\"consonants:\")\r\nprint(test(strs, n))\r\n\r\nn = 2\r\nprint(\"\\nNumber of consonants:\",n)\r\nprint(\"Words in the said string with\",n,\"consonants:\")\r\nprint(test(strs, n))\r\n\r\nn = 1\r\nprint(\"\\nNumber of consonants:\",n)\r\nprint(\"Words in the said string with\",n,\"consonants:\")\r\nprint(test(strs, n))', 0, 0, '2023-09-05 01:47:58', '2023-09-04 19:47:58', '2023-09-04 19:47:58', 1),
(419, 'Write a Python program to find which characters of a hexadecimal number correspond to prime numbers.', 'write-a-python-program-to-find-which-characters-of-a-hexadecimal-number-correspond-to-prime-numbers', '<p>def test(hn):</p><p>&nbsp; &nbsp; return [c in \"2357BD\" for c in hn]</p><p><br></p><p>hn = \"123ABCD\"</p><p>print(\"Original hexadecimal number:\",hn)</p><p>print(\"Characters of the said hexadecimal number correspond to prime numbers:\")</p><p>print(test(hn))</p><p><br></p><p>hn = \"123456\"</p><p>print(\"\\nOriginal hexadecimal number:\",hn)</p><p>print(\"Characters of the said hexadecimal number correspond to prime numbers:\")</p><p>print(test(hn))</p><p><br></p><p>hn = \"FACE\"</p><p>print(\"\\nOriginal hexadecimal number:\",hn)</p><p>print(\"Characters of the said hexadecimal number correspond to prime numbers:\")</p><p>print(test(hn))</p><div><br></div>', 'def test(hn):\r\n    return [c in \"2357BD\" for c in hn]\r\n\r\nhn = \"123ABCD\"\r\nprint(\"Original hexadecimal number:\",hn)\r\nprint(\"Characters of the said hexadecimal number correspond to prime numbers:\")\r\nprint(test(hn))\r\n\r\nhn = \"123456\"\r\nprint(\"\\nOriginal hexadecimal number:\",hn)\r\nprint(\"Characters of the said hexadecimal number correspond to prime numbers:\")\r\nprint(test(hn))\r\n\r\nhn = \"FACE\"\r\nprint(\"\\nOriginal hexadecimal number:\",hn)\r\nprint(\"Characters of the said hexadecimal number correspond to prime numbers:\")\r\nprint(test(hn))', 0, 0, '2023-09-05 01:53:39', '2023-09-04 19:53:39', '2023-09-04 19:53:39', 1),
(420, 'Write a Python program to find all even palindromes up to n.', 'write-a-python-program-to-find-all-even-palindromes-up-to-n', '<p>def test(n):</p><p>&nbsp; &nbsp; return [i for i in range(0,n,2) if str(i) == str(i)[::-1]]</p><p><br></p><p>n = 50</p><p>print(\"\\nEven palindromes up to\",n,\"-\")</p><p>print(test(n))</p><p><br></p><p>n = 100</p><p>print(\"\\nEven palindromes up to\",n,\"-\")</p><p>print(test(n))</p><p><br></p><p>n = 500</p><p>print(\"\\nEven palindromes up to\",n,\"-\")</p><p>print(test(n))</p><p><br></p><p>n = 2000</p><p>print(\"\\nEven palindromes up to\",n,\"-\")</p><p>print(test(n))</p><div><br></div>', 'def test(n):\r\n    return [i for i in range(0,n,2) if str(i) == str(i)[::-1]]\r\n\r\nn = 50\r\nprint(\"\\nEven palindromes up to\",n,\"-\")\r\nprint(test(n))\r\n\r\nn = 100\r\nprint(\"\\nEven palindromes up to\",n,\"-\")\r\nprint(test(n))\r\n\r\nn = 500\r\nprint(\"\\nEven palindromes up to\",n,\"-\")\r\nprint(test(n))\r\n\r\nn = 2000\r\nprint(\"\\nEven palindromes up to\",n,\"-\")\r\nprint(test(n))', 0, 0, '2023-09-05 01:56:16', '2023-09-04 19:56:16', '2023-09-04 19:56:16', 1),
(421, 'Given an array of numbers representing a branch on a binary tree, write a Python program to find the minimum even value and its index. In the case of a tie, return the smallest index. If there are no even numbers, the answer is [].', 'given-an-array-of-numbers-representing-a-branch-on-a-binary-tree-write-a-python-program-to-find-the-minimum-even-value-and-its-index-in-the-case-of-a-tie-return-the-smallest-index-if-there-are-no-even-numbers-the-answer-is', '<p>def test(nums):</p><p>&nbsp; &nbsp; if any(n % 2 == 0 for n in nums):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return min([v, i] for i, v in enumerate(nums) if v % 2 == 0)</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return [ ]</p><p><br></p><p>nums =&nbsp; [1, 9, 4, 6, 10, 11, 14, 8]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"Minimum even value and its index of the said array of numbers:\")</p><p>print(test(nums))</p><p><br></p><p>nums =&nbsp; [1, 7, 4, 4, 9, 2]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"Minimum even value and its index of the said array of numbers:\")</p><p>print(test(nums))</p><p><br></p><p>nums =&nbsp; [1, 7, 7, 5, 9]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"Minimum even value and its index of the said array of numbers:\")</p><p>print(test(nums))</p><div><br></div>', 'def test(nums):\r\n    if any(n % 2 == 0 for n in nums):\r\n        return min([v, i] for i, v in enumerate(nums) if v % 2 == 0)\r\n    else:\r\n        return [ ]\r\n\r\nnums =  [1, 9, 4, 6, 10, 11, 14, 8]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"Minimum even value and its index of the said array of numbers:\")\r\nprint(test(nums))\r\n\r\nnums =  [1, 7, 4, 4, 9, 2]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"Minimum even value and its index of the said array of numbers:\")\r\nprint(test(nums))\r\n\r\nnums =  [1, 7, 7, 5, 9]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"Minimum even value and its index of the said array of numbers:\")\r\nprint(test(nums))', 6, 0, '2023-09-05 02:00:39', '2023-09-04 20:00:39', '2023-09-14 10:08:40', 1);
