(438, 'Write a Python program to find the sum of the even elements that are at odd indices in a given list.', 'write-a-python-program-to-find-the-sum-of-the-even-elements-that-are-at-odd-indices-in-a-given-list', '<p>def test(nums):</p><p>&nbsp; &nbsp; return sum(i for i in nums[1::2] if i % 2 == 0)</p><p><br></p><p>nums = [1, 2, 3, 4, 5, 6, 7]</p><p>print(\"Original list of numbers:\")</p><p>print(nums)</p><p>print(\"Sum of the even elements of the said list that are at odd indices:\")</p><p>print(test(nums))</p><p><br></p><p>nums = [1, 2, 8, 3, 9, 4]</p><p>print(\"\\nOriginal list of numbers:\")</p><p>print(nums)</p><p>print(\"Sum of the even elements of the said list that are at odd indices:\")</p><p>print(test(nums))</p><div><br></div>', 'def test(nums):\r\n    return sum(i for i in nums[1::2] if i % 2 == 0)\r\n\r\nnums = [1, 2, 3, 4, 5, 6, 7]\r\nprint(\"Original list of numbers:\")\r\nprint(nums)\r\nprint(\"Sum of the even elements of the said list that are at odd indices:\")\r\nprint(test(nums))\r\n\r\nnums = [1, 2, 8, 3, 9, 4]\r\nprint(\"\\nOriginal list of numbers:\")\r\nprint(nums)\r\nprint(\"Sum of the even elements of the said list that are at odd indices:\")\r\nprint(test(nums))', 0, 0, '2023-09-06 02:36:59', '2023-09-05 20:36:59', '2023-09-05 20:36:59', 1),
(439, 'Write a Python program to find the string consisting of all the words whose lengths are prime numbers.', 'write-a-python-program-to-find-the-string-consisting-of-all-the-words-whose-lengths-are-prime-numbers', '<p>def test(strs):</p><p>&nbsp; &nbsp; return \" \".join(strs for strs in strs.split() if is_prime(len(strs)))</p><p><br></p><p>def is_prime(n):</p><p>&nbsp; &nbsp; return n &gt; 1 and all(n % j for j in range(2, int(n ** 0.5) + 1))</p><p><br></p><p>strs = \"The quick brown fox jumps over the lazy dog.\"</p><p>print(\"Original string:\")</p><p>print(strs)</p><p>print(\"Words whose lengths are prime numbers in the said string:\")</p><p>print(test(strs))</p><p><br></p><p>strs = \"Omicron Effect: Foreign Flights Won\'t Resume On Dec 15, Decision Later.\"</p><p>print(\"\\nOriginal string:\")</p><p>print(strs)</p><p>print(\"Words whose lengths are prime numbers in the said string:\")</p><p>print(test(strs))</p><div><br></div>', 'def test(strs):\r\n    return \" \".join(strs for strs in strs.split() if is_prime(len(strs)))\r\n\r\ndef is_prime(n):\r\n    return n > 1 and all(n % j for j in range(2, int(n ** 0.5) + 1))\r\n\r\nstrs = \"The quick brown fox jumps over the lazy dog.\"\r\nprint(\"Original string:\")\r\nprint(strs)\r\nprint(\"Words whose lengths are prime numbers in the said string:\")\r\nprint(test(strs))\r\n\r\nstrs = \"Omicron Effect: Foreign Flights Won\'t Resume On Dec 15, Decision Later.\"\r\nprint(\"\\nOriginal string:\")\r\nprint(strs)\r\nprint(\"Words whose lengths are prime numbers in the said string:\")\r\nprint(test(strs))', 0, 0, '2023-09-06 02:46:13', '2023-09-05 20:46:13', '2023-09-05 20:46:13', 1),
