(455, 'Write a Python program to round each float in a given list of numbers up to the next integer and return the running total of the integer squares.', 'write-a-python-program-to-round-each-float-in-a-given-list-of-numbers-up-to-the-next-integer-and-return-the-running-total-of-the-integer-squares', '<p>def test(nums):</p><p>&nbsp; &nbsp; from math import ceil</p><p>&nbsp; &nbsp; running_squares = [ ]</p><p>&nbsp; &nbsp; tot = 0</p><p>&nbsp; &nbsp; for v in nums:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; tot += ceil(v) ** 2</p><p>&nbsp; &nbsp; &nbsp; &nbsp; running_squares.append(tot)</p><p>&nbsp; &nbsp; return running_squares</p><p><br></p><p>nums = [2.6, 3.5, 6.7, 2.3, 5.6]</p><p>print(\"List of numbers:\",nums)</p><p>print(\"Round each float of the said list up to the next integer and return the running total of the integer squares:\")</p><p>print(test(nums))</p><p><br></p><p>nums = [301.1, 401.4, -23.1, 13554122.0, 10201.0101, 10000000.0]</p><p>print(\"\\nList of numbers:\",nums)</p><p>print(\"Round each float of the said list up to the next integer and return the running total of the integer squares:\")</p><p>print(test(nums))</p><div><br></div>', 'def test(nums):\r\n    from math import ceil\r\n    running_squares = [ ]\r\n    tot = 0\r\n    for v in nums:\r\n        tot += ceil(v) ** 2\r\n        running_squares.append(tot)\r\n    return running_squares\r\n\r\nnums = [2.6, 3.5, 6.7, 2.3, 5.6]\r\nprint(\"List of numbers:\",nums)\r\nprint(\"Round each float of the said list up to the next integer and return the running total of the integer squares:\")\r\nprint(test(nums))\r\n\r\nnums = [301.1, 401.4, -23.1, 13554122.0, 10201.0101, 10000000.0]\r\nprint(\"\\nList of numbers:\",nums)\r\nprint(\"Round each float of the said list up to the next integer and return the running total of the integer squares:\")\r\nprint(test(nums))', 0, 0, '2023-09-07 00:42:22', '2023-09-06 18:42:22', '2023-09-06 18:42:22', 1),
(456, 'Write a Python program to calculate the average of the numbers a through b (b not included) rounded to the nearest integer, in binary (or -1 if there are no such numbers).', 'write-a-python-program-to-calculate-the-average-of-the-numbers-a-through-b-b-not-included-rounded-to-the-nearest-integer-in-binary-or-1-if-there-are-no-such-numbers', '<p>def test(a,b):</p><p>&nbsp; &nbsp; r = range(a, b)</p><p>&nbsp; &nbsp; if len(r) == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return \"-1\"</p><p>&nbsp; &nbsp; return bin(round(sum(r) / len(r)))</p><p><br></p><p>a = 4</p><p>b = 7</p><p>print(\"Range:\",a,\",\",b)</p><p>print(\"Average of the numbers\",a,\"through\",b,\"rounded to nearest integer, in binary:\")</p><p>print(test(a, b))</p><p><br></p><p>a = 11</p><p>b = 19</p><p>print(\"\\nRange:\",a,\",\",b)</p><p>print(\"Average of the numbers\",a,\"through\",b,\"rounded to nearest integer, in binary:\")</p><p>print(test(a, b))</p><div><br></div>', 'def test(a,b):\r\n    r = range(a, b)\r\n    if len(r) == 0:\r\n        return \"-1\"\r\n    return bin(round(sum(r) / len(r)))\r\n\r\na = 4\r\nb = 7\r\nprint(\"Range:\",a,\",\",b)\r\nprint(\"Average of the numbers\",a,\"through\",b,\"rounded to nearest integer, in binary:\")\r\nprint(test(a, b))\r\n\r\na = 11\r\nb = 19\r\nprint(\"\\nRange:\",a,\",\",b)\r\nprint(\"Average of the numbers\",a,\"through\",b,\"rounded to nearest integer, in binary:\")\r\nprint(test(a, b))', 0, 0, '2023-09-07 00:47:03', '2023-09-06 18:47:03', '2023-09-06 18:47:03', 1),
