(466, 'Write a Python program to find all n-digit integers that start or end with 2.', 'write-a-python-program-to-find-all-n-digit-integers-that-start-or-end-with-2', '<p>def test(n):</p><p>&nbsp; &nbsp; ans = [ ]</p><p>&nbsp; &nbsp; for i in range(10 ** (n - 1), 10 ** n):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; assert len(str(i)) == n</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if str(i).startswith(\"2\") or str(i).endswith(\"2\"):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ans.append(i)</p><p>&nbsp; &nbsp; return ans</p><p><br></p><p>n = 1</p><p>print(\"Number:\",n)</p><p>print(\"All\",n,\"- digit integers that start or end with 2:\")</p><p>print(test(n))</p><p><br></p><p>n = 2</p><p>print(\"\\nNumber:\",n)</p><p>print(\"All\",n,\"- digit integers that start or end with 2:\")</p><p>print(test(n))</p><p><br></p><p>n = 3</p><p>print(\"\\nNumber:\",n)</p><p>print(\"All\",n,\"- digit integers that start or end with 2:\")</p><p>print(test(n))</p><div><br></div>', 'def test(n):\r\n    ans = [ ]\r\n    for i in range(10 ** (n - 1), 10 ** n):\r\n        assert len(str(i)) == n\r\n        if str(i).startswith(\"2\") or str(i).endswith(\"2\"):\r\n            ans.append(i)\r\n    return ans\r\n\r\nn = 1\r\nprint(\"Number:\",n)\r\nprint(\"All\",n,\"- digit integers that start or end with 2:\")\r\nprint(test(n))\r\n\r\nn = 2\r\nprint(\"\\nNumber:\",n)\r\nprint(\"All\",n,\"- digit integers that start or end with 2:\")\r\nprint(test(n))\r\n\r\nn = 3\r\nprint(\"\\nNumber:\",n)\r\nprint(\"All\",n,\"- digit integers that start or end with 2:\")\r\nprint(test(n))', 0, 0, '2023-09-07 01:28:26', '2023-09-06 19:28:26', '2023-09-06 19:28:26', 1),
(467, 'Write a Python program to start with a list of integers, keep every other element in place and otherwise sort the list.', 'write-a-python-program-to-start-with-a-list-of-integers-keep-every-other-element-in-place-and-otherwise-sort-the-list', '<p>def test(nums):</p><p>&nbsp; &nbsp; li = nums.copy( )</p><p>&nbsp; &nbsp; for i in range(len(li)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if i % 2 == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for j in range(i+2, len(li), 2):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if li[j] &lt; li[i]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; swap(li, i, j)</p><p><br></p><p>&nbsp; &nbsp; return li</p><p><br></p><p>def swap(li, i, j):</p><p>&nbsp; &nbsp; temp = li[i]</p><p>&nbsp; &nbsp; li[i] = li[j]</p><p>&nbsp; &nbsp; li[j] = temp</p><p><br></p><p>nums = [2, 5, 6, 3, 1, 4, 34]</p><p>print(\"Original list (triple) of lists:\")</p><p>print(nums)</p><p>print(\"In the said list, keep every other element in place and otherwise sort the list.:\")</p><p>print(test(nums))</p><p><br></p><p>nums = [8, 0, 7, 2, 9, 4, 1, 2, 8, 3]</p><p>print(\"\\nOriginal list (triple) of lists:\")</p><p>print(nums)</p><p>print(\"In the said list, keep every other element in place and otherwise sort the list.:\")</p><p>print(test(nums))</p><div><br></div>', 'def test(nums):\r\n    li = nums.copy( )\r\n    for i in range(len(li)):\r\n        if i % 2 == 0:\r\n            for j in range(i+2, len(li), 2):\r\n                if li[j] < li[i]:\r\n                    swap(li, i, j)\r\n\r\n    return li\r\n\r\ndef swap(li, i, j):\r\n    temp = li[i]\r\n    li[i] = li[j]\r\n    li[j] = temp\r\n\r\nnums = [2, 5, 6, 3, 1, 4, 34]\r\nprint(\"Original list (triple) of lists:\")\r\nprint(nums)\r\nprint(\"In the said list, keep every other element in place and otherwise sort the list.:\")\r\nprint(test(nums))\r\n\r\nnums = [8, 0, 7, 2, 9, 4, 1, 2, 8, 3]\r\nprint(\"\\nOriginal list (triple) of lists:\")\r\nprint(nums)\r\nprint(\"In the said list, keep every other element in place and otherwise sort the list.:\")\r\nprint(test(nums))', 0, 0, '2023-09-07 01:36:41', '2023-09-06 19:36:41', '2023-09-06 19:36:41', 1),
(468, 'Write a Python program to find the closest palindrome to a given string.', 'write-a-python-program-to-find-the-closest-palindrome-to-a-given-string', '<p>def test(s):</p><p>&nbsp; &nbsp; odd = 0</p><p>&nbsp; &nbsp; for i, c in enumerate(s):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if c != s[~i]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; odd += 1</p><p>&nbsp; &nbsp; if odd % 2 == 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; half = odd // 2</p><p>&nbsp; &nbsp; &nbsp; &nbsp; pal = \"\".join((s[i] if i &lt; half else s[~i] for i in range(len(s))))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return pal</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; half = odd // 2</p><p>&nbsp; &nbsp; &nbsp; &nbsp; pal = \"\".join((s[i] if i &lt;= half else s[~i] for i in range(len(s))))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return pal</p><p><br></p><p>s = \"cat\"</p><p>print(\"Original string:\",s)</p><p>print(\"Closest palindrome of the said string:\")</p><p>print(test(s))</p><p><br></p><p>s = \"madan\"</p><p>print(\"\\nOriginal string:\",s)</p><p>print(\"Closest palindrome of the said string:\")</p><p>print(test(s))</p><p><br></p><p>s = \"radivider\"</p><p>print(\"Original string:\",s)</p><p>print(\"Closest palindrome of the said string:\")</p><p>print(test(s))</p><p><br></p><p>s = \"madan\"</p><p>print(\"\\nOriginal string:\",s)</p><p>print(\"Closest palindrome of the said string:\")</p><p>print(test(s))</p><p><br></p><p>s = \"abc\"</p><p>print(\"Original string:\",s)</p><p>print(\"Closest palindrome of the said string:\")</p><p>print(test(s))</p><p><br></p><p>s = \"racecbr\"</p><p>print(\"\\nOriginal string:\",s)</p><p>print(\"Closest palindrome of the said string:\")</p><p>print(test(s))</p><div><br></div>', 'def test(s):\r\n    odd = 0\r\n    for i, c in enumerate(s):\r\n        if c != s[~i]:\r\n            odd += 1\r\n    if odd % 2 == 1:\r\n        half = odd // 2\r\n        pal = \"\".join((s[i] if i < half else s[~i] for i in range(len(s))))\r\n        return pal\r\n    else:\r\n        half = odd // 2\r\n        pal = \"\".join((s[i] if i <= half else s[~i] for i in range(len(s))))\r\n        return pal\r\n\r\ns = \"cat\"\r\nprint(\"Original string:\",s)\r\nprint(\"Closest palindrome of the said string:\")\r\nprint(test(s))\r\n\r\ns = \"madan\"\r\nprint(\"\\nOriginal string:\",s)\r\nprint(\"Closest palindrome of the said string:\")\r\nprint(test(s))\r\n\r\ns = \"radivider\"\r\nprint(\"Original string:\",s)\r\nprint(\"Closest palindrome of the said string:\")\r\nprint(test(s))\r\n\r\ns = \"madan\"\r\nprint(\"\\nOriginal string:\",s)\r\nprint(\"Closest palindrome of the said string:\")\r\nprint(test(s))\r\n\r\ns = \"abc\"\r\nprint(\"Original string:\",s)\r\nprint(\"Closest palindrome of the said string:\")\r\nprint(test(s))\r\n\r\ns = \"racecbr\"\r\nprint(\"\\nOriginal string:\",s)\r\nprint(\"Closest palindrome of the said string:\")\r\nprint(test(s))', 2, 0, '2023-09-07 01:40:12', '2023-09-06 19:40:12', '2023-10-01 20:08:14', 1),
(469, 'Given a string consisting of whitespace and groups of matched parentheses, write a Python program to split it into groups of perfectly matched parentheses without any whitespace.', 'given-a-string-consisting-of-whitespace-and-groups-of-matched-parentheses-write-a-python-program-to-split-it-into-groups-of-perfectly-matched-parentheses-without-any-whitespace', '<p>def test(combined):</p><p>&nbsp; &nbsp; ls = [ ]</p><p>&nbsp; &nbsp; s2 = \"\"</p><p>&nbsp; &nbsp; for s in combined.replace(\' \', \'\'):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s2 += s</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if s2.count(\"(\") == s2.count(\")\"):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ls.append(s2)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s2 = \"\"</p><p>&nbsp; &nbsp; return ls</p><p><br></p><p>combined = \'( ()) ((()()())) (()) ()\'</p><p>print(\"Parentheses string:\")</p><p>print(combined)</p><p>print(\"Separate parentheses groups of the said string:\")</p><p>print(test(combined))</p><p><br></p><p>combined = \'() (( ( )() (&nbsp; &nbsp;)) ) ( ())\'</p><p>print(\"\\nParentheses string:\")</p><p>print(combined)</p><p>print(\"Separate parentheses groups of the said string:\")</p><p>print(test(combined))</p><div><br></div>', 'def test(combined):\r\n    ls = [ ]\r\n    s2 = \"\"\r\n    for s in combined.replace(\' \', \'\'):\r\n        s2 += s\r\n        if s2.count(\"(\") == s2.count(\")\"):\r\n            ls.append(s2)\r\n            s2 = \"\"\r\n    return ls\r\n\r\ncombined = \'( ()) ((()()())) (()) ()\'\r\nprint(\"Parentheses string:\")\r\nprint(combined)\r\nprint(\"Separate parentheses groups of the said string:\")\r\nprint(test(combined))\r\n\r\ncombined = \'() (( ( )() (   )) ) ( ())\'\r\nprint(\"\\nParentheses string:\")\r\nprint(combined)\r\nprint(\"Separate parentheses groups of the said string:\")\r\nprint(test(combined))', 0, 0, '2023-09-07 01:43:51', '2023-09-06 19:43:51', '2023-09-06 19:43:51', 1),
(470, 'Write a Python program to find a palindrome of a given length containing a given string.', 'write-a-python-program-to-find-a-palindrome-of-a-given-length-containing-a-given-string', '<p>def test(s, length):</p><p>&nbsp; &nbsp; s_index = 0</p><p>&nbsp; &nbsp; length_half = (length - (length % 2)) // 2</p><p>&nbsp; &nbsp; ans = \"\"</p><p>&nbsp; &nbsp; while len(ans) &lt; length_half:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; ans += s[s_index%len(s)]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s_index += 1</p><p>&nbsp; &nbsp; if length % 2 == 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; ans += \"a\"</p><p>&nbsp; &nbsp; return ans + ans[::-1]</p><p><br></p><p>s = \'madam\'</p><p>length = 7</p><p>print(\"String and length of the palindrome:\",s,\",\",length)</p><p>print(\"Palindrome of the said string and length:\")</p><p>print(test(s, length))</p><p><br></p><p>s = \'madam\'</p><p>length = 6</p><p>print(\"\\nString and length of the palindrome:\",s,\",\",length)</p><p>print(\"Palindrome of the said string and length:\")</p><p>print(test(s, length))</p><p><br></p><p>length = 5</p><p>print(\"\\nString and length of the palindrome:\",s,\",\",length)</p><p>print(\"Palindrome of the said string and length:\")</p><p>print(test(s, length))</p><p><br></p><p>length = 3</p><p>print(\"\\nString and length of the palindrome:\",s,\",\",length)</p><p>print(\"Palindrome of the said string and length:\")</p><p>print(test(s, length))</p><p><br></p><p>length = 2</p><p>print(\"\\nString and length of the palindrome:\",s,\",\",length)</p><p>print(\"Palindrome of the said string and length:\")</p><p>print(test(s, length))</p><p><br></p><p>length = 1</p><p>print(\"\\nString and length of the palindrome:\",s,\",\",length)</p><p>print(\"Palindrome of the said string and length:\")</p><p>print(test(s, length))</p><div><br></div>', 'def test(s, length):\r\n    s_index = 0\r\n    length_half = (length - (length % 2)) // 2\r\n    ans = \"\"\r\n    while len(ans) < length_half:\r\n        ans += s[s_index%len(s)]\r\n        s_index += 1\r\n    if length % 2 == 1:\r\n        ans += \"a\"\r\n    return ans + ans[::-1]\r\n\r\ns = \'madam\'\r\nlength = 7\r\nprint(\"String and length of the palindrome:\",s,\",\",length)\r\nprint(\"Palindrome of the said string and length:\")\r\nprint(test(s, length))\r\n\r\ns = \'madam\'\r\nlength = 6\r\nprint(\"\\nString and length of the palindrome:\",s,\",\",length)\r\nprint(\"Palindrome of the said string and length:\")\r\nprint(test(s, length))\r\n\r\nlength = 5\r\nprint(\"\\nString and length of the palindrome:\",s,\",\",length)\r\nprint(\"Palindrome of the said string and length:\")\r\nprint(test(s, length))\r\n\r\nlength = 3\r\nprint(\"\\nString and length of the palindrome:\",s,\",\",length)\r\nprint(\"Palindrome of the said string and length:\")\r\nprint(test(s, length))\r\n\r\nlength = 2\r\nprint(\"\\nString and length of the palindrome:\",s,\",\",length)\r\nprint(\"Palindrome of the said string and length:\")\r\nprint(test(s, length))\r\n\r\nlength = 1\r\nprint(\"\\nString and length of the palindrome:\",s,\",\",length)\r\nprint(\"Palindrome of the said string and length:\")\r\nprint(test(s, length))', 2, 0, '2023-09-07 01:47:54', '2023-09-06 19:47:54', '2023-10-01 20:07:34', 1);
