(543, 'Write a Python program to convert an integer to a string in any base.', 'write-a-python-program-to-convert-an-integer-to-a-string-in-any-base', '<p>def to_string(n,base):</p><p>&nbsp; &nbsp; conver_tString = \"0123456789ABCDEF\"</p><p>&nbsp; &nbsp; if n &lt; base:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return conver_tString[n]</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return to_string(n//base,base) + conver_tString[n % base]</p><p><br></p><p>print(to_string(2835,16))</p><div><br></div>', 'def to_string(n,base):\r\n    conver_tString = \"0123456789ABCDEF\"\r\n    if n < base:\r\n        return conver_tString[n]\r\n    else:\r\n        return to_string(n//base,base) + conver_tString[n % base]\r\n\r\nprint(to_string(2835,16))', 0, 0, '2023-09-12 14:03:48', '2023-09-12 08:03:48', '2023-09-12 08:03:48', 1),
(544, 'Write a Python program to sum recursion lists.', 'write-a-python-program-to-sum-recursion-lists', '<p>def recursive_list_sum(data_list):</p><p>&nbsp; &nbsp; total = 0</p><p>&nbsp; &nbsp; for element in data_list:</p><p>&nbsp; &nbsp; if type(element) == type([]):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; total = total + recursive_list_sum(element)</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; total = total + element</p><p><br></p><p>&nbsp; &nbsp; return total</p><p><br></p><p>print( recursive_list_sum([1, 2, [3,4],[5,6]]))</p><div><br></div>', 'def recursive_list_sum(data_list):\r\n    total = 0\r\n    for element in data_list:\r\n    if type(element) == type([]):\r\n        total = total + recursive_list_sum(element)\r\n    else:\r\n        total = total + element\r\n\r\n    return total\r\n\r\nprint( recursive_list_sum([1, 2, [3,4],[5,6]]))', 2, 0, '2023-09-12 14:07:57', '2023-09-12 08:07:57', '2023-09-12 08:16:01', 1),
(545, 'Write a Python program to calculate the harmonic sum of n-1.', 'write-a-python-program-to-calculate-the-harmonic-sum-of-n-1', '<p>def harmonic_sum(n):</p><p>&nbsp; &nbsp; if n &lt; 2:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return 1</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return 1 / n + (harmonic_sum(n - 1))</p><p><br></p><p>print(harmonic_sum(7))</p><p>print(harmonic_sum(4))</p><div><br></div>', 'def harmonic_sum(n):\r\n    if n < 2:\r\n        return 1\r\n    else:\r\n        return 1 / n + (harmonic_sum(n - 1))\r\n\r\nprint(harmonic_sum(7))\r\nprint(harmonic_sum(4))', 0, 0, '2023-09-12 14:28:30', '2023-09-12 08:28:30', '2023-09-12 08:28:30', 1),
