(594, 'Write a Python program to find the first non-repeating character in a given string.', 'write-a-python-program-to-find-the-first-non-repeating-character-in-a-given-string', '<p>def first_non_repeating_character(str1):</p><p>&nbsp; &nbsp; char_order = [ ]</p><p>&nbsp; &nbsp; ctr = { }</p><p>&nbsp; &nbsp; for c in str1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if c in ctr:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr[c] += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr[c] = 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; char_order.append(c)</p><p>&nbsp; &nbsp; for c in char_order:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if ctr[c] == 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return c</p><p>&nbsp; &nbsp; return None</p><p><br></p><p>print(first_non_repeating_character(\'abcdef\'))</p><p>print(first_non_repeating_character(\'abcabcdef\'))</p><p>print(first_non_repeating_character(\'aabbcc\'))</p><div><br></div>', 'def first_non_repeating_character(str1):\r\n    char_order = [ ]\r\n    ctr = { }\r\n    for c in str1:\r\n        if c in ctr:\r\n            ctr[c] += 1\r\n        else:\r\n            ctr[c] = 1\r\n            char_order.append(c)\r\n    for c in char_order:\r\n        if ctr[c] == 1:\r\n            return c\r\n    return None\r\n\r\nprint(first_non_repeating_character(\'abcdef\'))\r\nprint(first_non_repeating_character(\'abcabcdef\'))\r\nprint(first_non_repeating_character(\'aabbcc\'))', 0, 0, '2023-09-13 00:17:55', '2023-09-12 18:17:55', '2023-09-12 18:17:55', 1),
(595, 'Write a Python program to print all permutations with a given repetition number of characters of a given string.', 'write-a-python-program-to-print-all-permutations-with-a-given-repetition-number-of-characters-of-a-given-string', '<p>from itertools import product</p><p><br></p><p>def all_repeat(str1, rno):</p><p>&nbsp; &nbsp; chars = list(str1)</p><p>&nbsp; &nbsp; results = [ ]</p><p>&nbsp; &nbsp; for c in product(chars, repeat = rno):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; results.append(c)</p><p>&nbsp; &nbsp; return results</p><p><br></p><p>print(all_repeat(\'xyz\', 3))</p><p>print(all_repeat(\'xyz\', 2))</p><p>print(all_repeat(\'abcd\', 4))</p><div><br></div>', 'from itertools import product\r\n\r\ndef all_repeat(str1, rno):\r\n    chars = list(str1)\r\n    results = [ ]\r\n    for c in product(chars, repeat = rno):\r\n        results.append(c)\r\n    return results\r\n\r\nprint(all_repeat(\'xyz\', 3))\r\nprint(all_repeat(\'xyz\', 2))\r\nprint(all_repeat(\'abcd\', 4))', 2, 0, '2023-09-13 00:21:11', '2023-09-12 18:21:11', '2023-09-12 18:21:22', 1),
(596, 'Write a Python program to find the first repeated character in a given string.', 'write-a-python-program-to-find-the-first-repeated-character-in-a-given-string', '<p>def first_repeated_char(str1):</p><p>&nbsp; &nbsp; for index,c in enumerate(str1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if str1[:index+1].count(c) &gt; 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return c&nbsp;</p><p>&nbsp; &nbsp; return \"None\"</p><p><br></p><p>print(first_repeated_char(\"abcdabcd\"))</p><p>print(first_repeated_char(\"abcd\"))</p><div><br></div>', 'def first_repeated_char(str1):\r\n    for index,c in enumerate(str1):\r\n        if str1[:index+1].count(c) > 1:\r\n            return c \r\n    return \"None\"\r\n\r\nprint(first_repeated_char(\"abcdabcd\"))\r\nprint(first_repeated_char(\"abcd\"))', 2, 0, '2023-09-13 00:24:31', '2023-09-12 18:24:31', '2023-09-12 18:25:46', 1),
(597, 'Write a Python program to find the first repeated character in a given string where the index of the first occurrence is smallest.', 'write-a-python-program-to-find-the-first-repeated-character-in-a-given-string-where-the-index-of-the-first-occurrence-is-smallest', '<p>def first_repeated_char_smallest_distance(str1):</p><p>&nbsp; &nbsp; temp = { }</p><p>&nbsp; &nbsp; for ch in str1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if ch in temp:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return ch, str1.index(ch);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; temp[ch] = 0</p><p>&nbsp; &nbsp; return \'None\'</p><p><br></p><p>print(first_repeated_char_smallest_distance(\"abcabc\"))</p><p>print(first_repeated_char_smallest_distance(\"abcb\"))</p><p>print(first_repeated_char_smallest_distance(\"abcc\"))</p><p>print(first_repeated_char_smallest_distance(\"abcxxy\"))</p><p>print(first_repeated_char_smallest_distance(\"abc\"))))</p><div><br></div>', 'def first_repeated_char_smallest_distance(str1):\r\n    temp = { }\r\n    for ch in str1:\r\n        if ch in temp:\r\n            return ch, str1.index(ch);\r\n        else:\r\n            temp[ch] = 0\r\n    return \'None\'\r\n\r\nprint(first_repeated_char_smallest_distance(\"abcabc\"))\r\nprint(first_repeated_char_smallest_distance(\"abcb\"))\r\nprint(first_repeated_char_smallest_distance(\"abcc\"))\r\nprint(first_repeated_char_smallest_distance(\"abcxxy\"))\r\nprint(first_repeated_char_smallest_distance(\"abc\"))))', 0, 0, '2023-09-13 00:27:43', '2023-09-12 18:27:43', '2023-09-12 18:27:43', 1),
(598, 'Write a Python program to find the first repeated word in a given string.', 'write-a-python-program-to-find-the-first-repeated-word-in-a-given-string', '<p>def first_repeated_word(str1):</p><p>&nbsp; &nbsp; temp = set( )</p><p>&nbsp; &nbsp; for word in str1.split():</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if word in temp:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return word;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; temp.add(word)</p><p>&nbsp; &nbsp; return \'None\'</p><p><br></p><p>print(first_repeated_word(\"ab ca bc ab\"))</p><p>print(first_repeated_word(\"ab ca bc ab ca ab bc\"))</p><p>print(first_repeated_word(\"ab ca bc ca ab bc\"))</p><p>print(first_repeated_word(\"ab ca bc\"))</p><div><br></div>', 'def first_repeated_word(str1):\r\n    temp = set( )\r\n    for word in str1.split():\r\n        if word in temp:\r\n            return word;\r\n        else:\r\n            temp.add(word)\r\n    return \'None\'\r\n\r\nprint(first_repeated_word(\"ab ca bc ab\"))\r\nprint(first_repeated_word(\"ab ca bc ab ca ab bc\"))\r\nprint(first_repeated_word(\"ab ca bc ca ab bc\"))\r\nprint(first_repeated_word(\"ab ca bc\"))', 0, 0, '2023-09-13 00:30:13', '2023-09-12 18:30:13', '2023-09-12 18:30:19', 1),
(599, 'Write a Python program to find the second most repeated word in a given string.', 'write-a-python-program-to-find-the-second-most-repeated-word-in-a-given-string', '<p>def word_count(str):</p><p>&nbsp; &nbsp; counts = dict( )</p><p>&nbsp; &nbsp; words = str.split( )</p><p><br></p><p>&nbsp; &nbsp; for word in words:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if word in counts:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; counts[word] += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; counts[word] = 1</p><p><br></p><p>&nbsp; &nbsp; counts_x = sorted(counts.items(), key=lambda kv: kv[1])</p><p>&nbsp; &nbsp; #print(counts_x)</p><p>&nbsp; &nbsp; return counts_x[-2]</p><p><br></p><p>print(word_count(\"Both of these issues are fixed by postponing the evaluation of annotations. Instead of compiling code which executes expressions in annotations at their definition time, the compiler stores the annotation in a string form equivalent to the AST of the expression in question. If needed, annotations can be resolved at runtime using typing.get_type_hints(). In the common case where this is not required, the annotations are cheaper to store (since short strings are interned by the interpreter) and make startup time faster.\"))</p><div><br></div>', 'def word_count(str):\r\n    counts = dict( )\r\n    words = str.split( )\r\n\r\n    for word in words:\r\n        if word in counts:\r\n            counts[word] += 1\r\n        else:\r\n            counts[word] = 1\r\n\r\n    counts_x = sorted(counts.items(), key=lambda kv: kv[1])\r\n    #print(counts_x)\r\n    return counts_x[-2]\r\n\r\nprint(word_count(\"Both of these issues are fixed by postponing the evaluation of annotations. Instead of compiling code which executes expressions in annotations at their definition time, the compiler stores the annotation in a string form equivalent to the AST of the expression in question. If needed, annotations can be resolved at runtime using typing.get_type_hints(). In the common case where this is not required, the annotations are cheaper to store (since short strings are interned by the interpreter) and make startup time faster.\"))', 2, 0, '2023-09-13 00:32:54', '2023-09-12 18:32:54', '2023-09-12 18:33:07', 1),
(600, 'Write a Python program to remove spaces from a given string.', 'write-a-python-program-to-remove-spaces-from-a-given-string', '<p>def remove_spaces(str1):</p><p>&nbsp; &nbsp; str1 = str1.replace(\' \',\'\')</p><p>&nbsp; &nbsp; return str1</p><p><br></p><p>print(remove_spaces(\"p y shi k ho\"))</p><p>print(remove_spaces(\"a b c\"))</p><div><br></div>', 'def remove_spaces(str1):\r\n    str1 = str1.replace(\' \',\'\')\r\n    return str1\r\n\r\nprint(remove_spaces(\"p y shi k ho\"))\r\nprint(remove_spaces(\"a b c\"))', 2, 0, '2023-09-13 00:35:35', '2023-09-12 18:35:35', '2023-09-12 18:35:48', 1),
(601, 'Write a Python program to move spaces to the front of a given string.', 'write-a-python-program-to-move-spaces-to-the-front-of-a-given-string', '<p>def move_Spaces_front(str1):</p><p>&nbsp; &nbsp; noSpaces_char = [ch for ch in str1 if ch!=\' \']</p><p>&nbsp; &nbsp; spaces_char = len(str1) - len(noSpaces_char)</p><p>&nbsp; &nbsp; result = \' \'*spaces_char</p><p>&nbsp; &nbsp; result = \'\"\'+result + \'\'.join(noSpaces_char)+\'\"\'</p><p>&nbsp; &nbsp; return(result)</p><p><br></p><p>print(move_Spaces_front(\"google .&nbsp; com&nbsp; \"))</p><p>print(move_Spaces_front(\"&nbsp; &nbsp;google.com&nbsp; \"))</p><div><br></div>', 'def move_Spaces_front(str1):\r\n    noSpaces_char = [ch for ch in str1 if ch!=\' \']\r\n    spaces_char = len(str1) - len(noSpaces_char)\r\n    result = \' \'*spaces_char\r\n    result = \'\"\'+result + \'\'.join(noSpaces_char)+\'\"\'\r\n    return(result)\r\n\r\nprint(move_Spaces_front(\"google .  com  \"))\r\nprint(move_Spaces_front(\"   google.com  \"))', 0, 0, '2023-09-13 00:43:13', '2023-09-12 18:43:13', '2023-09-12 18:43:13', 1),
(602, 'Write a Python program to find the maximum number of characters in a given string.', 'write-a-python-program-to-find-the-maximum-number-of-characters-in-a-given-string', '<p>def get_max_occuring_char(str1):</p><p>&nbsp; &nbsp; ASCII_SIZE = 256</p><p>&nbsp; &nbsp; ctr = [0] * ASCII_SIZE</p><p>&nbsp; &nbsp; max = -1</p><p>&nbsp; &nbsp; ch = \'\'</p><p>&nbsp; &nbsp; for i in str1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; ctr[ord(i)]+=1;</p><p><br></p><p>&nbsp; &nbsp; for i in str1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if max &lt; ctr[ord(i)]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; max = ctr[ord(i)]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ch = i</p><p>&nbsp; &nbsp; return ch</p><p><br></p><p>print(get_max_occuring_char(\"Python: Get file creation and modification date/times\"))</p><p>print(get_max_occuring_char(\"abcdefghijkb\"))</p><div><br></div>', 'def get_max_occuring_char(str1):\r\n    ASCII_SIZE = 256\r\n    ctr = [0] * ASCII_SIZE\r\n    max = -1\r\n    ch = \'\'\r\n    for i in str1:\r\n        ctr[ord(i)]+=1;\r\n\r\n    for i in str1:\r\n        if max < ctr[ord(i)]:\r\n            max = ctr[ord(i)]\r\n            ch = i\r\n    return ch\r\n\r\nprint(get_max_occuring_char(\"Python: Get file creation and modification date/times\"))\r\nprint(get_max_occuring_char(\"abcdefghijkb\"))', 4, 0, '2023-09-13 00:46:50', '2023-09-12 18:46:50', '2023-09-30 15:27:48', 1),
(603, 'Write a Python program to capitalize the first and last letters of each word in a given string.', 'write-a-python-program-to-capitalize-the-first-and-last-letters-of-each-word-in-a-given-string', '<p>def capitalize_first_last_letters(str1):</p><p>&nbsp; &nbsp; &nbsp;str1 = result = str1.title( )</p><p>&nbsp; &nbsp; &nbsp;result =&nbsp; \"\"</p><p>&nbsp; &nbsp; &nbsp;for word in str1.split():</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result += word[:-1] + word[-1].upper() + \" \"</p><p>&nbsp; &nbsp; &nbsp;return result[:-1]</p><p><br></p><p>print(capitalize_first_last_letters(\"python exercises practice solution\"))</p><p>print(capitalize_first_last_letters(\"pyshikho\"))</p><div><br></div>', 'def capitalize_first_last_letters(str1):\r\n     str1 = result = str1.title( )\r\n     result =  \"\"\r\n     for word in str1.split():\r\n          result += word[:-1] + word[-1].upper() + \" \"\r\n     return result[:-1]\r\n\r\nprint(capitalize_first_last_letters(\"python exercises practice solution\"))\r\nprint(capitalize_first_last_letters(\"pyshikho\"))', 2, 0, '2023-09-13 00:49:44', '2023-09-12 18:49:44', '2023-09-13 07:22:21', 1),
(604, 'Write a Python program to remove duplicate characters from a given string.', 'write-a-python-program-to-remove-duplicate-characters-from-a-given-string', '<p>from collections import OrderedDict</p><p><br></p><p>def remove_duplicate(str1):</p><p>&nbsp; &nbsp; return \"\".join(OrderedDict.fromkeys(str1))</p><p><br></p><p>print(remove_duplicate(\"python exercises practice solution\"))</p><p>print(remove_duplicate(\"Pyshikho\"))</p><div><br></div>', 'from collections import OrderedDict\r\n\r\ndef remove_duplicate(str1):\r\n    return \"\".join(OrderedDict.fromkeys(str1))\r\n\r\nprint(remove_duplicate(\"python exercises practice solution\"))\r\nprint(remove_duplicate(\"Pyshikho\"))', 0, 0, '2023-09-13 13:32:39', '2023-09-13 07:32:39', '2023-09-13 07:32:39', 1),
(605, 'Write a Python program to compute the sum of the digits in a given string.', 'write-a-python-program-to-compute-the-sum-of-the-digits-in-a-given-string', '<p>def sum_digits_string(str1):</p><p>&nbsp; &nbsp; sum_digit = 0</p><p>&nbsp; &nbsp; for x in str1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if x.isdigit() == True:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; z = int(x)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; sum_digit = sum_digit + z</p><p><br></p><p>&nbsp; &nbsp; return sum_digit</p><p><br></p><p>print(sum_digits_string(\"123abcd45\"))</p><p>print(sum_digits_string(\"abcd1234\"))</p><div><br></div>', 'def sum_digits_string(str1):\r\n    sum_digit = 0\r\n    for x in str1:\r\n        if x.isdigit() == True:\r\n            z = int(x)\r\n            sum_digit = sum_digit + z\r\n\r\n    return sum_digit\r\n\r\nprint(sum_digits_string(\"123abcd45\"))\r\nprint(sum_digits_string(\"abcd1234\"))', 0, 0, '2023-09-13 13:36:22', '2023-09-13 07:36:22', '2023-09-13 07:36:22', 1);
