(617, 'Write a Python program to find the minimum window in a given string that will contain all the characters of another given string.', 'write-a-python-program-to-find-the-minimum-window-in-a-given-string-that-will-contain-all-the-characters-of-another-given-string', '<p>import collections</p><p><br></p><p>def min_window(str1, str2):</p><p>&nbsp; &nbsp; result_char, missing_char = collections.Counter(str2), len(str2)</p><p>&nbsp; &nbsp; i = p = q = 0</p><p>&nbsp; &nbsp; for j, c in enumerate(str1, 1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; missing_char -= result_char[c] &gt; 0</p><p>&nbsp; &nbsp; &nbsp; &nbsp; result_char[c] -= 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if not missing_char:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; while i &lt; q and result_char[str1[i]] &lt; 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result_char[str1[i]] += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if not q or j - i &lt;= q - p:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p, q = i, j</p><p>&nbsp; &nbsp; return str1[p:q]</p><p><br></p><p>str1 = \"PRWSOERIUSFK\"</p><p>str2 = \"OSU\"</p><p>print(\"Original Strings:\\n\",str1,\"\\n\",str2)</p><p>print(\"Minimum window:\")</p><p>print(min_window(str1,str2))</p><div><br></div>', 'import collections\r\n\r\ndef min_window(str1, str2):\r\n    result_char, missing_char = collections.Counter(str2), len(str2)\r\n    i = p = q = 0\r\n    for j, c in enumerate(str1, 1):\r\n        missing_char -= result_char[c] > 0\r\n        result_char[c] -= 1\r\n        if not missing_char:\r\n            while i < q and result_char[str1[i]] < 0:\r\n                result_char[str1[i]] += 1\r\n                i += 1\r\n            if not q or j - i <= q - p:\r\n                p, q = i, j\r\n    return str1[p:q]\r\n\r\nstr1 = \"PRWSOERIUSFK\"\r\nstr2 = \"OSU\"\r\nprint(\"Original Strings:\\n\",str1,\"\\n\",str2)\r\nprint(\"Minimum window:\")\r\nprint(min_window(str1,str2))', 0, 0, '2023-09-13 14:42:00', '2023-09-13 08:42:00', '2023-09-13 08:42:00', 1),
(618, 'Write a Python program to find the smallest window that contains all characters in a given string.', 'write-a-python-program-to-find-the-smallest-window-that-contains-all-characters-in-a-given-string', '<p>from collections import defaultdict</p><p><br></p><p>def find_sub_string(str):</p><p>&nbsp; &nbsp; str_len = len(str)</p><p><br></p><p>&nbsp; &nbsp; # Count all distinct characters.</p><p>&nbsp; &nbsp; dist_count_char = len(set([x for x in str]))</p><p><br></p><p>&nbsp; &nbsp; ctr, start_pos, start_pos_index, min_len = 0, 0, -1, 9999999999</p><p>&nbsp; &nbsp; curr_count = defaultdict(lambda: 0)</p><p>&nbsp; &nbsp; for i in range(str_len):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; curr_count[str[i]] += 1</p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; if curr_count[str[i]] == 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr += 1</p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; if ctr == dist_count_char:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; while curr_count[str[start_pos]] &gt; 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if curr_count[str[start_pos]] &gt; 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; curr_count[str[start_pos]] -= 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; start_pos += 1</p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; len_window = i - start_pos + 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if min_len &gt; len_window:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; min_len = len_window</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; start_pos_index = start_pos</p><p>&nbsp; &nbsp; return str[start_pos_index: start_pos_index + min_len]</p><p><br></p><p>str1 = \"asdaewsqgtwwsa\"</p><p>print(\"Original Strings:\\n\",str1)</p><p>print(\"\\nSmallest window that contains all characters of the said string:\")</p><p>print(find_sub_string(str1))</p><div><br></div>', 'from collections import defaultdict\r\n\r\ndef find_sub_string(str):