(630, 'Write a Python program to find the common values that appear in two given strings.', 'write-a-python-program-to-find-the-common-values-that-appear-in-two-given-strings', '<p>def intersection_of_two_string(str1, str2):</p><p>&nbsp; &nbsp; result = \"\"</p><p>&nbsp; &nbsp; for ch in str1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if ch in str2 and not ch in result:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result += ch</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>str1 = \'Python3\'</p><p>str2 = \'Python2.7\'</p><p>print(\"Original strings:\")</p><p>print(str1)</p><p>print(str2)</p><p>print(\"\\nIntersection of two said String:\")</p><p>print(intersection_of_two_string(str1, str2))</p><div><br></div>', 'def intersection_of_two_string(str1, str2):\r\n    result = \"\"\r\n    for ch in str1:\r\n        if ch in str2 and not ch in result:\r\n            result += ch\r\n    return result\r\n\r\nstr1 = \'Python3\'\r\nstr2 = \'Python2.7\'\r\nprint(\"Original strings:\")\r\nprint(str1)\r\nprint(str2)\r\nprint(\"\\nIntersection of two said String:\")\r\nprint(intersection_of_two_string(str1, str2))', 0, 0, '2023-09-13 21:57:38', '2023-09-13 15:57:38', '2023-09-13 15:57:38', 1),
(631, 'Write a Python program to check whether a given string contains a capital letter, a lower case letter, a number and a minimum length.', 'write-a-python-program-to-check-whether-a-given-string-contains-a-capital-letter-a-lower-case-letter-a-number-and-a-minimum-length', '<p>def check_string(s):</p><p>&nbsp; &nbsp; messg = [ ]</p><p>&nbsp; &nbsp; if not any(x.isupper() for x in s):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; messg.append(\'String must have 1 upper case character.\')</p><p>&nbsp; &nbsp; if not any(x.islower() for x in s):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; messg.append(\'String must have 1 lower case character.\')</p><p>&nbsp; &nbsp; if not any(x.isdigit() for x in s):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; messg.append(\'String must have 1 number.\')</p><p>&nbsp; &nbsp; if len(s) &lt; 8:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; messg.append(\'String length should be atleast 8.\')</p><p>&nbsp; &nbsp; if not messg:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; messg.append(\'Valid string.\')</p><p>&nbsp; &nbsp; return messg</p><p><br></p><p>s = input(\"Input the string: \")</p><p>print(check_string(s))</p><div><br></div>', 'def check_string(s):\r\n    messg = [ ]\r\n    if not any(x.isupper() for x in s):\r\n        messg.append(\'String must have 1 upper case character.\')\r\n    if not any(x.islower() for x in s):\r\n        messg.append(\'String must have 1 lower case character.\')\r\n    if not any(x.isdigit() for x in s):\r\n        messg.append(\'String must have 1 number.\')\r\n    if len(s) < 8:\r\n        messg.append(\'String length should be atleast 8.\')\r\n    if not messg:\r\n        messg.append(\'Valid string.\')\r\n    return messg\r\n\r\n# Input the string\r\ns = input( )\r\nprint(check_string(s))', 2, 1, '2023-09-13 22:02:31', '2023-09-13 16:02:31', '2023-09-13 16:04:01', 1),
(632, 'Write a Python program to remove unwanted characters from a given string.', 'write-a-python-program-to-remove-unwanted-characters-from-a-given-string', '<p>def remove_chars(str1, unwanted_chars):</p><p>&nbsp; &nbsp; for i in unwanted_chars:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; str1 = str1.replace(i, \'\')</p><p>&nbsp; &nbsp; return str1</p><p><br></p><p>str1 = \"Pyth*^on Exercis^es\"</p><p>str2 = \"A%^!B#*CD\"</p><p><br></p><p>unwanted_chars = [\"#\", \"*\", \"!\", \"^\", \"%\"]</p><p>print (\"Original String : \" + str1)</p><p>print(\"After removing unwanted characters:\")</p><p>print(remove_chars(str1, unwanted_chars))</p><p>print (\"\\nOriginal String : \" + str2)</p><p>print(\"After removing unwanted characters:\")</p><p>print(remove_chars(str2, unwanted_chars))</p><div><br></div>', 'def remove_chars(str1, unwanted_chars):\r\n    for i in unwanted_chars:\r\n        str1 = str1.replace(i, \'\')\r\n    return str1\r\n\r\nstr1 = \"Pyth*^on Exercis^es\"\r\nstr2 = \"A%^!B#*CD\"\r\n\r\nunwanted_chars = [\"#\", \"*\", \"!\", \"^\", \"%\"]\r\nprint (\"Original String : \" + str1)\r\nprint(\"After removing unwanted characters:\")\r\nprint(remove_chars(str1, unwanted_chars))\r\nprint (\"\\nOriginal String : \" + str2)\r\nprint(\"After removing unwanted characters:\")\r\nprint(remove_chars(str2, unwanted_chars))', 0, 0, '2023-09-13 22:06:51', '2023-09-13 16:06:51', '2023-09-13 16:06:51', 1),
(633, 'Write a Python program to remove duplicate words from a given string.', 'write-a-python-program-to-remove-duplicate-words-from-a-given-string', '<p>def unique_list(text_str):</p><p>&nbsp; &nbsp; l = text_str.split( )</p><p>&nbsp; &nbsp; temp = [ ]</p><p>&nbsp; &nbsp; for x in l:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if x not in temp:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; temp.append(x)</p><p>&nbsp; &nbsp; return \' \'.join(temp)</p><p><br></p><p>text_str = \"Python Exercises Practice Solution Exercises\"</p><p>print(\"Original String:\")</p><p>print(text_str)</p><p>print(\"\\nAfter removing duplicate words from the said string:\")</p><p>print(unique_list(text_str))</p><div><br></div>', 'def unique_list(text_str):\r\n    l = text_str.split( )\r\n    temp = [ ]\r\n    for x in l:\r\n        if x not in temp:\r\n            temp.append(x)\r\n    return \' \'.join(temp)\r\n\r\ntext_str = \"Python Exercises Practice Solution Exercises\"\r\nprint(\"Original String:\")\r\nprint(text_str)\r\nprint(\"\\nAfter removing duplicate words from the said string:\")\r\nprint(unique_list(text_str))', 2, 0, '2023-09-13 22:09:38', '2023-09-13 16:09:38', '2023-09-19 08:30:40', 1),
(634, 'Write a Python program to convert a given heterogeneous list of scalars into a string.', 'write-a-python-program-to-convert-a-given-heterogeneous-list-of-scalars-into-a-string', '<p>def heterogeneous_list_to_str(lst):</p><p>&nbsp; &nbsp; result = \',\'.join(str(x) for x in lst)</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>h_data = [\"Red\", 100, -50, \"green\", \"w,3,r\", 12.12, False]</p><p>print(\"Original list:\")</p><p>print(h_data)</p><p>print(\"\\nConvert the heterogeneous list of scalars into a string:\")</p><p>print(heterogeneous_list_to_str(h_data))</p><div><br></div>', 'def heterogeneous_list_to_str(lst):\r\n    result = \',\'.join(str(x) for x in lst)\r\n    return result\r\n\r\nh_data = [\"Red\", 100, -50, \"green\", \"w,3,r\", 12.12, False]\r\nprint(\"Original list:\")\r\nprint(h_data)\r\nprint(\"\\nConvert the heterogeneous list of scalars into a string:\")\r\nprint(heterogeneous_list_to_str(h_data))', 0, 0, '2023-09-13 22:15:04', '2023-09-13 16:15:04', '2023-09-13 16:15:04', 1),
(635, 'Write a Python program to find string similarity between two given strings.', 'write-a-python-program-to-find-string-similarity-between-two-given-strings', '<p>import difflib</p><p><br></p><p>def string_similarity(str1, str2):</p><p>&nbsp; &nbsp; result =&nbsp; difflib.SequenceMatcher(a=str1.lower(), b=str2.lower( ))</p><p>&nbsp; &nbsp; return result.ratio( )</p><p><br></p><p>str1 = \'Python Exercises\'</p><p>str2 = \'Python Exercises\'</p><p>print(\"Original string:\")</p><p>print(str1)</p><p>print(str2)</p><p>print(\"Similarity between two said strings:\")</p><p>print(string_similarity(str1,str2))</p><p><br></p><p>str1 = \'Python Exercises\'</p><p>str2 = \'Python Exercise\'</p><p>print(\"\\nOriginal string:\")</p><p>print(str1)</p><p>print(str2)</p><p>print(\"Similarity between two said strings:\")</p><p>print(string_similarity(str1,str2))</p><p><br></p><p>str1 = \'Python Exercises\'</p><p>str2 = \'Python Ex.\'</p><p>print(\"\\nOriginal string:\")</p><p>print(str1)</p><p>print(str2)</p><p>print(\"Similarity between two said strings:\")</p><p>print(string_similarity(str1,str2))</p><p><br></p><p>str1 = \'Python Exercises\'</p><p>str2 = \'Python\'</p><p>print(\"\\nOriginal string:\")</p><p>print(str1)</p><p>print(str2)</p><p>print(\"Similarity between two said strings:\")</p><p>print(string_similarity(str1,str2))</p><p><br></p><p>str1 = \'Python Exercises\'</p><p>str1 = \'Java Exercises\'</p><p>print(\"\\nOriginal string:\")</p><p>print(str1)</p><p>print(str2)</p><p>print(\"Similarity between two said strings:\")</p><p>print(string_similarity(str1,str2))</p><div><br></div>', 'import difflib\r\n\r\ndef string_similarity(str1, str2):\r\n    result =  difflib.SequenceMatcher(a=str1.lower(), b=str2.lower( ))\r\n    return result.ratio( )\r\n\r\nstr1 = \'Python Exercises\'\r\nstr2 = \'Python Exercises\'\r\nprint(\"Original string:\")\r\nprint(str1)\r\nprint(str2)\r\nprint(\"Similarity between two said strings:\")\r\nprint(string_similarity(str1,str2))\r\n\r\nstr1 = \'Python Exercises\'\r\nstr2 = \'Python Exercise\'\r\nprint(\"\\nOriginal string:\")\r\nprint(str1)\r\nprint(str2)\r\nprint(\"Similarity between two said strings:\")\r\nprint(string_similarity(str1,str2))\r\n\r\nstr1 = \'Python Exercises\'\r\nstr2 = \'Python Ex.\'\r\nprint(\"\\nOriginal string:\")\r\nprint(str1)\r\nprint(str2)\r\nprint(\"Similarity between two said strings:\")\r\nprint(string_similarity(str1,str2))\r\n\r\nstr1 = \'Python Exercises\'\r\nstr2 = \'Python\'\r\nprint(\"\\nOriginal string:\")\r\nprint(str1)\r\nprint(str2)\r\nprint(\"Similarity between two said strings:\")\r\nprint(string_similarity(str1,str2))\r\n\r\nstr1 = \'Python Exercises\'\r\nstr1 = \'Java Exercises\'\r\nprint(\"\\nOriginal string:\")\r\nprint(str1)\r\nprint(str2)\r\nprint(\"Similarity between two said strings:\")\r\nprint(string_similarity(str1,str2))', 0, 0, '2023-09-13 22:20:13', '2023-09-13 16:20:13', '2023-09-13 16:20:13', 0),
(636, 'Write a Python program to extract numbers from a given string.', 'write-a-python-program-to-extract-numbers-from-a-given-string', '<p>def test(str1):</p><p>&nbsp; &nbsp; result = [int(str1) for str1 in str1.split() if str1.isdigit( )]</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>str1 = \"red 12 black 45 green\"</p><p>print(\"Original string:\", str1)</p><p>print(\"Extract numbers from the said string:\")</p><p>print(test(str1))</p><div><br></div>', 'def test(str1):\r\n    result = [int(str1) for str1 in str1.split() if str1.isdigit( )]\r\n    return result\r\n\r\nstr1 = \"red 12 black 45 green\"\r\nprint(\"Original string:\", str1)\r\nprint(\"Extract numbers from the said string:\")\r\nprint(test(str1))', 0, 0, '2023-09-13 22:22:01', '2023-09-13 16:22:01', '2023-09-13 16:22:01', 1),
(637, 'Write a Python program to convert a hexadecimal color code to a tuple of integers corresponding to its RGB components.', 'write-a-python-program-to-convert-a-hexadecimal-color-code-to-a-tuple-of-integers-corresponding-to-its-rgb-components', '<p>def hex_to_rgb(hex):</p><p>&nbsp; &nbsp; return tuple(int(hex[i:i+2], 16) for i in (0, 2, 4))</p><p><br></p><p>print(hex_to_rgb(\'FFA501\'))</p><p>print(hex_to_rgb(\'FFFFFF\'))</p><p>print(hex_to_rgb(\'000000\'))</p><p>print(hex_to_rgb(\'FF0000\'))</p><p>print(hex_to_rgb(\'000080\'))</p><p>print(hex_to_rgb(\'C0C0C0\'))</p><div><br></div>', 'def hex_to_rgb(hex):\r\n    return tuple(int(hex[i:i+2], 16) for i in (0, 2, 4))\r\n\r\nprint(hex_to_rgb(\'FFA501\'))\r\nprint(hex_to_rgb(\'FFFFFF\'))\r\nprint(hex_to_rgb(\'000000\'))\r\nprint(hex_to_rgb(\'FF0000\'))\r\nprint(hex_to_rgb(\'000080\'))\r\nprint(hex_to_rgb(\'C0C0C0\'))', 0, 0, '2023-09-13 22:43:19', '2023-09-13 16:43:19', '2023-09-13 16:43:19', 1),
(638, 'Write a Python program to convert the values of RGB components to a hexadecimal color code.', 'write-a-python-program-to-convert-the-values-of-rgb-components-to-a-hexadecimal-color-code', '<p>def rgb_to_hex(r, g, b):</p><p>&nbsp; &nbsp; return (\'{:02X}\' * 3).format(r, g, b)</p><p><br></p><p>print(rgb_to_hex(255, 165, 1))</p><p>print(rgb_to_hex(255, 255, 255))</p><p>print(rgb_to_hex(0, 0, 0))</p><p>print(rgb_to_hex(0, 0, 128))</p><p>print(rgb_to_hex(192, 192, 192))</p><div><br></div>', 'def rgb_to_hex(r, g, b):\r\n    return (\'{:02X}\' * 3).format(r, g, b)\r\n\r\nprint(rgb_to_hex(255, 165, 1))\r\nprint(rgb_to_hex(255, 255, 255))\r\nprint(rgb_to_hex(0, 0, 0))\r\nprint(rgb_to_hex(0, 0, 128))\r\nprint(rgb_to_hex(192, 192, 192))', 0, 0, '2023-09-13 22:45:53', '2023-09-13 16:45:53', '2023-09-13 16:45:53', 1);
