(639, 'Write a Python program to convert a given string to Camelcase.', 'write-a-python-program-to-convert-a-given-string-to-camelcase', '<p>from re import sub</p><p><br></p><p>def camel_case(s):</p><p>&nbsp; &nbsp; s = sub(r\"(_|-)+\", \" \", s).title().replace(\" \", \"\")</p><p>&nbsp; &nbsp; return \'\'.join([s[0].lower(), s[1:]])</p><p><br></p><p>print(camel_case(\'JavaScript\'))</p><p>print(camel_case(\'Foo-Bar\'))</p><p>print(camel_case(\'foo_bar\'))</p><p>print(camel_case(\'--foo.bar\'))</p><p>print(camel_case(\'Foo-BAR\'))</p><p>print(camel_case(\'fooBAR\'))</p><p>print(camel_case(\'foo bar\'))</p><div><br></div>', 'from re import sub\r\n\r\ndef camel_case(s):\r\n    s = sub(r\"(_|-)+\", \" \", s).title().replace(\" \", \"\")\r\n    return \'\'.join([s[0].lower(), s[1:]])\r\n\r\nprint(camel_case(\'JavaScript\'))\r\nprint(camel_case(\'Foo-Bar\'))\r\nprint(camel_case(\'foo_bar\'))\r\nprint(camel_case(\'--foo.bar\'))\r\nprint(camel_case(\'Foo-BAR\'))\r\nprint(camel_case(\'fooBAR\'))\r\nprint(camel_case(\'foo bar\'))', 0, 0, '2023-09-13 22:52:01', '2023-09-13 16:52:01', '2023-09-13 16:52:01', 1),
(640, 'Write a Python program to convert a given string to Snake case.', 'write-a-python-program-to-convert-a-given-string-to-snake-case', '<p>from re import sub</p><p><br></p><p>def snake_case(s):</p><p>&nbsp; &nbsp; return \'_\'.join(</p><p>&nbsp; &nbsp; &nbsp; sub(\'([A-Z][a-z]+)\', r\' \\1\',</p><p>&nbsp; &nbsp; &nbsp; sub(\'([A-Z]+)\', r\' \\1\',</p><p>&nbsp; &nbsp; &nbsp; s.replace(\'-\', \' \'))).split( )).lower( )</p><p><br></p><p>print(snake_case(\'JavaScript\'))</p><p>print(snake_case(\'Foo-Bar\'))</p><p>print(snake_case(\'foo_bar\'))</p><p>print(snake_case(\'--foo.bar\'))</p><p>print(snake_case(\'Foo-BAR\'))</p><p>print(snake_case(\'fooBAR\'))</p><p>print(snake_case(\'foo bar\'))</p><div><br></div>', 'from re import sub\r\n\r\ndef snake_case(s):\r\n    return \'_\'.join(\r\n      sub(\'([A-Z][a-z]+)\', r\' \\1\',\r\n      sub(\'([A-Z]+)\', r\' \\1\',\r\n      s.replace(\'-\', \' \'))).split( )).lower( )\r\n\r\nprint(snake_case(\'JavaScript\'))\r\nprint(snake_case(\'Foo-Bar\'))\r\nprint(snake_case(\'foo_bar\'))\r\nprint(snake_case(\'--foo.bar\'))\r\nprint(snake_case(\'Foo-BAR\'))\r\nprint(snake_case(\'fooBAR\'))\r\nprint(snake_case(\'foo bar\'))', 0, 0, '2023-09-13 23:02:43', '2023-09-13 17:02:43', '2023-09-13 17:02:43', 1),
(641, 'Write a Python program to decapitalize the first letter of a given string.', 'write-a-python-program-to-decapitalize-the-first-letter-of-a-given-string', '<p>def decapitalize_first_letter(s, upper_rest = False):</p><p>&nbsp; &nbsp; return \'\'.join([s[:1].lower(), (s[1:].upper() if upper_rest else s[1:])])</p><p><br></p><p>print(decapitalize_first_letter(\'Java Script\'))</p><p>print(decapitalize_first_letter(\'Python\'))</p><div><br></div>', 'def decapitalize_first_letter(s, upper_rest = False):\r\n    return \'\'.join([s[:1].lower(), (s[1:].upper() if upper_rest else s[1:])])\r\n\r\nprint(decapitalize_first_letter(\'Java Script\'))\r\nprint(decapitalize_first_letter(\'Python\'))', 0, 0, '2023-09-13 23:04:30', '2023-09-13 17:04:30', '2023-09-13 17:04:30', 1),
