(671, 'Write a Python program to flatten a shallow list.', 'write-a-python-program-to-flatten-a-shallow-list', '<p>import itertools</p><p><br></p><p>original_list = [[2,4,3],[1,5,6], [9], [7,9,0]]</p><p><br></p><p>new_merged_list = list(itertools.chain(*original_list))</p><p>print(new_merged_list)</p><div><br></div>', 'import itertools\r\n\r\noriginal_list = [[2,4,3],[1,5,6], [9], [7,9,0]]\r\n\r\nnew_merged_list = list(itertools.chain(*original_list))\r\nprint(new_merged_list)', 0, 0, '2023-09-14 17:55:56', '2023-09-14 11:55:56', '2023-09-14 11:55:56', 1),
(672, 'Write a Python program to find the second smallest number in a list.', 'write-a-python-program-to-find-the-second-smallest-number-in-a-list', '<p>def second_smallest(numbers):</p><p>&nbsp; &nbsp; if (len(numbers)&lt;2):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return</p><p>&nbsp; &nbsp; if ((len(numbers)==2)&nbsp; and (numbers[0] == numbers[1]) ):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return</p><p>&nbsp; &nbsp; dup_items = set( )</p><p>&nbsp; &nbsp; uniq_items = [ ]</p><p>&nbsp; &nbsp; for x in numbers:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if x not in dup_items:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; uniq_items.append(x)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; dup_items.add(x)</p><p>&nbsp; &nbsp; uniq_items.sort( )</p><p>&nbsp; &nbsp; return&nbsp; uniq_items[1]</p><p><br></p><p>print(second_smallest([1, 2, -8, -2, 0, -2]))</p><p>print(second_smallest([1, 1, 0, 0, 2, -2, -2]))</p><p>print(second_smallest([1, 1, 1, 0, 0, 0, 2, -2, -2]))</p><p>print(second_smallest([2,2]))</p><p>print(second_smallest([2]))</p><div><br></div>', 'def second_smallest(numbers):\r\n    if (len(numbers)<2):\r\n        return\r\n    if ((len(numbers)==2)  and (numbers[0] == numbers[1]) ):\r\n        return\r\n    dup_items = set( )\r\n    uniq_items = [ ]\r\n    for x in numbers:\r\n        if x not in dup_items:\r\n            uniq_items.append(x)\r\n            dup_items.add(x)\r\n    uniq_items.sort( )\r\n    return  uniq_items[1]\r\n\r\nprint(second_smallest([1, 2, -8, -2, 0, -2]))\r\nprint(second_smallest([1, 1, 0, 0, 2, -2, -2]))\r\nprint(second_smallest([1, 1, 1, 0, 0, 0, 2, -2, -2]))\r\nprint(second_smallest([2,2]))\r\nprint(second_smallest([2]))', 4, 0, '2023-09-14 18:24:45', '2023-09-14 12:24:45', '2023-09-30 14:07:34', 1),
(673, 'Write a Python program to get the frequency of elements in a list.', 'write-a-python-program-to-get-the-frequency-of-elements-in-a-list', '<p>import collections</p><p><br></p><p>my_list = [10,10,10,10,20,20,20,20,40,40,50,50,30]</p><p><br></p><p>print(\"Original List : \",my_list)</p><p>ctr = collections.Counter(my_list)</p><p>print(\"Frequency of the elements in the List : \",ctr)</p><div><br></div>', 'import collections\r\n\r\nmy_list = [10,10,10,10,20,20,20,20,40,40,50,50,30]\r\n\r\nprint(\"Original List : \",my_list)\r\nctr = collections.Counter(my_list)\r\nprint(\"Frequency of the elements in the List : \",ctr)', 6, 0, '2023-09-14 18:33:42', '2023-09-14 12:33:42', '2023-09-26 19:22:10', 1),
(674, 'Write a Python program to count the number of elements in a list within a specified range.', 'write-a-python-program-to-count-the-number-of-elements-in-a-list-within-a-specified-range', '<p>def count_range_in_list(li, min, max):</p><p>&nbsp; &nbsp; ctr = 0</p><p>&nbsp; &nbsp; for x in li:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if min &lt;= x &lt;= max:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr += 1</p><p>&nbsp; &nbsp; return ctr</p><p><br></p><p>list1 = [10,20,30,40,40,40,70,80,99]</p><p>print(count_range_in_list(list1, 40, 100))</p><p><br></p><p>list2 = [\'a\',\'b\',\'c\',\'d\',\'e\',\'f\']</p><p>print(count_range_in_list(list2, \'a\', \'e\'))</p><div><br></div>', 'def count_range_in_list(li, min, max):\r\n    ctr = 0\r\n    for x in li:\r\n        if min <= x <= max:\r\n            ctr += 1\r\n    return ctr\r\n\r\nlist1 = [10,20,30,40,40,40,70,80,99]\r\nprint(count_range_in_list(list1, 40, 100))\r\n\r\nlist2 = [\'a\',\'b\',\'c\',\'d\',\'e\',\'f\']\r\nprint(count_range_in_list(list2, \'a\', \'e\'))', 0, 0, '2023-09-14 21:37:15', '2023-09-14 15:37:15', '2023-09-14 15:37:15', 1),
(675, 'Write a Python program to check whether a list contains a sublist.', 'write-a-python-program-to-check-whether-a-list-contains-a-sublist', '<p>def is_Sublist(l, s):</p><p>&nbsp; &nbsp; sub_set = False</p><p>&nbsp; &nbsp; if s == [ ]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; sub_set = True</p><p>&nbsp; &nbsp; elif s == l:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; sub_set = True</p><p>&nbsp; &nbsp; elif len(s) &gt; len(l):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; sub_set = False</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; for i in range(len(l)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if l[i] == s[0]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; n = 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; while (n &lt; len(s)) and (l[i+n] == s[n]):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; n += 1</p><p><span style=\"white-space: pre; white-space: normal;\">				</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if n == len(s):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; sub_set = True</p><p><br></p><p>&nbsp; &nbsp; return sub_set</p><p><br></p><p>a = [2,4,3,5,7]</p><p>b = [4,3]</p><p>c = [3,7]</p><p>print(is_Sublist(a, b))</p><p>print(is_Sublist(a, c))</p><div><br></div>', 'def is_Sublist(l, s):\r\n    sub_set = False\r\n    if s == [ ]:\r\n        sub_set = True\r\n    elif s == l:\r\n        sub_set = True\r\n    elif len(s) > len(l):\r\n        sub_set = False\r\n    else:\r\n        for i in range(len(l)):\r\n            if l[i] == s[0]:\r\n                n = 1\r\n                while (n < len(s)) and (l[i+n] == s[n]):\r\n                    n += 1\r\n				\r\n                if n == len(s):\r\n                    sub_set = True\r\n\r\n    return sub_set\r\n\r\na = [2,4,3,5,7]\r\nb = [4,3]\r\nc = [3,7]\r\nprint(is_Sublist(a, b))\r\nprint(is_Sublist(a, c))', 0, 0, '2023-09-14 21:45:00', '2023-09-14 15:45:00', '2023-09-14 15:45:00', 1),
(676, 'Write a Python program to generate all sublists of a list.', 'write-a-python-program-to-generate-all-sublists-of-a-list', '<p>from itertools import combinations</p><p><br></p><p>def sub_lists(my_list):</p><p>&nbsp; &nbsp; subs = [ ]</p><p>&nbsp; &nbsp; for i in range(0, len(my_list)+1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; temp = [list(x) for x in combinations(my_list, i)]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if len(temp)&gt;0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; subs.extend(temp)</p><p>&nbsp; &nbsp; return subs</p><p><br></p><p>l1 = [10, 20, 30, 40]</p><p>l2 = [\'X\', \'Y\', \'Z\']</p><p><br></p><p>print(\"Original list:\")</p><p>print(l1)</p><p>print(\"Sublists of the said list:\")</p><p>print(sub_lists(l1))</p><p><br></p><p>print(\"\\nOriginal list:\")</p><p>print(l2)</p><p>print(\"Sublists of the said list:\")</p><p>print(sub_lists(l2))</p><div><br></div>', 'from itertools import combinations\r\n\r\ndef sub_lists(my_list):\r\n    subs = [ ]\r\n    for i in range(0, len(my_list)+1):\r\n        temp = [list(x) for x in combinations(my_list, i)]\r\n        if len(temp)>0:\r\n            subs.extend(temp)\r\n    return subs\r\n\r\nl1 = [10, 20, 30, 40]\r\nl2 = [\'X\', \'Y\', \'Z\']\r\n\r\nprint(\"Original list:\")\r\nprint(l1)\r\nprint(\"Sublists of the said list:\")\r\nprint(sub_lists(l1))\r\n\r\nprint(\"\\nOriginal list:\")\r\nprint(l2)\r\nprint(\"Sublists of the said list:\")\r\nprint(sub_lists(l2))', 0, 0, '2023-09-14 22:20:38', '2023-09-14 16:20:38', '2023-09-14 16:20:38', 1),
(677, 'Write a Python program to create a list by concatenating a given list with a range from 1 to n.', 'write-a-python-program-to-create-a-list-by-concatenating-a-given-list-with-a-range-from-1-to-n', '<p>my_list = [\'p\', \'q\']</p><p>n = 4</p><p><br></p><p>new_list = [\'{}{}\'.format(x, y) for y in range(1, n+1) for x in my_list]</p><p>print(new_list)</p><div><br></div>', 'my_list = [\'p\', \'q\']\r\nn = 4\r\n\r\nnew_list = [\'{}{}\'.format(x, y) for y in range(1, n+1) for x in my_list]\r\nprint(new_list)', 0, 0, '2023-09-14 23:01:35', '2023-09-14 17:01:35', '2023-09-14 17:01:35', 1);
