(678, 'Write a Python program to find common items in two lists.', 'write-a-python-program-to-find-common-items-in-two-lists', '<p>color1 = \"Red\", \"Green\", \"Orange\", \"White\"</p><p>color2 = \"Black\", \"Green\", \"White\", \"Pink\"</p><p><br></p><p>print(set(color1) &amp; set(color2))</p><div><br></div>', 'color1 = \"Red\", \"Green\", \"Orange\", \"White\"\r\ncolor2 = \"Black\", \"Green\", \"White\", \"Pink\"\r\n\r\nprint(set(color1) & set(color2))', 0, 0, '2023-09-14 23:12:04', '2023-09-14 17:12:04', '2023-09-14 17:12:04', 1),
(679, 'Write a Python program to change the position of every n-th value to the (n+1)th in a list.', 'write-a-python-program-to-change-the-position-of-every-n-th-value-to-the-n1th-in-a-list', '<p>from itertools import zip_longest, chain, tee</p><p><br></p><p>def replace2copy(lst):</p><p>&nbsp; &nbsp; lst1, lst2 = tee(iter(lst), 2)</p><p>&nbsp; &nbsp; return list(chain.from_iterable(zip_longest(lst[1::2], lst[::2])))</p><p><br></p><p>n = [0,1,2,3,4,5]</p><p>print(replace2copy(n))</p><div><br></div>', 'from itertools import zip_longest, chain, tee\r\n\r\ndef replace2copy(lst):\r\n    lst1, lst2 = tee(iter(lst), 2)\r\n    return list(chain.from_iterable(zip_longest(lst[1::2], lst[::2])))\r\n\r\nn = [0,1,2,3,4,5]\r\nprint(replace2copy(n))', 0, 0, '2023-09-14 23:13:51', '2023-09-14 17:13:51', '2023-09-14 17:13:51', 1),
(680, 'Write a Python program to convert a list of multiple integers into a single integer.', 'write-a-python-program-to-convert-a-list-of-multiple-integers-into-a-single-integer', '<p>L = [11, 33, 50]</p><p><br></p><p>print(\"Original List: \",L)</p><p><br></p><p>x = int(\"\".join(map(str, L)))</p><p><br></p><p>print(\"Single Integer: \",x)</p><div><br></div>', 'L = [11, 33, 50]\r\n\r\nprint(\"Original List: \",L)\r\n\r\nx = int(\"\".join(map(str, L)))\r\n\r\nprint(\"Single Integer: \",x)', 0, 0, '2023-09-14 23:15:45', '2023-09-14 17:15:44', '2023-09-14 17:15:44', 1),
(681, 'Write a Python program to split a list based on the first character of a word.', 'write-a-python-program-to-split-a-list-based-on-the-first-character-of-a-word', '<p>from itertools import groupby</p><p>from operator import itemgetter</p><p><br></p><p>word_list = [\'be\',\'have\',\'do\',\'say\',\'get\',\'make\',\'go\',\'know\',\'take\',\'see\',\'come\',\'think\',\'look\',\'want\',\'give\',\'use\',\'find\',\'tell\',\'ask\',\'work\',\'seem\',\'feel\',\'leave\',\'call\']</p><p><br></p><p>for letter, words in groupby(sorted(word_list), key=itemgetter(0)):</p><p>&nbsp; &nbsp; print(letter)</p><p>&nbsp; &nbsp; for word in words:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(word)</p><div><br></div>', 'from itertools import groupby\r\nfrom operator import itemgetter\r\n\r\nword_list = [\'be\',\'have\',\'do\',\'say\',\'get\',\'make\',\'go\',\'know\',\'take\',\'see\',\'come\',\'think\',\'look\',\'want\',\'give\',\'use\',\'find\',\'tell\',\'ask\',\'work\',\'seem\',\'feel\',\'leave\',\'call\']\r\n\r\nfor letter, words in groupby(sorted(word_list), key=itemgetter(0)):\r\n    print(letter)\r\n    for word in words:\r\n        print(word)', 4, 0, '2023-09-14 23:18:22', '2023-09-14 17:18:22', '2023-09-18 13:31:20', 1),
