(716, 'Write a Python program to create a list reflecting the run-length encoding from a given list of integers or a given list of characters.', 'write-a-python-program-to-create-a-list-reflecting-the-run-length-encoding-from-a-given-list-of-integers-or-a-given-list-of-characters', '<p>from itertools import groupby</p><p><br></p><p>def encode_list(s_list):</p><p>&nbsp; &nbsp; return [[len(list(group)), key] for key, group in groupby(s_list)]</p><p><br></p><p>n_list = [1,1,2,3,4,4.3,5, 1]</p><p>print(\"Original list:\")</p><p>print(n_list)</p><p>print(\"\\nList reflecting the run-length encoding from the said list:\")</p><p>print(encode_list(n_list))</p><p><br></p><p>n_list = \'automatically\'</p><p>print(\"\\nOriginal String:\")</p><p>print(n_list)</p><p>print(\"\\nList reflecting the run-length encoding from the said string:\")</p><p>print(encode_list(n_list))</p><div><br></div>', 'from itertools import groupby\r\n\r\ndef encode_list(s_list):\r\n    return [[len(list(group)), key] for key, group in groupby(s_list)]\r\n\r\nn_list = [1,1,2,3,4,4.3,5, 1]\r\nprint(\"Original list:\")\r\nprint(n_list)\r\nprint(\"\\nList reflecting the run-length encoding from the said list:\")\r\nprint(encode_list(n_list))\r\n\r\nn_list = \'automatically\'\r\nprint(\"\\nOriginal String:\")\r\nprint(n_list)\r\nprint(\"\\nList reflecting the run-length encoding from the said string:\")\r\nprint(encode_list(n_list))', 0, 0, '2023-09-18 19:23:13', '2023-09-18 13:23:13', '2023-09-18 13:23:13', 1),
(717, 'Write a Python program to create a list reflecting the modified run-length encoding from a given list of integers or a given list of characters.', 'write-a-python-program-to-create-a-list-reflecting-the-modified-run-length-encoding-from-a-given-list-of-integers-or-a-given-list-of-characters', '<p>from itertools import groupby</p><p><br></p><p>def modified_encode(alist):</p><p>&nbsp; &nbsp; def ctr_ele(el):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if len(el)&gt;1: return [len(el), el[0]]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else: return el[0]</p><p>&nbsp; &nbsp; return [ctr_ele(list(group)) for key, group in groupby(alist)]</p><p><br></p><p>n_list = [1,1,2,3,4,4,5, 1]</p><p>print(\"Original list:\")</p><p>print(n_list)</p><p>print(\"\\nList reflecting the modified run-length encoding from the said list:\")</p><p>print(modified_encode(n_list))</p><p><br></p><p>n_list = \'aabcddddadnss\'</p><p>print(\"\\nOriginal String:\")</p><p>print(n_list)</p><p>print(\"\\nList reflecting the modified run-length encoding from the said string:\")</p><p>print(modified_encode(n_list))</p><div><br></div>', 'from itertools import groupby\r\n\r\ndef modified_encode(alist):\r\n    def ctr_ele(el):\r\n        if len(el)>1: return [len(el), el[0]]\r\n        else: return el[0]\r\n    return [ctr_ele(list(group)) for key, group in groupby(alist)]\r\n\r\nn_list = [1,1,2,3,4,4,5, 1]\r\nprint(\"Original list:\")\r\nprint(n_list)\r\nprint(\"\\nList reflecting the modified run-length encoding from the said list:\")\r\nprint(modified_encode(n_list))\r\n\r\nn_list = \'aabcddddadnss\'\r\nprint(\"\\nOriginal String:\")\r\nprint(n_list)\r\nprint(\"\\nList reflecting the modified run-length encoding from the said string:\")\r\nprint(modified_encode(n_list))', 0, 0, '2023-09-18 19:26:53', '2023-09-18 13:26:53', '2023-09-18 13:26:53', 1),
(718, 'Write a Python program to decode a run-length message.', 'write-a-python-program-to-decode-a-run-length-message', '<p>def decode(alist):</p><p>&nbsp; &nbsp; def aux(g):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(g, list):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return [(g[1], range(g[0]))]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return [(g, [0])]</p><p>&nbsp; &nbsp; return [x for g in alist for x, R in aux(g) for i in R]</p><p><br></p><p>n_list = [[2, 1], 2, 3, [2, 4], 5, 1]</p><p>print(\"Original encoded list:\")</p><p>print(n_list)</p><p>print(\"\\nDecode a run-length encoded said list:\")</p><p>print(decode(n_list))</p><div><br></div>', 'def decode(alist):\r\n    def aux(g):\r\n        if isinstance(g, list):\r\n            return [(g[1], range(g[0]))]\r\n        else:\r\n            return [(g, [0])]\r\n    return [x for g in alist for x, R in aux(g) for i in R]\r\n\r\nn_list = [[2, 1], 2, 3, [2, 4], 5, 1]\r\nprint(\"Original encoded list:\")\r\nprint(n_list)\r\nprint(\"\\nDecode a run-length encoded said list:\")\r\nprint(decode(n_list))', 0, 0, '2023-09-18 19:29:09', '2023-09-18 13:29:09', '2023-09-18 13:29:09', 1),
