(731, 'Write a Python program to find a list with maximum and minimum lengths.', 'write-a-python-program-to-find-a-list-with-maximum-and-minimum-lengths', '<p>def max_length_list(input_list):</p><p>&nbsp; &nbsp; max_length = max(len(x) for x in input_list )</p><p>&nbsp; &nbsp; max_list = max(input_list, key = len)</p><p>&nbsp; &nbsp; return(max_length, max_list)</p><p><br></p><p>def min_length_list(input_list):</p><p>&nbsp; &nbsp; min_length = min(len(x) for x in input_list )</p><p>&nbsp; &nbsp; min_list = min(input_list, key = len)</p><p>&nbsp; &nbsp; return(min_length, min_list)</p><p><br></p><p>list1 = [[0], [1, 3], [5, 7], [9, 11], [13, 15, 17]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(\"\\nList with maximum length of lists:\")</p><p>print(max_length_list(list1))</p><p>print(\"\\nList with minimum length of lists:\")</p><p>print(min_length_list(list1))</p><p><br></p><p>list1 =&nbsp; [[0], [1, 3], [5, 7], [9, 11], [3, 5, 7]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(\"\\nList with maximum length of lists:\")</p><p>print(max_length_list(list1))</p><p>print(\"\\nList with minimum length of lists:\")</p><p>print(min_length_list(list1))</p><p><br></p><p>list1 =&nbsp; [[12], [1, 3], [1, 34, 5, 7], [9, 11], [3, 5, 7]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(\"\\nList with maximum length of lists:\")</p><p>print(max_length_list(list1))</p><p>print(\"\\nList with minimum length of lists:\")</p><p>print(min_length_list(list1))</p><div><br></div>', 'def max_length_list(input_list):\r\n    max_length = max(len(x) for x in input_list )\r\n    max_list = max(input_list, key = len)\r\n    return(max_length, max_list)\r\n\r\ndef min_length_list(input_list):\r\n    min_length = min(len(x) for x in input_list )\r\n    min_list = min(input_list, key = len)\r\n    return(min_length, min_list)\r\n\r\nlist1 = [[0], [1, 3], [5, 7], [9, 11], [13, 15, 17]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(\"\\nList with maximum length of lists:\")\r\nprint(max_length_list(list1))\r\nprint(\"\\nList with minimum length of lists:\")\r\nprint(min_length_list(list1))\r\n\r\nlist1 =  [[0], [1, 3], [5, 7], [9, 11], [3, 5, 7]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(\"\\nList with maximum length of lists:\")\r\nprint(max_length_list(list1))\r\nprint(\"\\nList with minimum length of lists:\")\r\nprint(min_length_list(list1))\r\n\r\nlist1 =  [[12], [1, 3], [1, 34, 5, 7], [9, 11], [3, 5, 7]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(\"\\nList with maximum length of lists:\")\r\nprint(max_length_list(list1))\r\nprint(\"\\nList with minimum length of lists:\")\r\nprint(min_length_list(list1))', 2, 0, '2023-09-18 21:23:33', '2023-09-18 15:23:33', '2023-09-19 11:38:17', 1),
(732, 'Write a Python program to check if a nested list is a subset of another nested list.', 'write-a-python-program-to-check-if-a-nested-list-is-a-subset-of-another-nested-list', '<p>def checkSubset(input_list1, input_list2):</p><p>&nbsp; &nbsp; return all(map(input_list1.__contains__, input_list2))</p><p><br></p><p>list1 = [[1, 3], [5, 7], [9, 11], [13, 15, 17]]</p><p>list2 = [[1, 3],[13,15,17]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(list2)</p><p>print(\"\\nIf the one of the said list is a subset of another.:\")</p><p>print(checkSubset(list1, list2))</p><p><br></p><p>list1 = [[[1,2],[2,3]],[[3,4],[5,6]]]</p><p>list2 = [[[3,4], [5, 6]]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(list2)</p><p>print(\"\\nIf the one of the said list is a subset of another.:\")</p><p>print(checkSubset(list1, list2))</p><p><br></p><p>list1 = [[[1,2],[2,3]],[[3,4],[5,7]]]</p><p>list2 = [[[3,4], [5, 6]]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(list2)</p><p>print(\"\\nIf the one of the said list is a subset of another.:\")</p><p>print(checkSubset(list1, list2))</p><div><br></div>', 'def checkSubset(input_list1, input_list2):\r\n    return all(map(input_list1.__contains__, input_list2))\r\n\r\nlist1 = [[1, 3], [5, 7], [9, 11], [13, 15, 17]]\r\nlist2 = [[1, 3],[13,15,17]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(list2)\r\nprint(\"\\nIf the one of the said list is a subset of another.:\")\r\nprint(checkSubset(list1, list2))\r\n\r\nlist1 = [[[1,2],[2,3]],[[3,4],[5,6]]]\r\nlist2 = [[[3,4], [5, 6]]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(list2)\r\nprint(\"\\nIf the one of the said list is a subset of another.:\")\r\nprint(checkSubset(list1, list2))\r\n\r\nlist1 = [[[1,2],[2,3]],[[3,4],[5,7]]]\r\nlist2 = [[[3,4], [5, 6]]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(list2)\r\nprint(\"\\nIf the one of the said list is a subset of another.:\")\r\nprint(checkSubset(list1, list2))', 0, 0, '2023-09-18 21:56:42', '2023-09-18 15:56:42', '2023-09-18 15:56:42', 1),
(733, 'Write a Python program to count the number of sublists that contain a particular element.', 'write-a-python-program-to-count-the-number-of-sublists-that-contain-a-particular-element', '<p>def count_element_in_list(input_list, x):</p><p>&nbsp; &nbsp; ctr = 0</p><p>&nbsp; &nbsp; for i in range(len(input_list)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if x in input_list[i]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr+= 1</p><p><br></p><p>&nbsp; &nbsp; return ctr</p><p><br></p><p>list1 = [[1, 3], [5, 7], [1, 11], [1, 15, 7]]</p><p>print(\"Original list:\")</p><p>print(list1)</p><p>print(\"\\nCount 1 in the said list:\")</p><p>print(count_element_in_list(list1, 1))</p><p>print(\"\\nCount 7 in the said list:\")</p><p>print(count_element_in_list(list1, 7))</p><p><br></p><p>list1 = [[\'A\', \'B\'], [\'A\', \'C\'], [\'A\', \'D\', \'E\'], [\'B\', \'C\', \'D\']]</p><p>print(\"\\nOriginal list:\")</p><p>print(list1)</p><p>print(\"\\nCount \'A\' in the said list:\")</p><p>print(count_element_in_list(list1, \'A\'))</p><p>print(\"\\nCount \'E\' in the said list:\")</p><p>print(count_element_in_list(list1, \'E\'))</p><div><br></div>', 'def count_element_in_list(input_list, x):\r\n    ctr = 0\r\n    for i in range(len(input_list)):\r\n        if x in input_list[i]:\r\n            ctr+= 1\r\n\r\n    return ctr\r\n\r\nlist1 = [[1, 3], [5, 7], [1, 11], [1, 15, 7]]\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(\"\\nCount 1 in the said list:\")\r\nprint(count_element_in_list(list1, 1))\r\nprint(\"\\nCount 7 in the said list:\")\r\nprint(count_element_in_list(list1, 7))\r\n\r\nlist1 = [[\'A\', \'B\'], [\'A\', \'C\'], [\'A\', \'D\', \'E\'], [\'B\', \'C\', \'D\']]\r\nprint(\"\\nOriginal list:\")\r\nprint(list1)\r\nprint(\"\\nCount \'A\' in the said list:\")\r\nprint(count_element_in_list(list1, \'A\'))\r\nprint(\"\\nCount \'E\' in the said list:\")\r\nprint(count_element_in_list(list1, \'E\'))', 0, 0, '2023-09-18 21:59:47', '2023-09-18 15:59:47', '2023-09-18 15:59:56', 1);
