(772, 'Write a Python program to find all index positions of the maximum and minimum values in a given list of numbers.', 'write-a-python-program-to-find-all-index-positions-of-the-maximum-and-minimum-values-in-a-given-list-of-numbers', '<p>def position_max_min(nums):</p><p>&nbsp; &nbsp; max_val = max(nums)</p><p>&nbsp; &nbsp; min_val = min(nums)</p><p>&nbsp; &nbsp; max_result = [i for i, j in enumerate(nums) if j == max_val]</p><p>&nbsp; &nbsp; min_result = [i for i, j in enumerate(nums) if j == min_val]</p><p>&nbsp; &nbsp; return max_result,min_result</p><p><br></p><p>nums = [12,33,23,10,67,89,45,667,23,12,11,10,54]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>result = position_max_min(nums)</p><p>print(\"\\nIndex positions of the maximum value of the said list:\")</p><p>print(result[0])</p><p>print(\"\\nIndex positions of the minimum value of the said list:\")</p><p>print(result[1])</p><div><br></div>', 'def position_max_min(nums):\r\n    max_val = max(nums)\r\n    min_val = min(nums)\r\n    max_result = [i for i, j in enumerate(nums) if j == max_val]\r\n    min_result = [i for i, j in enumerate(nums) if j == min_val]\r\n    return max_result,min_result\r\n\r\nnums = [12,33,23,10,67,89,45,667,23,12,11,10,54]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nresult = position_max_min(nums)\r\nprint(\"\\nIndex positions of the maximum value of the said list:\")\r\nprint(result[0])\r\nprint(\"\\nIndex positions of the minimum value of the said list:\")\r\nprint(result[1])', 2, 0, '2023-09-19 14:22:52', '2023-09-19 08:22:52', '2023-09-19 11:39:01', 1),
(773, 'Write a Python program to check if two lists have the same elements in them in same order or not.', 'write-a-python-program-to-check-if-two-lists-have-the-same-elements-in-them-in-same-order-or-not', '<p>def same_order(l1, l2):</p><p>&nbsp; &nbsp; common_elements = set(l1) &amp; set(l2)</p><p>&nbsp; &nbsp; l1 = [e for e in l1 if e in common_elements]</p><p>&nbsp; &nbsp; l2 = [e for e in l2 if e in common_elements]</p><p>&nbsp; &nbsp; return l1 == l2</p><p><br></p><p>color1 = [\"red\",\"green\",\"black\",\"orange\"]</p><p>color2 = [\"red\",\"pink\",\"green\",\"white\",\"black\"]</p><p>color3 = [\"white\",\"orange\",\"pink\",\"black\"]</p><p><br></p><p>print(\"Original lists:\")</p><p>print(color1)</p><p>print(color2)</p><p>print(color3)</p><p>print(\"\\nTest common elements between color1 and color2 are in same order?\")</p><p>print(same_order(color1, color2))</p><p>print(\"\\nTest common elements between color1 and color3 are in same order?\")</p><p>print(same_order(color1, color3))</p><p>print(\"\\nTest common elements between color2 and color3 are in same order?\")</p><p>print(same_order(color2, color3))</p><div><br></div>', 'def same_order(l1, l2):\r\n    common_elements = set(l1) & set(l2)\r\n    l1 = [e for e in l1 if e in common_elements]\r\n    l2 = [e for e in l2 if e in common_elements]\r\n    return l1 == l2\r\n\r\ncolor1 = [\"red\",\"green\",\"black\",\"orange\"]\r\ncolor2 = [\"red\",\"pink\",\"green\",\"white\",\"black\"]\r\ncolor3 = [\"white\",\"orange\",\"pink\",\"black\"]\r\n\r\nprint(\"Original lists:\")\r\nprint(color1)\r\nprint(color2)\r\nprint(color3)\r\nprint(\"\\nTest common elements between color1 and color2 are in same order?\")\r\nprint(same_order(color1, color2))\r\nprint(\"\\nTest common elements between color1 and color3 are in same order?\")\r\nprint(same_order(color1, color3))\r\nprint(\"\\nTest common elements between color2 and color3 are in same order?\")\r\nprint(same_order(color2, color3))', 0, 0, '2023-09-19 14:25:32', '2023-09-19 08:25:32', '2023-09-19 08:25:32', 1),
(774, 'Write a Python program to find the difference between two lists including duplicate elements.', 'write-a-python-program-to-find-the-difference-between-two-lists-including-duplicate-elements', '<p>def list_difference(l1,l2):</p><p>&nbsp; &nbsp; result = list(l1)</p><p>&nbsp; &nbsp; for el in l2:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; result.remove(el)</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>l1 = [1,1,2,3,3,4,4,5,6,7]</p><p>l2 = [1,1,2,4,5,6]</p><p><br></p><p>print(\"Original lists:\")</p><p>print(l1)</p><p>print(l2)</p><p>print(\"\\nDifference between two said list including duplicate elements):\")</p><p>print(list_difference(l1,l2))</p><div><br></div>', 'def list_difference(l1,l2):\r\n    result = list(l1)\r\n    for el in l2:\r\n        result.remove(el)\r\n    return result\r\n\r\nl1 = [1,1,2,3,3,4,4,5,6,7]\r\nl2 = [1,1,2,4,5,6]\r\n\r\nprint(\"Original lists:\")\r\nprint(l1)\r\nprint(l2)\r\nprint(\"\\nDifference between two said list including duplicate elements):\")\r\nprint(list_difference(l1,l2))', 2, 0, '2023-09-19 14:27:39', '2023-09-19 08:27:39', '2023-09-30 16:00:00', 1),
(775, 'Write a Python program to iterate over all pairs of consecutive items in a given list.', 'write-a-python-program-to-iterate-over-all-pairs-of-consecutive-items-in-a-given-list', '<p>def pairwise(l1):</p><p>&nbsp; &nbsp; temp = [ ]</p><p>&nbsp; &nbsp; for i in range(len(l1) - 1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; current_element, next_element = l1[i], l1[i + 1]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; x = (current_element, next_element)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; temp.append(x)</p><p>&nbsp; &nbsp; return temp</p><p><br></p><p>l1 = [1,1,2,3,3,4,4,5]</p><p>print(\"Original lists:\")</p><p>print(l1)</p><p>print(\"\\nIterate over all pairs of consecutive items of the said list:\")</p><p>print(pairwise(l1))</p><div><br></div>', 'def pairwise(l1):\r\n    temp = [ ]\r\n    for i in range(len(l1) - 1):\r\n        current_element, next_element = l1[i], l1[i + 1]\r\n        x = (current_element, next_element)\r\n        temp.append(x)\r\n    return temp\r\n\r\nl1 = [1,1,2,3,3,4,4,5]\r\nprint(\"Original lists:\")\r\nprint(l1)\r\nprint(\"\\nIterate over all pairs of consecutive items of the said list:\")\r\nprint(pairwise(l1))', 0, 0, '2023-09-19 14:29:20', '2023-09-19 08:29:20', '2023-09-19 08:29:20', 1),
(776, 'Write a Python program to remove duplicate words from a given list of strings.', 'write-a-python-program-to-remove-duplicate-words-from-a-given-list-of-strings', '<p>def unique_list(l):</p><p>&nbsp; &nbsp; temp = [ ]</p><p>&nbsp; &nbsp; for x in l:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if x not in temp:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; temp.append(x)</p><p>&nbsp; &nbsp; return temp</p><p><br></p><p>text_str = [\"Python\", \"Exercises\", \"Practice\", \"Solution\", \"Exercises\"]</p><p>print(\"Original String:\")</p><p>print(text_str)</p><p>print(\"\\nAfter removing duplicate words from the said list of strings:\")</p><p>print(unique_list(text_str))</p><div><br></div>', 'def unique_list(l):\r\n    temp = [ ]\r\n    for x in l:\r\n        if x not in temp:\r\n            temp.append(x)\r\n    return temp\r\n\r\ntext_str = [\"Python\", \"Exercises\", \"Practice\", \"Solution\", \"Exercises\"]\r\nprint(\"Original String:\")\r\nprint(text_str)\r\nprint(\"\\nAfter removing duplicate words from the said list of strings:\")\r\nprint(unique_list(text_str))', 0, 0, '2023-09-19 14:34:37', '2023-09-19 08:34:37', '2023-09-19 08:34:37', 1),
(777, 'Write a Python program to find the first even and odd number in a given list of numbers.', 'write-a-python-program-to-find-the-first-even-and-odd-number-in-a-given-list-of-numbers', '<p>def first_even_odd(nums):</p><p>&nbsp; &nbsp; first_even = next((el for el in nums if el%2==0),-1)</p><p>&nbsp; &nbsp; first_odd = next((el for el in nums if el%2!=0),-1)</p><p>&nbsp; &nbsp; return first_even,first_odd</p><p><br></p><p>nums= [1,3,5,7,4,1,6,8]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"\\nFirst even and odd number of the said list of numbers:\")</p><p>print(first_even_odd(nums))</p><div><br></div>', 'def first_even_odd(nums):\r\n    first_even = next((el for el in nums if el%2==0),-1)\r\n    first_odd = next((el for el in nums if el%2!=0),-1)\r\n    return first_even,first_odd\r\n\r\nnums= [1,3,5,7,4,1,6,8]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"\\nFirst even and odd number of the said list of numbers:\")\r\nprint(first_even_odd(nums))', 0, 0, '2023-09-19 14:36:32', '2023-09-19 08:36:32', '2023-09-19 08:36:32', 1),
(778, 'Write a Python program to sort a given mixed list of integers and strings. Numbers must be sorted before strings.', 'write-a-python-program-to-sort-a-given-mixed-list-of-integers-and-strings-numbers-must-be-sorted-before-strings', '<p>def sort_mixed_list(mixed_list):</p><p>&nbsp; &nbsp; int_part = sorted([i for i in mixed_list if type(i) is int])</p><p>&nbsp; &nbsp; str_part = sorted([i for i in mixed_list if type(i) is str])</p><p>&nbsp; &nbsp; return int_part + str_part</p><p><br></p><p>mixed_list = [19,\'red\',12,\'green\',\'blue\', 10,\'white\',\'green\',1]</p><p>print(\"Original list:\")</p><p>print(mixed_list)</p><p>print(\"\\nSort the said&nbsp; mixed list of integers and strings:\")</p><p>print(sort_mixed_list(mixed_list))</p><div><br></div>', 'def sort_mixed_list(mixed_list):\r\n    int_part = sorted([i for i in mixed_list if type(i) is int])\r\n    str_part = sorted([i for i in mixed_list if type(i) is str])\r\n    return int_part + str_part\r\n\r\nmixed_list = [19,\'red\',12,\'green\',\'blue\', 10,\'white\',\'green\',1]\r\nprint(\"Original list:\")\r\nprint(mixed_list)\r\nprint(\"\\nSort the said  mixed list of integers and strings:\")\r\nprint(sort_mixed_list(mixed_list))', 0, 0, '2023-09-19 14:38:41', '2023-09-19 08:38:41', '2023-09-19 08:38:41', 1),
