(783, 'Write a Python program to get the frequency of elements in a given list of lists.', 'write-a-python-program-to-get-the-frequency-of-elements-in-a-given-list-of-lists', '<p>def count_elements_lists(nums):</p><p>&nbsp; &nbsp; nums = [item for sublist in nums for item in sublist]</p><p>&nbsp; &nbsp; dic_data = { }</p><p>&nbsp; &nbsp; for num in nums:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if num in dic_data.keys():</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; dic_data[num] += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; key = num</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; value = 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; dic_data[key] = value</p><p>&nbsp; &nbsp; return dic_data</p><p><br></p><p>nums = [</p><p>&nbsp; &nbsp; &nbsp; &nbsp; [1,2,3,2],</p><p>&nbsp; &nbsp; &nbsp; &nbsp; [4,5,6,2],</p><p>&nbsp; &nbsp; &nbsp; &nbsp; [7,8,9,5],</p><p>&nbsp; &nbsp; &nbsp; &nbsp;]</p><p><br></p><p>print(\"Original list of lists:\")</p><p>print(nums)</p><p>print(\"\\nFrequency of the elements in the said list of lists:\")</p><p>print(count_elements_lists(nums))</p><div><br></div>', 'def count_elements_lists(nums):\r\n    nums = [item for sublist in nums for item in sublist]\r\n    dic_data = { }\r\n    for num in nums:\r\n        if num in dic_data.keys():\r\n            dic_data[num] += 1\r\n        else:\r\n            key = num\r\n            value = 1\r\n            dic_data[key] = value\r\n    return dic_data\r\n\r\nnums = [\r\n        [1,2,3,2],\r\n        [4,5,6,2],\r\n        [7,8,9,5],\r\n       ]\r\n\r\nprint(\"Original list of lists:\")\r\nprint(nums)\r\nprint(\"\\nFrequency of the elements in the said list of lists:\")\r\nprint(count_elements_lists(nums))', 2, 0, '2023-09-19 16:28:23', '2023-09-19 10:28:23', '2023-09-26 19:24:08', 1),
(784, 'Write a Python program to generate a number in a specified range except for some specific numbers.', 'write-a-python-program-to-generate-a-number-in-a-specified-range-except-for-some-specific-numbers', '<p>from random import choice</p><p><br></p><p>def generate_random(start_range, end_range, nums):</p><p>&nbsp; &nbsp; result = choice([i for i in range(start_range,end_range) if i not in nums])</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>start_range = 1</p><p>end_range = 10</p><p><br></p><p>nums = [2, 9, 10]</p><p>print(\"\\nGenerate a number in a specified range (1, 10) except [2, 9, 10]\")</p><p>print(generate_random(start_range,end_range,nums))</p><p><br></p><p>start_range = -5</p><p>end_range = 5</p><p>nums = [-5,0,4,3,2]</p><p><br></p><p>print(\"\\nGenerate a number in a specified range (-5, 5) except [-5,0,4,3,2]\")</p><p>print(generate_random(start_range,end_range,nums))</p><div><br></div>', 'from random import choice\r\n\r\ndef generate_random(start_range, end_range, nums):\r\n    result = choice([i for i in range(start_range,end_range) if i not in nums])\r\n    return result\r\n\r\nstart_range = 1\r\nend_range = 10\r\n\r\nnums = [2, 9, 10]\r\nprint(\"\\nGenerate a number in a specified range (1, 10) except [2, 9, 10]\")\r\nprint(generate_random(start_range,end_range,nums))\r\n\r\nstart_range = -5\r\nend_range = 5\r\nnums = [-5,0,4,3,2]\r\n\r\nprint(\"\\nGenerate a number in a specified range (-5, 5) except [-5,0,4,3,2]\")\r\nprint(generate_random(start_range,end_range,nums))', 0, 0, '2023-09-19 16:56:31', '2023-09-19 10:56:31', '2023-09-19 10:56:31', 1),
