(790, 'Write a Python program to find the maximum and minimum values in a given list within a specified index range.', 'write-a-python-program-to-find-the-maximum-and-minimum-values-in-a-given-list-within-a-specified-index-range', '<p>def reverse_list_of_lists(nums,lr,hr):</p><p>&nbsp; &nbsp; temp = [ ]</p><p>&nbsp; &nbsp; for idx, el in enumerate(nums):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if idx &gt;= lr and idx &lt; hr:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; temp.append(el)</p><p>&nbsp; &nbsp; result_max = max(temp)</p><p>&nbsp; &nbsp; result_min = min(temp)</p><p>&nbsp; &nbsp; return result_max, result_min</p><p><br></p><p>nums = [4,3,0,5,3,0,2,3,4,2,4,3,5]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"\\nIndex range:\")</p><p>lr = 3</p><p>hr = 8</p><p>print(lr,\"to\",hr)</p><p>print(\"\\nMaximum and minimum values of the said given list within index range:\")</p><p>print(reverse_list_of_lists(nums,lr,hr))</p><div><br></div>', 'def reverse_list_of_lists(nums,lr,hr):\r\n    temp = [ ]\r\n    for idx, el in enumerate(nums):\r\n        if idx >= lr and idx < hr:\r\n            temp.append(el)\r\n    result_max = max(temp)\r\n    result_min = min(temp)\r\n    return result_max, result_min\r\n\r\nnums = [4,3,0,5,3,0,2,3,4,2,4,3,5]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"\\nIndex range:\")\r\nlr = 3\r\nhr = 8\r\nprint(lr,\"to\",hr)\r\nprint(\"\\nMaximum and minimum values of the said given list within index range:\")\r\nprint(reverse_list_of_lists(nums,lr,hr))', 0, 0, '2023-09-19 17:41:34', '2023-09-19 11:41:34', '2023-09-19 11:41:34', 1),
(791, 'Write a Python program to combine two sorted lists using the heapq module.', 'write-a-python-program-to-combine-two-sorted-lists-using-the-heapq-module', '<p>from heapq import merge</p><p><br></p><p>nums1 = [1, 3, 5, 7, 9, 11]</p><p>nums2 = [0, 2, 4, 6, 8, 10]</p><p>print(\"Original sorted lists:\")</p><p>print(nums1)</p><p>print(nums2)</p><p>print(\"\\nAfter merging the said two sorted lists:\")</p><p>print(list(merge(nums1, nums2)))</p><div><br></div>', 'from heapq import merge\r\n\r\nnums1 = [1, 3, 5, 7, 9, 11]\r\nnums2 = [0, 2, 4, 6, 8, 10]\r\nprint(\"Original sorted lists:\")\r\nprint(nums1)\r\nprint(nums2)\r\nprint(\"\\nAfter merging the said two sorted lists:\")\r\nprint(list(merge(nums1, nums2)))', 0, 0, '2023-09-19 22:39:02', '2023-09-19 16:39:02', '2023-09-19 16:39:02', 1),
(792, 'Write a Python program to check if a given element occurs at least n times in a list.', 'write-a-python-program-to-check-if-a-given-element-occurs-at-least-n-times-in-a-list', '<p>def check_element_in_list(lst, x, n):</p><p>&nbsp; &nbsp; t = 0</p><p>&nbsp; &nbsp; try:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; for _ in range(n):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; t = lst.index(x, t) + 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return True</p><p>&nbsp; &nbsp; except ValueError:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return False</p><p><br></p><p>nums = [0,1,3,5,0,3,4,5,0,8,0,3,6,0,3,1,1,0]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p><br></p><p>x = 3</p><p>n = 4</p><p>print(\"\\nCheck if\",x,\"occurs at least\",n,\"times in a list:\")</p><p>print(check_element_in_list(nums,x,n))</p><p>x = 0</p><p>n = 5</p><p>print(\"\\nCheck if\",x,\"occurs at least\",n,\"times in a list:\")</p><p>print(check_element_in_list(nums,x,n))</p><p>x = 8</p><p>n = 3</p><p>print(\"\\nCheck if\",x,\"occurs at least\",n,\"times in a list:\")</p><p>print(check_element_in_list(nums,x,n))</p><div><br></div>', 'def check_element_in_list(lst, x, n):\r\n    t = 0\r\n    try:\r\n        for _ in range(n):\r\n            t = lst.index(x, t) + 1\r\n        return True\r\n    except ValueError:\r\n        return False\r\n\r\nnums = [0,1,3,5,0,3,4,5,0,8,0,3,6,0,3,1,1,0]\r\nprint(\"Original list:\")\r\nprint(nums)\r\n\r\nx = 3\r\nn = 4\r\nprint(\"\\nCheck if\",x,\"occurs at least\",n,\"times in a list:\")\r\nprint(check_element_in_list(nums,x,n))\r\nx = 0\r\nn = 5\r\nprint(\"\\nCheck if\",x,\"occurs at least\",n,\"times in a list:\")\r\nprint(check_element_in_list(nums,x,n))\r\nx = 8\r\nn = 3\r\nprint(\"\\nCheck if\",x,\"occurs at least\",n,\"times in a list:\")\r\nprint(check_element_in_list(nums,x,n))', 0, 0, '2023-09-19 22:42:31', '2023-09-19 16:42:31', '2023-09-19 16:42:31', 1),
(793, 'Write a Python program to join two given list of lists of the same length, element wise.', 'write-a-python-program-to-join-two-given-list-of-lists-of-the-same-length-element-wise', '<p>def elementswise_join(l1, l2):</p><p>&nbsp; &nbsp; result = [x + y for x, y in zip(l1, l2)]</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>nums1 = [[10,20], [30,40], [50,60], [30,20,80]]</p><p>nums2 = [[61], [12,14,15], [12,13,19,20], [12]]</p><p>print(\"Original lists:\")</p><p>print(nums1)</p><p>print(nums2)</p><p>print(\"\\nJoin the said two lists element wise:\")</p><p>print(elementswise_join(nums1, nums2))</p><p><br></p><p>list1 = [[\'a\',\'b\'], [\'b\',\'c\',\'d\'], [\'e\', \'f\']]</p><p>list2 = [[\'p\',\'q\'], [\'p\',\'s\',\'t\'], [\'u\',\'v\',\'w\']]</p><p>print(\"\\nOriginal lists:\")</p><p>print(list1)</p><p>print(list2)</p><p>print(\"\\nJoin the said two lists element wise:\")</p><p>print(elementswise_join(list1, list2))</p><div><br></div>', 'def elementswise_join(l1, l2):\r\n    result = [x + y for x, y in zip(l1, l2)]\r\n    return result\r\n\r\nnums1 = [[10,20], [30,40], [50,60], [30,20,80]]\r\nnums2 = [[61], [12,14,15], [12,13,19,20], [12]]\r\nprint(\"Original lists:\")\r\nprint(nums1)\r\nprint(nums2)\r\nprint(\"\\nJoin the said two lists element wise:\")\r\nprint(elementswise_join(nums1, nums2))\r\n\r\nlist1 = [[\'a\',\'b\'], [\'b\',\'c\',\'d\'], [\'e\', \'f\']]\r\nlist2 = [[\'p\',\'q\'], [\'p\',\'s\',\'t\'], [\'u\',\'v\',\'w\']]\r\nprint(\"\\nOriginal lists:\")\r\nprint(list1)\r\nprint(list2)\r\nprint(\"\\nJoin the said two lists element wise:\")\r\nprint(elementswise_join(list1, list2))', 0, 0, '2023-09-19 22:46:32', '2023-09-19 16:46:32', '2023-09-19 16:46:32', 1),
(794, 'Write a Python program to add two given lists of different lengths, starting on the left.', 'write-a-python-program-to-add-two-given-lists-of-different-lengths-starting-on-the-left', '<p>def elementswise_left_join(l1, l2):</p><p>&nbsp; &nbsp; f_len = len(l1)-(len(l2) - 1)</p><p>&nbsp; &nbsp; for i in range(0, len(l2), 1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if f_len - i &gt;= len(l1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; l1[i] = l1[i] + l2[i]</p><p>&nbsp; &nbsp; return l1</p><p><br></p><p>nums1 = [2, 4, 7, 0, 5, 8]</p><p>nums2 = [3, 3, -1, 7]</p><p>print(\"\\nOriginal lists:\")</p><p>print(nums1)</p><p>print(nums2)</p><p>print(\"\\nAdd said two lists from left:\")</p><p>print(elementswise_left_join(nums1,nums2))</p><p><br></p><p>nums3 = [1, 2, 3, 4, 5, 6]</p><p>nums4 = [2, 4, -3]</p><p>print(\"\\nOriginal lists:\")</p><p>print(nums3)</p><p>print(nums4)</p><p>print(\"\\nAdd said two lists from left:\")</p><p>print(elementswise_left_join(nums3,nums4))</p><div><br></div>', 'def elementswise_left_join(l1, l2):\r\n    f_len = len(l1)-(len(l2) - 1)\r\n    for i in range(0, len(l2), 1):\r\n        if f_len - i >= len(l1):\r\n            break\r\n        else:\r\n            l1[i] = l1[i] + l2[i]\r\n    return l1\r\n\r\nnums1 = [2, 4, 7, 0, 5, 8]\r\nnums2 = [3, 3, -1, 7]\r\nprint(\"\\nOriginal lists:\")\r\nprint(nums1)\r\nprint(nums2)\r\nprint(\"\\nAdd said two lists from left:\")\r\nprint(elementswise_left_join(nums1,nums2))\r\n\r\nnums3 = [1, 2, 3, 4, 5, 6]\r\nnums4 = [2, 4, -3]\r\nprint(\"\\nOriginal lists:\")\r\nprint(nums3)\r\nprint(nums4)\r\nprint(\"\\nAdd said two lists from left:\")\r\nprint(elementswise_left_join(nums3,nums4))', 2, 0, '2023-09-19 23:01:28', '2023-09-19 17:01:28', '2023-10-02 18:48:38', 1),
(795, 'Write a Python program to interleave lists of varying lengths.', 'write-a-python-program-to-interleave-lists-of-varying-lengths', '<p>def interleave_diff_len_lists(list1, list2, list3, list4):</p><p>&nbsp; &nbsp; result = [ ]</p><p>&nbsp; &nbsp; l1 = len(list1)</p><p>&nbsp; &nbsp; l2 = len(list2)</p><p>&nbsp; &nbsp; l3 = len(list3)</p><p>&nbsp; &nbsp; l4 = len(list4)</p><p><br></p><p>&nbsp; &nbsp; for i in range(max(l1, l2, l3, l4)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if i &lt; l1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result.append(list1[i])</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if i &lt; l2:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result.append(list2[i])</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if i &lt; l3:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result.append(list3[i])</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if i &lt; l4:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result.append(list4[i])</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>nums1 = [2, 4, 7, 0, 5, 8]</p><p>nums2 = [2, 5, 8]</p><p>nums3 = [0, 1]</p><p>nums4 = [3, 3, -1, 7]</p><p><br></p><p>print(\"\\nOriginal lists:\")</p><p>print(nums1)</p><p>print(nums2)</p><p>print(nums3)</p><p>print(nums4)</p><p><br></p><p>print(\"\\nInterleave said lists of different lengths:\")</p><p>print(interleave_diff_len_lists(nums1, nums2, nums3, nums4))</p><div><br></div>', 'def interleave_diff_len_lists(list1, list2, list3, list4):\r\n    result = [ ]\r\n    l1 = len(list1)\r\n    l2 = len(list2)\r\n    l3 = len(list3)\r\n    l4 = len(list4)\r\n\r\n    for i in range(max(l1, l2, l3, l4)):\r\n        if i < l1:\r\n            result.append(list1[i])\r\n        if i < l2:\r\n            result.append(list2[i])\r\n        if i < l3:\r\n            result.append(list3[i])\r\n        if i < l4:\r\n            result.append(list4[i])\r\n    return result\r\n\r\nnums1 = [2, 4, 7, 0, 5, 8]\r\nnums2 = [2, 5, 8]\r\nnums3 = [0, 1]\r\nnums4 = [3, 3, -1, 7]\r\n\r\nprint(\"\\nOriginal lists:\")\r\nprint(nums1)\r\nprint(nums2)\r\nprint(nums3)\r\nprint(nums4)\r\n\r\nprint(\"\\nInterleave said lists of different lengths:\")\r\nprint(interleave_diff_len_lists(nums1, nums2, nums3, nums4))', 0, 0, '2023-09-19 23:16:20', '2023-09-19 17:16:20', '2023-09-19 17:16:20', 1),
(796, 'Write a Python program to find the maximum and minimum values in a given list of tuples.', 'write-a-python-program-to-find-the-maximum-and-minimum-values-in-a-given-list-of-tuples', '<p>from operator import itemgetter</p><p><br></p><p>def max_min_list_tuples(class_students):</p><p>&nbsp; &nbsp; return_max = max(class_students,key=itemgetter(1))[1]</p><p>&nbsp; &nbsp; return_min = min(class_students,key=itemgetter(1))[1]</p><p>&nbsp; &nbsp; return return_max, return_min</p><p><br></p><p>class_students = [(\'V\', 60), (\'VI\', 70), (\'VII\', 75), (\'VIII\', 72), (\'IX\', 78), (\'X\', 70)]</p><p>print(\"Original list with tuples:\")</p><p>print(class_students)</p><p>print(\"\\nMaximum and minimum values of the said list of tuples:\")</p><p>print(max_min_list_tuples(class_students))</p><div><br></div>', 'from operator import itemgetter\r\n\r\ndef max_min_list_tuples(class_students):\r\n    return_max = max(class_students,key=itemgetter(1))[1]\r\n    return_min = min(class_students,key=itemgetter(1))[1]\r\n    return return_max, return_min\r\n\r\nclass_students = [(\'V\', 60), (\'VI\', 70), (\'VII\', 75), (\'VIII\', 72), (\'IX\', 78), (\'X\', 70)]\r\nprint(\"Original list with tuples:\")\r\nprint(class_students)\r\nprint(\"\\nMaximum and minimum values of the said list of tuples:\")\r\nprint(max_min_list_tuples(class_students))', 2, 0, '2023-09-19 23:19:03', '2023-09-19 17:19:03', '2023-09-19 21:39:50', 1),
(797, 'Write a Python program to append the same value/a list multiple times to a list/list-of-lists.', 'write-a-python-program-to-append-the-same-valuea-list-multiple-times-to-a-listlist-of-lists', '<p>print(\"Add a value(7), 5 times, to a list:\")</p><p>nums = [ ]</p><p>nums += 5 * [\'7\']</p><p>print(nums)</p><p><br></p><p>nums1 = [1,2,3,4]</p><p>print(\"\\nAdd 5, 6 times, to a list:\")</p><p>nums1 += 6 * [5]</p><p>print(nums1)</p><p><br></p><p>print(\"\\nAdd a list, 4 times, to a list of lists:\")</p><p>nums1 = [ ]</p><p>nums1 += 4 * [[1,2,5]]</p><p>print(nums1)</p><p><br></p><p>print(\"\\nAdd a list, 4 times, to a list of lists:\")</p><p>nums1 = [[5,6,7]]</p><p>nums1 += 4 * [[1,2,5]]</p><p>print(nums1)</p><div><br></div>', 'print(\"Add a value(7), 5 times, to a list:\")\r\nnums = [ ]\r\nnums += 5 * [\'7\']\r\nprint(nums)\r\n\r\nnums1 = [1,2,3,4]\r\nprint(\"\\nAdd 5, 6 times, to a list:\")\r\nnums1 += 6 * [5]\r\nprint(nums1)\r\n\r\nprint(\"\\nAdd a list, 4 times, to a list of lists:\")\r\nnums1 = [ ]\r\nnums1 += 4 * [[1,2,5]]\r\nprint(nums1)\r\n\r\nprint(\"\\nAdd a list, 4 times, to a list of lists:\")\r\nnums1 = [[5,6,7]]\r\nnums1 += 4 * [[1,2,5]]\r\nprint(nums1)', 0, 0, '2023-09-19 23:23:01', '2023-09-19 17:23:01', '2023-09-19 17:23:01', 1),
(798, 'Write a Python program to remove the first specified number of elements from a given list satisfying a condition.', 'write-a-python-program-to-remove-the-first-specified-number-of-elements-from-a-given-list-satisfying-a-condition', '<p>def condition_match(x):</p><p>&nbsp; &nbsp; return ((x % 2) == 0)</p><p><br></p><p>def remove_items_con(data, N):</p><p>&nbsp; &nbsp; ctr = 1</p><p>&nbsp; &nbsp; result = [ ]</p><p>&nbsp; &nbsp; for x in data:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if ctr &gt; N or not condition_match(x):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; result.append(x)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr = ctr + 1</p><p>&nbsp; &nbsp; return result</p><p><br></p><p>nums = [3,10,4,7,5,7,8,3,3,4,5,9,3,4,9,8,5]</p><p>N = 4</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"\\nRemove first 4 even numbers from the said list:\")</p><p>print(remove_items_con(nums, N))</p><div><br></div>', 'def condition_match(x):\r\n    return ((x % 2) == 0)\r\n\r\ndef remove_items_con(data, N):\r\n    ctr = 1\r\n    result = [ ]\r\n    for x in data:\r\n        if ctr > N or not condition_match(x):\r\n            result.append(x)\r\n        else:\r\n            ctr = ctr + 1\r\n    return result\r\n\r\nnums = [3,10,4,7,5,7,8,3,3,4,5,9,3,4,9,8,5]\r\nN = 4\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"\\nRemove first 4 even numbers from the said list:\")\r\nprint(remove_items_con(nums, N))', 0, 0, '2023-09-19 23:25:58', '2023-09-19 17:25:58', '2023-09-19 17:25:58', 1),
(799, 'Write a Python program to check if a given list increases strictly. Moreover, if removing only one element from the list results in a strictly increasing list, we still consider the list true.', 'write-a-python-program-to-check-if-a-given-list-increases-strictly-moreover-if-removing-only-one-element-from-the-list-results-in-a-strictly-increasing-list-we-still-consider-the-list-true', '<p>def almost_increasing_sequence(sequence):</p><p>&nbsp; &nbsp; if len(sequence) &lt; 3:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return True</p><p><br></p><p>&nbsp; &nbsp; a, b, *sequence = sequence</p><p>&nbsp; &nbsp; skipped = 0</p><p>&nbsp; &nbsp; for c in sequence:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if a &lt; b &lt; c:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a, b = b, c</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; continue</p><p>&nbsp; &nbsp; &nbsp; &nbsp; elif b &lt; c:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a, b = b, c</p><p>&nbsp; &nbsp; &nbsp; &nbsp; elif a &lt; c:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a, b = a, c</p><p>&nbsp; &nbsp; &nbsp; &nbsp; skipped += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if skipped == 2:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return False</p><p>&nbsp; &nbsp; return a &lt; b</p><p><br></p><p>print(almost_increasing_sequence([ ]))</p><p>print(almost_increasing_sequence([1]))</p><p>print(almost_increasing_sequence([1, 2]))</p><p>print(almost_increasing_sequence([1, 2, 3]))</p><p>print(almost_increasing_sequence([3, 1, 2]))</p><p>print(almost_increasing_sequence([1, 2, 3, 0, 4, 5, 6]))</p><p>print(almost_increasing_sequence([1, 2, 3, 0]))</p><p>print(almost_increasing_sequence([1, 2, 0, 3]))</p><p>print(almost_increasing_sequence([10, 1, 2, 3, 4, 5]))</p><p>print(almost_increasing_sequence([1, 2, 10, 3, 4]))</p><p>print(almost_increasing_sequence([1, 2, 3, 12, 4, 5]))</p><p><br></p><p>print(almost_increasing_sequence([3, 2, 1]))</p><p>print(almost_increasing_sequence([1, 2, 0, -1]))</p><p>print(almost_increasing_sequence([5, 6, 1, 2]))</p><p>print(almost_increasing_sequence([1, 2, 3, 0, -1]))</p><p>print(almost_increasing_sequence([10, 11, 12, 2, 3, 4, 5]))</p><div><br></div>', 'def almost_increasing_sequence(sequence):\r\n    if len(sequence) < 3:\r\n        return True\r\n\r\n    a, b, *sequence = sequence\r\n    skipped = 0\r\n    for c in sequence:\r\n        if a < b < c:\r\n            a, b = b, c\r\n            continue\r\n        elif b < c:\r\n            a, b = b, c\r\n        elif a < c:\r\n            a, b = a, c\r\n        skipped += 1\r\n        if skipped == 2:\r\n            return False\r\n    return a < b\r\n\r\nprint(almost_increasing_sequence([ ]))\r\nprint(almost_increasing_sequence([1]))\r\nprint(almost_increasing_sequence([1, 2]))\r\nprint(almost_increasing_sequence([1, 2, 3]))\r\nprint(almost_increasing_sequence([3, 1, 2]))\r\nprint(almost_increasing_sequence([1, 2, 3, 0, 4, 5, 6]))\r\nprint(almost_increasing_sequence([1, 2, 3, 0]))\r\nprint(almost_increasing_sequence([1, 2, 0, 3]))\r\nprint(almost_increasing_sequence([10, 1, 2, 3, 4, 5]))\r\nprint(almost_increasing_sequence([1, 2, 10, 3, 4]))\r\nprint(almost_increasing_sequence([1, 2, 3, 12, 4, 5]))\r\n\r\nprint(almost_increasing_sequence([3, 2, 1]))\r\nprint(almost_increasing_sequence([1, 2, 0, -1]))\r\nprint(almost_increasing_sequence([5, 6, 1, 2]))\r\nprint(almost_increasing_sequence([1, 2, 3, 0, -1]))\r\nprint(almost_increasing_sequence([10, 11, 12, 2, 3, 4, 5]))', 0, 0, '2023-09-19 23:32:39', '2023-09-19 17:32:39', '2023-09-19 17:32:39', 1),
(800, 'Write a Python program to find the last occurrence of a specified item in a given list.', 'write-a-python-program-to-find-the-last-occurrence-of-a-specified-item-in-a-given-list', '<p>def last_occurrence(l1, ch):</p><p>&nbsp; &nbsp; return \'\'.join(l1).rindex(ch)</p><p><br></p><p>chars = [\'s\',\'d\',\'f\',\'s\',\'d\',\'f\',\'s\',\'f\',\'k\',\'o\',\'p\',\'i\',\'w\',\'e\',\'k\',\'c\']</p><p>print(\"Original list:\")</p><p>print(chars)</p><p>ch = \'f\'</p><p>print(\"Last occurrence of\",ch,\"in the said list:\")</p><p>print(last_occurrence(chars, ch))</p><p>ch = \'c\'</p><p>print(\"Last occurrence of\",ch,\"in the said list:\")</p><p>print(last_occurrence(chars, ch))</p><p>ch = \'k\'</p><p>print(\"Last occurrence of\",ch,\"in the said list:\")</p><p>print(last_occurrence(chars, ch))</p><p>ch = \'w\'</p><p>print(\"Last occurrence of\",ch,\"in the said list:\")</p><p>print(last_occurrence(chars, ch))</p><div><br></div>', 'def last_occurrence(l1, ch):\r\n    return \'\'.join(l1).rindex(ch)\r\n\r\nchars = [\'s\',\'d\',\'f\',\'s\',\'d\',\'f\',\'s\',\'f\',\'k\',\'o\',\'p\',\'i\',\'w\',\'e\',\'k\',\'c\']\r\nprint(\"Original list:\")\r\nprint(chars)\r\nch = \'f\'\r\nprint(\"Last occurrence of\",ch,\"in the said list:\")\r\nprint(last_occurrence(chars, ch))\r\nch = \'c\'\r\nprint(\"Last occurrence of\",ch,\"in the said list:\")\r\nprint(last_occurrence(chars, ch))\r\nch = \'k\'\r\nprint(\"Last occurrence of\",ch,\"in the said list:\")\r\nprint(last_occurrence(chars, ch))\r\nch = \'w\'\r\nprint(\"Last occurrence of\",ch,\"in the said list:\")\r\nprint(last_occurrence(chars, ch))', 0, 0, '2023-09-19 23:44:53', '2023-09-19 17:44:53', '2023-09-19 17:44:53', 1),
(801, 'Write a Python program to get the index of the first element that is greater than a specified element.', 'write-a-python-program-to-get-the-index-of-the-first-element-that-is-greater-than-a-specified-element', '<p>def first_index(l1, n):</p><p>&nbsp; &nbsp; return next(a[0] for a in enumerate(l1) if a[1] &gt; n)</p><p><br></p><p>nums = [12,45,23,67,78,90,100,76,38,62,73,29,83]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>n = 73</p><p>print(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")</p><p>print(first_index(nums,n))</p><p>n = 21</p><p>print(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")</p><p>print(first_index(nums,n))</p><p>n = 80</p><p>print(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")</p><p>print(first_index(nums,n))</p><p>n = 55</p><p>print(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")</p><p>print(first_index(nums,n))</p><div><br></div>', 'def first_index(l1, n):\r\n    return next(a[0] for a in enumerate(l1) if a[1] > n)\r\n\r\nnums = [12,45,23,67,78,90,100,76,38,62,73,29,83]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nn = 73\r\nprint(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")\r\nprint(first_index(nums,n))\r\nn = 21\r\nprint(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")\r\nprint(first_index(nums,n))\r\nn = 80\r\nprint(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")\r\nprint(first_index(nums,n))\r\nn = 55\r\nprint(\"\\nIndex of the first element which is greater than\",n,\"in the said list:\")\r\nprint(first_index(nums,n))', 0, 0, '2023-09-20 01:23:36', '2023-09-19 19:23:36', '2023-09-19 19:23:36', 1),
(802, 'Write a Python program to count the number of items in a given list that are both even and greater than 73.', 'write-a-python-program-to-count-the-number-of-items-in-a-given-list-that-are-both-even-and-greater-than-73', '<p>def first_index(l1):</p><p>&nbsp; &nbsp; count = 0</p><p>&nbsp; &nbsp; for num in l1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if num % 2 == 0 and num &gt; 73:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; count += 1</p><p>&nbsp; &nbsp; return count</p><p><br></p><p>nums = [12,45,23,67,78,90,45,32,100,76,38,62,73,29,83]</p><p><br></p><p>print(\"Original list:\")</p><p>print(nums)</p><p>n = 45</p><p>print(\"\\nNumber of Items of the said list which are even and greater than\",n)</p><p>print(first_index(nums))</p><div><br></div>', 'def first_index(l1):\r\n    count = 0\r\n    for num in l1:\r\n        if num % 2 == 0 and num > 73:\r\n            count += 1\r\n    return count\r\n\r\nnums = [12,45,23,67,78,90,45,32,100,76,38,62,73,29,83]\r\n\r\nprint(\"Original list:\")\r\nprint(nums)\r\nn = 45\r\nprint(\"\\nNumber of Items of the said list which are even and greater than\",n)\r\nprint(first_index(nums))', 0, 0, '2023-09-20 01:38:15', '2023-09-19 19:38:15', '2023-09-19 19:38:15', 1),
(803, 'Write a Python program to split a given list into specified-sized chunks.', 'write-a-python-program-to-split-a-given-list-into-specified-sized-chunks', '<p>def split_list(lst, chunk_size):</p><p>&nbsp; &nbsp; result = [ ]</p><p><br></p><p>&nbsp; &nbsp; for i in range(0, len(lst), chunk_size):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; result.append(lst[i:i + chunk_size])</p><p><br></p><p>&nbsp; &nbsp; return result</p><p><br></p><p>my_list = [12,45,23,67,78,90,45,32,100,76,38,62,73,29,83]</p><p>print(\"Original list:\")</p><p>print(my_list )</p><p>chunk_size = 3</p><p>print(\"\\nSplit the said list into equal size\",chunk_size)</p><p>print(split_list(my_list ,chunk_size))</p><p>chunk_size = 4</p><p>print(\"\\nSplit the said list into equal size\",chunk_size)</p><p>print(split_list(my_list ,chunk_size))</p><p>chunk_size = 5</p><p>print(\"\\nSplit the said list into equal size\",chunk_size)</p><p>print(split_list(my_list ,chunk_size))</p>', 'def split_list(lst, chunk_size):\r\n    result = [ ]\r\n\r\n    for i in range(0, len(lst), chunk_size):\r\n        result.append(lst[i:i + chunk_size])\r\n\r\n    return result\r\n\r\nmy_list = [12,45,23,67,78,90,45,32,100,76,38,62,73,29,83]\r\nprint(\"Original list:\")\r\nprint(my_list )\r\nchunk_size = 3\r\nprint(\"\\nSplit the said list into equal size\",chunk_size)\r\nprint(split_list(my_list ,chunk_size))\r\nchunk_size = 4\r\nprint(\"\\nSplit the said list into equal size\",chunk_size)\r\nprint(split_list(my_list ,chunk_size))\r\nchunk_size = 5\r\nprint(\"\\nSplit the said list into equal size\",chunk_size)\r\nprint(split_list(my_list ,chunk_size))', 0, 0, '2023-09-20 01:59:10', '2023-09-19 19:59:10', '2023-09-19 19:59:10', 1);
