(804, 'Write a Python program to remove the None value from a given list.', 'write-a-python-program-to-remove-the-none-value-from-a-given-list', '<p>def remove_none(nums):</p><p>&nbsp; &nbsp; for x in list(nums):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if x is None:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nums.remove(x)</p><p>&nbsp; &nbsp; return nums</p><p><br></p><p>nums = [12, 0, None, 23, None, -55, 234, 89, None, 0, 6, -12]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"\\nRemove None value from the said list:\")</p><p>print(remove_none(nums))</p><div><br></div>', 'def remove_none(nums):\r\n    for x in list(nums):\r\n        if x is None:\r\n            nums.remove(x)\r\n    return nums\r\n\r\nnums = [12, 0, None, 23, None, -55, 234, 89, None, 0, 6, -12]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"\\nRemove None value from the said list:\")\r\nprint(remove_none(nums))', 0, 0, '2023-09-20 02:11:27', '2023-09-19 20:11:27', '2023-09-19 20:11:27', 1),
(805, 'Write a Python program to convert a given list of strings into a list of lists.', 'write-a-python-program-to-convert-a-given-list-of-strings-into-a-list-of-lists', '<p>def strings_to_listOflists(list1):</p><p>&nbsp; &nbsp; result = [ ]</p><p>&nbsp; &nbsp; for word in list1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; x = list(word)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; result.append(x)</p><p><br></p><p>&nbsp; &nbsp; return result</p><p><br></p><p>colors = [\"Red\", \"Maroon\", \"Yellow\", \"Olive\"]</p><p>print(\'Original list of strings:\')</p><p>print(colors)</p><p>print(\"\\nConvert the said list of strings into list of lists:\")</p><p>print(strings_to_listOflists(colors))</p><div><br></div>', 'def strings_to_listOflists(list1):\r\n    result = [ ]\r\n    for word in list1:\r\n        x = list(word)\r\n        result.append(x)\r\n\r\n    return result\r\n\r\ncolors = [\"Red\", \"Maroon\", \"Yellow\", \"Olive\"]\r\nprint(\'Original list of strings:\')\r\nprint(colors)\r\nprint(\"\\nConvert the said list of strings into list of lists:\")\r\nprint(strings_to_listOflists(colors))', 2, 0, '2023-09-20 02:24:53', '2023-09-19 20:24:53', '2023-09-19 20:35:11', 1),
(806, 'Write a Python program to display vertically each element of a given list, list of lists.', 'write-a-python-program-to-display-vertically-each-element-of-a-given-list-list-of-lists', '<p>text = [\"a\", \"b\", \"c\", \"d\",\"e\", \"f\"]</p><p><br></p><p>print(\"Original list:\")</p><p>print(text)</p><p>print(\"\\nDisplay each element vertically of the said list:\")</p><p>for i in text:</p><p>&nbsp; &nbsp; &nbsp;print(i)</p><p><br></p><p>nums = [[1, 2, 5], [4, 5, 8], [7, 3, 6]]</p><p><br></p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"\\nDisplay each element vertically of the said list of lists:\")</p><p>for a,b,c in zip(*nums):</p><p>&nbsp; &nbsp; &nbsp;print(a, b, c)</p><div><br></div>', 'text = [\"a\", \"b\", \"c\", \"d\",\"e\", \"f\"]\r\n\r\nprint(\"Original list:\")\r\nprint(text)\r\nprint(\"\\nDisplay each element vertically of the said list:\")\r\nfor i in text:\r\n     print(i)\r\n\r\nnums = [[1, 2, 5], [4, 5, 8], [7, 3, 6]]\r\n\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"\\nDisplay each element vertically of the said list of lists:\")\r\nfor a,b,c in zip(*nums):\r\n     print(a, b, c)', 0, 0, '2023-09-20 02:29:29', '2023-09-19 20:29:29', '2023-09-19 20:29:29', 0),
