(865, 'Write a Python program to sort Counter by value.', 'write-a-python-program-to-sort-counter-by-value', '<div><div>from collections import Counter, OrderedDict</div><div><br></div><div>def sort_counter_by_value(counter):</div><div>&nbsp; &nbsp; # Convert the Counter to a list of key-value pairs</div><div>&nbsp; &nbsp; items = list(counter.items())</div><div><br></div><div>&nbsp; &nbsp; # Sort the list by values in descending order</div><div>&nbsp; &nbsp; sorted_items = sorted(items, key=lambda x: x[1], reverse=True)</div><div><br></div><div>&nbsp; &nbsp; # Create a new ordered dictionary from the sorted list</div><div>&nbsp; &nbsp; sorted_counter = OrderedDict(sorted_items)</div><div><br></div><div>&nbsp; &nbsp; return sorted_counter&nbsp;</div><div><br></div><div>my_counter = Counter({\'a\': 3, \'b\': 1, \'c\': 2, \'d\': 5})</div><div><br></div><div>sorted_counter = sort_counter_by_value(my_counter)</div><div>print(\"Sorted Counter:\")</div><div>print(sorted_counter)</div></div><div><br></div>', 'from collections import Counter, OrderedDict\r\n\r\ndef sort_counter_by_value(counter):\r\n    # Convert the Counter to a list of key-value pairs\r\n    items = list(counter.items())\r\n\r\n    # Sort the list by values in descending order\r\n    sorted_items = sorted(items, key=lambda x: x[1], reverse=True)\r\n\r\n    # Create a new ordered dictionary from the sorted list\r\n    sorted_counter = OrderedDict(sorted_items)\r\n\r\n    return sorted_counter \r\n\r\nmy_counter = Counter({\'a\': 3, \'b\': 1, \'c\': 2, \'d\': 5})\r\n\r\nsorted_counter = sort_counter_by_value(my_counter)\r\nprint(\"Sorted Counter:\")\r\nprint(sorted_counter)', 0, 0, '2023-09-23 02:52:33', '2023-09-22 20:52:33', '2023-09-22 20:52:33', 1),
(866, 'Write a Python program to create a dictionary from two lists without losing duplicate values.', 'write-a-python-program-to-create-a-dictionary-from-two-lists-without-losing-duplicate-values', '<div>def create_dict_from_lists(keys, values):</div><div>&nbsp; &nbsp; # Check if the length of keys and values lists are the same</div><div>&nbsp; &nbsp; if len(keys) != len(values):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; raise ValueError(\"The lists must have the same length.\")</div><div><br></div><div>&nbsp; &nbsp; # Use dictionary comprehension to create the dictionary</div><div>&nbsp; &nbsp; result_dict = {key: [ ] for key in keys}</div><div><br></div><div>&nbsp; &nbsp; for key, value in zip(keys, values):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; result_dict[key].append(value)</div><div><br></div><div>&nbsp; &nbsp; return result_dict</div><div><br></div><div>keys_list = [\'A\', \'B\', \'A\', \'C\', \'B\']</div><div>values_list = [1, 2, 3, 4, 5]</div><div><br></div><div>result_dict = create_dict_from_lists(keys_list, values_list)</div><div><br></div><div>print(\"Dictionary from Lists:\")</div><div>print(result_dict)</div><div><br></div>', 'def create_dict_from_lists(keys, values):\r\n    # Check if the length of keys and values lists are the same\r\n    if len(keys) != len(values):\r\n        raise ValueError(\"The lists must have the same length.\")\r\n\r\n    # Use dictionary comprehension to create the dictionary\r\n    result_dict = {key: [ ] for key in keys}\r\n\r\n    for key, value in zip(keys, values):\r\n        result_dict[key].append(value)\r\n\r\n    return result_dict\r\n\r\nkeys_list = [\'A\', \'B\', \'A\', \'C\', \'B\']\r\nvalues_list = [1, 2, 3, 4, 5]\r\n\r\nresult_dict = create_dict_from_lists(keys_list, values_list)\r\n\r\nprint(\"Dictionary from Lists:\")\r\nprint(result_dict)', 0, 0, '2023-09-23 03:04:21', '2023-09-22 21:04:21', '2023-09-22 21:04:21', 1),
