(876, 'Write a Python program to split a given dictionary of lists into lists of dictionaries.', 'write-a-python-program-to-split-a-given-dictionary-of-lists-into-lists-of-dictionaries', '<div>data = {</div><div>&nbsp; &nbsp; \"name\": [\"Alice\", \"Bob\", \"Charlie\"],</div><div>&nbsp; &nbsp; \"age\": [25, 30, 35],</div><div>&nbsp; &nbsp; \"city\": [\"New York\", \"Los Angeles\", \"Chicago\"],</div><div>}</div><div><br></div><div># Split the dictionary of lists into lists of dictionaries</div><div>list_of_dicts = [ ]</div><div><br></div><div>list_length = len(data[\"name\"])</div><div><br></div><div>for i in range(list_length):</div><div>&nbsp; &nbsp; new_dict = { }</div><div>&nbsp; &nbsp; for key, values in data.items( ):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; new_dict[key] = values[i]</div><div>&nbsp; &nbsp; list_of_dicts.append(new_dict)</div><div><br></div><div>print(list_of_dicts)</div><div><br></div>', 'data = {\r\n    \"name\": [\"Alice\", \"Bob\", \"Charlie\"],\r\n    \"age\": [25, 30, 35],\r\n    \"city\": [\"New York\", \"Los Angeles\", \"Chicago\"],\r\n}\r\n\r\n# Split the dictionary of lists into lists of dictionaries\r\nlist_of_dicts = [ ]\r\n\r\nlist_length = len(data[\"name\"])\r\n\r\nfor i in range(list_length):\r\n    new_dict = { }\r\n    for key, values in data.items( ):\r\n        new_dict[key] = values[i]\r\n    list_of_dicts.append(new_dict)\r\n\r\nprint(list_of_dicts)', 0, 0, '2023-09-25 08:57:04', '2023-09-25 02:57:04', '2023-09-25 02:57:04', 1),
(877, 'Write a Python program to remove a specified dictionary from a given list.', 'write-a-python-program-to-remove-a-specified-dictionary-from-a-given-list', '<div>list_of_dicts = [</div><div>&nbsp; &nbsp; {\"name\": \"Alice\", \"age\": 25},</div><div>&nbsp; &nbsp; {\"name\": \"Bob\", \"age\": 30},</div><div>&nbsp; &nbsp; {\"name\": \"Charlie\", \"age\": 35},</div><div>&nbsp; &nbsp; {\"name\": \"David\", \"age\": 40},</div><div>]</div><div><br></div><div>dict_to_remove = {\"name\": \"Bob\", \"age\": 30}</div><div><br></div><div>if dict_to_remove in list_of_dicts:</div><div>&nbsp; &nbsp; list_of_dicts.remove(dict_to_remove)</div><div>else:</div><div>&nbsp; &nbsp; print(\"Dictionary not found in the list.\")</div><div><br></div><div>print(\"Remove the specified item from the said list of dictionary:\")</div><div>for item in list_of_dicts:</div><div>&nbsp; &nbsp; print(item)</div><div><br></div>', 'list_of_dicts = [\r\n    {\"name\": \"Alice\", \"age\": 25},\r\n    {\"name\": \"Bob\", \"age\": 30},\r\n    {\"name\": \"Charlie\", \"age\": 35},\r\n    {\"name\": \"David\", \"age\": 40},\r\n]\r\n\r\ndict_to_remove = {\"name\": \"Bob\", \"age\": 30}\r\n\r\nif dict_to_remove in list_of_dicts:\r\n    list_of_dicts.remove(dict_to_remove)\r\nelse:\r\n    print(\"Dictionary not found in the list.\")\r\n\r\nprint(\"Remove the specified item from the said list of dictionary:\")\r\nfor item in list_of_dicts:\r\n    print(item)', 0, 0, '2023-09-25 09:12:26', '2023-09-25 03:12:26', '2023-09-25 03:12:26', 1),
(878, 'Write a Python program to convert string values of a given dictionary into integer/float datatypes.', 'write-a-python-program-to-convert-string-values-of-a-given-dictionary-into-integerfloat-datatypes', '<div>def convert_to_int(lst):</div><div>&nbsp; &nbsp; result = [dict([a, int(x)] for a, x in b.items( )) for b in lst]</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>def convert_to_float(lst):</div><div>&nbsp; &nbsp; result = [dict([a, float(x)] for a, x in b.items( )) for b in lst]</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>nums =[{ \'x\':\'10\' , \'y\':\'20\' , \'z\':\'30\' }, { \'p\':\'40\', \'q\':\'50\', \'r\':\'60\'}]</div><div>print(\"Original list:\")</div><div>print(nums)</div><div>print(\"\\nString values of a given dictionary, into integer types:\")</div><div>print(convert_to_int(nums))</div><div><br></div><div>nums =[{ \'x\':\'10.12\', \'y\':\'20.23\', \'z\':\'30\'}, { \'p\':\'40.00\', \'q\':\'50.19\', \'r\':\'60.99\'}]</div><div>print(\"\\nOriginal list:\")</div><div>print(nums)</div><div>print(\"\\nString values of a given dictionary, into float types:\")</div><div>print(convert_to_float(nums))</div><div><br></div>', 'def convert_to_int(lst):\r\n    result = [dict([a, int(x)] for a, x in b.items( )) for b in lst]\r\n    return result\r\n\r\ndef convert_to_float(lst):\r\n    result = [dict([a, float(x)] for a, x in b.items( )) for b in lst]\r\n    return result\r\n\r\nnums =[{ \'x\':\'10\' , \'y\':\'20\' , \'z\':\'30\' }, { \'p\':\'40\', \'q\':\'50\', \'r\':\'60\'}]\r\nprint(\"Original list:\")\r\nprint(nums)\r\nprint(\"\\nString values of a given dictionary, into integer types:\")\r\nprint(convert_to_int(nums))\r\n\r\nnums =[{ \'x\':\'10.12\', \'y\':\'20.23\', \'z\':\'30\'}, { \'p\':\'40.00\', \'q\':\'50.19\', \'r\':\'60.99\'}]\r\nprint(\"\\nOriginal list:\")\r\nprint(nums)\r\nprint(\"\\nString values of a given dictionary, into float types:\")\r\nprint(convert_to_float(nums))', 0, 0, '2023-09-25 09:15:00', '2023-09-25 03:15:00', '2023-09-25 03:15:00', 1),
(879, 'Write a Python program to clear the list values in a dictionary where lists are used as values', 'write-a-python-program-to-clear-the-list-values-in-a-dictionary-where-lists-are-used-as-values', '<div>def test(dictionary):</div><div>&nbsp; &nbsp; for key in dictionary:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; dictionary[key].clear( )</div><div>&nbsp; &nbsp; return dictionary</div><div><br></div><div>dictionary = {&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\'C1\' : [10,20,30],&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\'C2\' : [20,30,40],</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\'C3\' : [12,34]</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}</div><div><br></div><div>print(\"\\nOriginal Dictionary:\")</div><div>print(dictionary)</div><div>print(\"\\nClear the list values in the said dictionary:\")</div><div>print(test(dictionary))</div><div><br></div>', 'def test(dictionary):\r\n    for key in dictionary:\r\n        dictionary[key].clear( )\r\n    return dictionary\r\n\r\ndictionary = { \r\n               \'C1\' : [10,20,30], \r\n               \'C2\' : [20,30,40],\r\n               \'C3\' : [12,34]\r\n             }\r\n\r\nprint(\"\\nOriginal Dictionary:\")\r\nprint(dictionary)\r\nprint(\"\\nClear the list values in the said dictionary:\")\r\nprint(test(dictionary))', 0, 0, '2023-09-25 09:19:01', '2023-09-25 03:19:01', '2023-09-25 03:19:01', 1);
