(880, 'Write a Python program to update the list values in the said dictionary where lists are used as values.', 'write-a-python-program-to-clear-the-list-values-in-a-dictionary-where-lists-are-used-as-values', '<div>def test(dictionary):</div><div>&nbsp; &nbsp; dictionary[\'Math\'] = [x+1 for x in dictionary[\'Math\']]</div><div>&nbsp; &nbsp; dictionary[\'Physics\'] = [x-2 for x in dictionary[\'Physics\']]</div><div>&nbsp; &nbsp; return dictionary</div><div><br></div><div>dictionary = {&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\'Math\' : [88, 89, 90],</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\'Physics\' : [92, 94, 89],</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;\'Chemistry\' : [90, 87, 93]</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}</div><div><br></div><div>print(\"\\nOriginal Dictionary:\")</div><div>print(dictionary)</div><div>print(\"\\nUpdate the list values of the said dictionary:\")</div><div>print(test(dictionary))</div><div><br></div>', 'def test(dictionary):\r\n    dictionary[\'Math\'] = [x+1 for x in dictionary[\'Math\']]\r\n    dictionary[\'Physics\'] = [x-2 for x in dictionary[\'Physics\']]\r\n    return dictionary\r\n\r\ndictionary = { \r\n               \'Math\' : [88, 89, 90],\r\n               \'Physics\' : [92, 94, 89],\r\n               \'Chemistry\' : [90, 87, 93]\r\n             }\r\n\r\nprint(\"\\nOriginal Dictionary:\")\r\nprint(dictionary)\r\nprint(\"\\nUpdate the list values of the said dictionary:\")\r\nprint(test(dictionary))', 0, 0, '2023-09-25 10:07:20', '2023-09-25 04:07:20', '2023-09-25 04:07:20', 1),
(881, 'Write a Python program to extract a list of values from a given list of dictionaries.', 'write-a-python-program-to-extract-a-list-of-values-from-a-given-list-of-dictionaries', '<div>def test(lst, marks):</div><div>&nbsp; &nbsp; result = [d[marks] for d in lst if marks in d]</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>marks = [{\'Math\': 90, \'Science\': 92},</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;{\'Math\': 89, \'Science\': 94},</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;{\'Math\': 92, \'Science\': 88}]</div><div><br></div><div>print(\"\\nOriginal Dictionary:\")</div><div>print(marks)</div><div>subj = \"Science\"</div><div>print(\"\\nExtract a list of values from said list of dictionaries where subject =\",subj)</div><div>print(test(marks, subj))</div><div><br></div><div>print(\"\\nOriginal Dictionary:\")</div><div>print(marks)</div><div>subj = \"Math\"</div><div>print(\"\\nExtract a list of values from said list of dictionaries where subject =\",subj)</div><div>print(test(marks, subj))</div><div><br></div>', 'def test(lst, marks):\r\n    result = [d[marks] for d in lst if marks in d]\r\n    return result\r\n\r\nmarks = [{\'Math\': 90, \'Science\': 92},\r\n         {\'Math\': 89, \'Science\': 94},\r\n         {\'Math\': 92, \'Science\': 88}]\r\n\r\nprint(\"\\nOriginal Dictionary:\")\r\nprint(marks)\r\nsubj = \"Science\"\r\nprint(\"\\nExtract a list of values from said list of dictionaries where subject =\",subj)\r\nprint(test(marks, subj))\r\n\r\nprint(\"\\nOriginal Dictionary:\")\r\nprint(marks)\r\nsubj = \"Math\"\r\nprint(\"\\nExtract a list of values from said list of dictionaries where subject =\",subj)\r\nprint(test(marks, subj))', 2, 0, '2023-09-25 10:12:00', '2023-09-25 04:12:00', '2023-09-25 14:08:55', 1),
(882, 'Write a Python program to find the length of a dictionary of values.', 'write-a-python-program-to-find-the-length-of-a-dictionary-of-values', '<div>def test(dictt):</div><div>&nbsp; &nbsp; result = { }</div><div>&nbsp; &nbsp; for val in dictt.values( ):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; result[val] = len(val)</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>color_dict = {1 : \'red\', 2 : \'green\', 3 : \'black\', 4 : \'white\', 5 : \'black\'}</div><div>print(\"\\nOriginal Dictionary:\")</div><div>print(color_dict)</div><div>print(\"Length of dictionary values:\")</div><div>print(test(color_dict))</div><div><br></div><div>color_dict = {\'1\' : \'Austin Little\', \'2\' : \'Natasha Howard\', \'3\' : \'Alfred Mullins\', \'4\' : \'Jamie Rowe\'}</div><div>print(\"\\nOriginal Dictionary:\")</div><div>print(color_dict)</div><div>print(\"Length of dictionary values:\")</div><div>print(test(color_dict))</div><div><br></div>', 'def test(dictt):\r\n    result = { }\r\n    for val in dictt.values( ):\r\n        result[val] = len(val)\r\n    return result\r\n\r\ncolor_dict = {1 : \'red\', 2 : \'green\', 3 : \'black\', 4 : \'white\', 5 : \'black\'}\r\nprint(\"\\nOriginal Dictionary:\")\r\nprint(color_dict)\r\nprint(\"Length of dictionary values:\")\r\nprint(test(color_dict))\r\n\r\ncolor_dict = {\'1\' : \'Austin Little\', \'2\' : \'Natasha Howard\', \'3\' : \'Alfred Mullins\', \'4\' : \'Jamie Rowe\'}\r\nprint(\"\\nOriginal Dictionary:\")\r\nprint(color_dict)\r\nprint(\"Length of dictionary values:\")\r\nprint(test(color_dict))', 0, 0, '2023-09-25 10:14:32', '2023-09-25 04:14:32', '2023-09-25 04:14:32', 1),
