(956, 'Write a Python program to check if two given sets have no elements in common.', 'write-a-python-program-to-check-if-two-given-sets-have-no-elements-in-common', '<div>x = {1,2,3,4}</div><div>y = {4,5,6,7}</div><div>z = {8}</div><div><br></div><div>print(\"Original set elements:\")</div><div>print(x)</div><div>print(y)</div><div>print(z)</div><div><br></div><div>print(\"\\nConfirm two given sets have no element(s) in common:\")</div><div>print(\"\\nCompare x and y:\")</div><div>print(x.isdisjoint(y))</div><div><br></div><div>print(\"\\nCompare x and z:\")</div><div>print(z.isdisjoint(x))</div><div><br></div><div>print(\"\\nCompare y and z:\")</div><div>print(y.isdisjoint(z))</div><div><br></div>', 'x = {1,2,3,4}\r\ny = {4,5,6,7}\r\nz = {8}\r\n\r\nprint(\"Original set elements:\")\r\nprint(x)\r\nprint(y)\r\nprint(z)\r\n\r\nprint(\"\\nConfirm two given sets have no element(s) in common:\")\r\nprint(\"\\nCompare x and y:\")\r\nprint(x.isdisjoint(y))\r\n\r\nprint(\"\\nCompare x and z:\")\r\nprint(z.isdisjoint(x))\r\n\r\nprint(\"\\nCompare y and z:\")\r\nprint(y.isdisjoint(z))', 0, 0, '2023-09-27 00:47:21', '2023-09-26 18:47:21', '2023-09-26 18:47:21', 1),
(957, 'Write a Python program to check if a given set is a superset of itself and a superset of another given set.', 'write-a-python-program-to-check-if-a-given-set-is-a-superset-of-itself-and-a-superset-of-another-given-set', '<div>my_set = {1, 2, 3, 4, 5}</div><div><br></div><div># Check if my_set is a superset of itself</div><div>is_superset_of_itself = my_set.issuperset(my_set)</div><div><br></div><div>another_set = {3, 4}</div><div><br></div><div># Check if my_set is a superset of another_set</div><div>is_superset_of_another_set = my_set.issuperset(another_set)</div><div><br></div><div>if is_superset_of_itself:</div><div>&nbsp; &nbsp; print(\"my_set is a superset of itself (using issuperset() method)\")</div><div>else:</div><div>&nbsp; &nbsp; print(\"my_set is not a superset of itself (using issuperset() method)\")</div><div><br></div><div>if is_superset_of_another_set:</div><div>&nbsp; &nbsp; print(\"my_set is a superset of another_set (using issuperset() method)\")</div><div>else:</div><div>&nbsp; &nbsp; print(\"my_set is not a superset of another_set (using issuperset() method)\")</div><div><br></div><div># Using the &gt;= operator</div><div>is_superset_operator = my_set &gt;= another_set</div><div><br></div><div>if is_superset_operator:</div><div>&nbsp; &nbsp; print(\"my_set is a superset of another_set (using &gt;= operator)\")</div><div>else:</div><div>&nbsp; &nbsp; print(\"my_set is not a superset of another_set (using &gt;= operator)\")</div><div><br></div>', 'my_set = {1, 2, 3, 4, 5}\r\n\r\n# Check if my_set is a superset of itself\r\nis_superset_of_itself = my_set.issuperset(my_set)\r\n\r\nanother_set = {3, 4}\r\n\r\n# Check if my_set is a superset of another_set\r\nis_superset_of_another_set = my_set.issuperset(another_set)\r\n\r\nif is_superset_of_itself:\r\n    print(\"my_set is a superset of itself (using issuperset() method)\")\r\nelse:\r\n    print(\"my_set is not a superset of itself (using issuperset() method)\")\r\n\r\nif is_superset_of_another_set:\r\n    print(\"my_set is a superset of another_set (using issuperset() method)\")\r\nelse:\r\n    print(\"my_set is not a superset of another_set (using issuperset() method)\")\r\n\r\n# Using the >= operator\r\nis_superset_operator = my_set >= another_set\r\n\r\nif is_superset_operator:\r\n    print(\"my_set is a superset of another_set (using >= operator)\")\r\nelse:\r\n    print(\"my_set is not a superset of another_set (using >= operator)\")', 0, 0, '2023-09-27 01:02:31', '2023-09-26 19:02:31', '2023-09-26 19:02:31', 1),
(958, 'Write a Python program to find elements in a given set that are not in another set.', 'write-a-python-program-to-find-elements-in-a-given-set-that-are-not-in-another-set', '<div>sn1 = {1,2,3,4,5}</div><div>sn2 = {4,5,6,7,8}</div><div><br></div><div>print(\"Original sets:\")</div><div>print(sn1)</div><div>print(sn2)</div><div><br></div><div>print(\"Difference of sn1 and sn2 using difference():\")</div><div>print(sn1.difference(sn2))</div><div>print(\"Difference of sn2 and sn1 using difference():\")</div><div>print(sn2.difference(sn1))</div><div><br></div><div>print(\"Difference of sn1 and sn2 using - operator:\")</div><div>print(sn1-sn2)</div><div>print(\"Difference of sn2 and sn1 using - operator:\")</div><div>print(sn2-sn1)</div><div><br></div>', 'sn1 = {1,2,3,4,5}\r\nsn2 = {4,5,6,7,8}\r\n\r\nprint(\"Original sets:\")\r\nprint(sn1)\r\nprint(sn2)\r\n\r\nprint(\"Difference of sn1 and sn2 using difference():\")\r\nprint(sn1.difference(sn2))\r\nprint(\"Difference of sn2 and sn1 using difference():\")\r\nprint(sn2.difference(sn1))\r\n\r\nprint(\"Difference of sn1 and sn2 using - operator:\")\r\nprint(sn1-sn2)\r\nprint(\"Difference of sn2 and sn1 using - operator:\")\r\nprint(sn2-sn1)', 0, 0, '2023-09-27 01:06:18', '2023-09-26 19:06:18', '2023-09-26 19:06:18', 1),
(959, 'Write a Python program to remove the intersection of a second set with a first set.', 'write-a-python-program-to-remove-the-intersection-of-a-second-set-with-a-first-set', '<div>sn1 = {1,2,3,4,5}</div><div>sn2 = {4,5,6,7,8}</div><div><br></div><div>print(\"Original sets:\")</div><div>print(sn1)</div><div>print(sn2)</div><div><br></div><div>print(\"\\nRemove the intersection of a 2nd set from the 1st set using difference_update():\")</div><div>sn1.difference_update(sn2)</div><div>print(\"sn1: \",sn1)</div><div>print(\"sn2: \",sn2)</div><div><br></div><div>sn1 = {1,2,3,4,5}</div><div>sn2 = {4,5,6,7,8}</div><div><br></div><div>print(\"\\nRemove the intersection of a 2nd set from the 1st set using -= operator:\")</div><div>sn1-=sn2</div><div>print(\"sn1: \",sn1)</div><div>print(\"sn2: \",sn2)</div><div><br></div>', 'sn1 = {1,2,3,4,5}\r\nsn2 = {4,5,6,7,8}\r\n\r\nprint(\"Original sets:\")\r\nprint(sn1)\r\nprint(sn2)\r\n\r\nprint(\"\\nRemove the intersection of a 2nd set from the 1st set using difference_update():\")\r\nsn1.difference_update(sn2)\r\nprint(\"sn1: \",sn1)\r\nprint(\"sn2: \",sn2)\r\n\r\nsn1 = {1,2,3,4,5}\r\nsn2 = {4,5,6,7,8}\r\n\r\nprint(\"\\nRemove the intersection of a 2nd set from the 1st set using -= operator:\")\r\nsn1-=sn2\r\nprint(\"sn1: \",sn1)\r\nprint(\"sn2: \",sn2)', 0, 0, '2023-09-27 01:10:00', '2023-09-26 19:10:00', '2023-09-26 19:10:00', 1),
(960, 'Write a Python program to find all the unique words and count the frequency of occurrence from a given list of strings. Use Python set data type.', 'write-a-python-program-to-find-all-the-unique-words-and-count-the-frequency-of-occurrence-from-a-given-list-of-strings-use-python-set-data-type', '<div>def word_count(words):</div><div>&nbsp; &nbsp; word_set = set(words)</div><div>&nbsp; &nbsp; word_counts = { }</div><div>&nbsp; &nbsp; for word in word_set:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; word_counts[word] = words.count(word)</div><div>&nbsp; &nbsp; return word_counts</div><div><br></div><div>words = [\'Red\', \'Green\', \'Red\', \'Blue\', \'Red\', \'Red\', \'Green\']</div><div>print(word_count(words))</div><div><br></div>', 'def word_count(words):\r\n    word_set = set(words)\r\n    word_counts = { }\r\n    for word in word_set:\r\n        word_counts[word] = words.count(word)\r\n    return word_counts\r\n\r\nwords = [\'Red\', \'Green\', \'Red\', \'Blue\', \'Red\', \'Red\', \'Green\']\r\nprint(word_count(words))', 0, 0, '2023-09-27 01:27:58', '2023-09-26 19:27:58', '2023-09-26 19:27:58', 1);
