(996, 'Write a Python program to append items from inerrable to the end of the array.', 'write-a-python-program-to-append-items-from-inerrable-to-the-end-of-the-array', '<div>from array import *</div><div><br></div><div>array_num = array(\'i\', [1, 3, 5, 7, 9])</div><div>print(\"Original array: \"+str(array_num))</div><div>array_num.extend(array_num)</div><div>print(\"Extended array: \"+str(array_num))</div><div><br></div>', 'from array import *\r\n\r\narray_num = array(\'i\', [1, 3, 5, 7, 9])\r\nprint(\"Original array: \"+str(array_num))\r\narray_num.extend(array_num)\r\nprint(\"Extended array: \"+str(array_num))', 0, 0, '2023-10-01 01:08:33', '2023-09-30 19:08:33', '2023-09-30 19:08:33', 1),
(997, 'Write a Python program to append items to a specified list.', 'write-a-python-program-to-append-items-to-a-specified-list', '<div>from array import *</div><div><br></div><div>num_list = [1, 2, 6, -8]</div><div>array_num = array(\'i\', [ ])</div><div>print(\"Items in the list: \" + str(num_list))</div><div><br></div><div>print(\"Append items from the list: \")</div><div>array_num.fromlist(num_list)</div><div>print(\"Items in the array: \"+str(array_num))</div><div><br></div>', 'from array import *\r\n\r\nnum_list = [1, 2, 6, -8]\r\narray_num = array(\'i\', [ ])\r\nprint(\"Items in the list: \" + str(num_list))\r\n\r\nprint(\"Append items from the list: \")\r\narray_num.fromlist(num_list)\r\nprint(\"Items in the array: \"+str(array_num))', 0, 0, '2023-10-01 01:13:28', '2023-09-30 19:13:28', '2023-09-30 19:13:28', 1),
