(999, 'Write a Python program to remove a specified item using the index of an array.', 'write-a-python-program-to-remove-a-specified-item-using-the-index-of-an-array', '<div>from array import *</div><div><br></div><div>array_num = array(\'i\', [1, 3, 5, 7, 9])</div><div>print(\"Original array: \"+str(array_num))</div><div><br></div><div>print(\"Remove the third item form the array:\")</div><div>array_num.pop(2)</div><div>print(\"New array: \"+str(array_num))</div><div><br></div>', 'from array import *\r\n\r\narray_num = array(\'i\', [1, 3, 5, 7, 9])\r\nprint(\"Original array: \"+str(array_num))\r\n\r\nprint(\"Remove the third item form the array:\")\r\narray_num.pop(2)\r\nprint(\"New array: \"+str(array_num))', 2, 0, '2023-10-01 01:18:44', '2023-09-30 19:18:44', '2023-09-30 19:19:28', 1),
(1000, 'Write a Python program to remove the first occurrence of a specified element from an array.', 'write-a-python-program-to-remove-the-first-occurrence-of-a-specified-element-from-an-array', '<div>from array import *</div><div><br></div><div>array_num = array(\'i\', [1, 3, 5, 3, 7, 1, 9, 3])</div><div>print(\"Original array: \"+str(array_num))</div><div>print(\"Remove the first occurrence of 3 from the said array:\")</div><div>array_num.remove(3)</div><div>print(\"New array: \"+str(array_num))</div><div><br></div>', 'from array import *\r\n\r\narray_num = array(\'i\', [1, 3, 5, 3, 7, 1, 9, 3])\r\nprint(\"Original array: \"+str(array_num))\r\nprint(\"Remove the first occurrence of 3 from the said array:\")\r\narray_num.remove(3)\r\nprint(\"New array: \"+str(array_num))', 0, 0, '2023-10-01 01:20:33', '2023-09-30 19:20:33', '2023-09-30 19:20:33', 1),
