(1041, 'Write a Python program to find numbers within a given range where every number is divisible by every digit it contains.', 'write-a-python-program-to-find-numbers-within-a-given-range-where-every-number-is-divisible-by-every-digit-it-contains', '<div>def divisible_by_digits(start_num, end_num):</div><div>&nbsp; &nbsp; return [n for n in range(start_num, end_num+1)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if not any(map(lambda x: int(x) == 0 or n%int(x) != 0, str(n)))]</div><div><br></div><div>print(divisible_by_digits(1,22))</div><div><br></div>', 'def divisible_by_digits(start_num, end_num):\r\n    return [n for n in range(start_num, end_num+1)\r\n                if not any(map(lambda x: int(x) == 0 or n%int(x) != 0, str(n)))]\r\n\r\nprint(divisible_by_digits(1,22))', 0, 0, '2023-10-03 01:27:41', '2023-10-02 19:27:41', '2023-10-02 19:27:41', 1),
(1042, 'Write a Python program to create the next bigger number by rearranging the digits of a given number.', 'write-a-python-program-to-create-the-next-bigger-number-by-rearranging-the-digits-of-a-given-number', '<div>def rearrange_bigger(n):</div><div>&nbsp; &nbsp; #Break the number into digits and store in a list</div><div>&nbsp; &nbsp; nums = list(str(n))</div><div>&nbsp; &nbsp; for i in range(len(nums)-2,-1,-1):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; if nums[i] &lt; nums[i+1]:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; z = nums[i:]</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; y = min(filter(lambda x: x &gt; z[0], z))</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; z.remove(y)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; z.sort( )</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nums[i:] = [y] + z</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return int(\"\".join(nums))</div><div>&nbsp; &nbsp; return False</div><div><br></div><div>n = 12</div><div>print(\"Original number:\",n)</div><div>print(\"Next bigger number:\",rearrange_bigger(n))</div><div><br></div><div>n = 10</div><div>print(\"\\nOriginal number:\",n)</div><div>print(\"Next bigger number:\",rearrange_bigger(n))</div><div><br></div><div>n = 201</div><div>print(\"\\nOriginal number:\",n)</div><div>print(\"Next bigger number:\",rearrange_bigger(n))</div><div><br></div><div>n = 102</div><div>print(\"\\nOriginal number:\",n)</div><div>print(\"Next bigger number:\",rearrange_bigger(n))</div><div><br></div><div>n = 445</div><div>print(\"\\nOriginal number:\",n)</div><div>print(\"Next bigger number:\",rearrange_bigger(n))</div><div><br></div>', 'def rearrange_bigger(n):\r\n    #Break the number into digits and store in a list\r\n    nums = list(str(n))\r\n    for i in range(len(nums)-2,-1,-1):\r\n        if nums[i] < nums[i+1]:\r\n            z = nums[i:]\r\n            y = min(filter(lambda x: x > z[0], z))\r\n            z.remove(y)\r\n            z.sort( )\r\n            nums[i:] = [y] + z\r\n            return int(\"\".join(nums))\r\n    return False\r\n\r\nn = 12\r\nprint(\"Original number:\",n)\r\nprint(\"Next bigger number:\",rearrange_bigger(n))\r\n\r\nn = 10\r\nprint(\"\\nOriginal number:\",n)\r\nprint(\"Next bigger number:\",rearrange_bigger(n))\r\n\r\nn = 201\r\nprint(\"\\nOriginal number:\",n)\r\nprint(\"Next bigger number:\",rearrange_bigger(n))\r\n\r\nn = 102\r\nprint(\"\\nOriginal number:\",n)\r\nprint(\"Next bigger number:\",rearrange_bigger(n))\r\n\r\nn = 445\r\nprint(\"\\nOriginal number:\",n)\r\nprint(\"Next bigger number:\",rearrange_bigger(n))', 0, 0, '2023-10-03 01:30:35', '2023-10-02 19:30:35', '2023-10-02 19:30:35', 1),
(1043, 'Write a Python program to find a list with maximum and minimum length using lambda.', 'write-a-python-program-to-find-a-list-with-maximum-and-minimum-length-using-lambda', '<div>def max_length_list(input_list):</div><div>&nbsp; &nbsp; max_length = max(len(x) for x in input_list )</div><div>&nbsp; &nbsp; max_list = max(input_list, key = lambda i: len(i))</div><div>&nbsp; &nbsp; return(max_length, max_list)</div><div><br></div><div>def min_length_list(input_list):</div><div>&nbsp; &nbsp; min_length = min(len(x) for x in input_list )</div><div>&nbsp; &nbsp; min_list = min(input_list, key = lambda i: len(i))</div><div>&nbsp; &nbsp; return(min_length, min_list)</div><div><br></div><div>list1 = [[0], [1, 3], [5, 7], [9, 11], [13, 15, 17]]</div><div><br></div><div>print(\"Original list:\")</div><div>print(list1)</div><div><br></div><div>print(\"\\nList with maximum length of lists:\")</div><div>print(max_length_list(list1))</div><div>print(\"\\nList with minimum length of lists:\")</div><div>print(min_length_list(list1))</div><div><br></div>', 'def max_length_list(input_list):\r\n    max_length = max(len(x) for x in input_list )\r\n    max_list = max(input_list, key = lambda i: len(i))\r\n    return(max_length, max_list)\r\n\r\ndef min_length_list(input_list):\r\n    min_length = min(len(x) for x in input_list )\r\n    min_list = min(input_list, key = lambda i: len(i))\r\n    return(min_length, min_list)\r\n\r\nlist1 = [[0], [1, 3], [5, 7], [9, 11], [13, 15, 17]]\r\n\r\nprint(\"Original list:\")\r\nprint(list1)\r\n\r\nprint(\"\\nList with maximum length of lists:\")\r\nprint(max_length_list(list1))\r\nprint(\"\\nList with minimum length of lists:\")\r\nprint(min_length_list(list1))', 0, 0, '2023-10-03 01:34:53', '2023-10-02 19:34:53', '2023-10-02 19:34:53', 1),
(1044, 'Write a Python program to sort each sublist of strings in a given list of lists using lambda.', 'write-a-python-program-to-sort-each-sublist-of-strings-in-a-given-list-of-lists-using-lambda', '<div>def sort_sublists(input_list):</div><div>&nbsp; &nbsp; result = [sorted(x, key = lambda x:x[0]) for x in input_list]</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>color1 = [[\"green\", \"orange\"], [\"black\", \"white\"], [\"white\", \"black\", \"orange\"]]</div><div><br></div><div>print(\"\\nOriginal list:\")</div><div>print(color1)</div><div>print(\"\\nAfter sorting each sublist of the said list of lists:\")</div><div>print(sort_sublists(color1))</div><div><br></div>', 'def sort_sublists(input_list):\r\n    result = [sorted(x, key = lambda x:x[0]) for x in input_list]\r\n    return result\r\n\r\ncolor1 = [[\"green\", \"orange\"], [\"black\", \"white\"], [\"white\", \"black\", \"orange\"]]\r\n\r\nprint(\"\\nOriginal list:\")\r\nprint(color1)\r\nprint(\"\\nAfter sorting each sublist of the said list of lists:\")\r\nprint(sort_sublists(color1))', 0, 0, '2023-10-03 01:36:27', '2023-10-02 19:36:27', '2023-10-02 19:36:27', 1),
(1045, 'Write a Python program to sort a given list of lists by length and value using lambda.', 'write-a-python-program-to-sort-a-given-list-of-lists-by-length-and-value-using-lambda', '<div>def sort_sublists(input_list):</div><div>&nbsp; &nbsp; result = sorted(input_list, key=lambda l: (len(l), l))</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>list1 = [[2], [0], [1, 3], [0, 7], [9, 11], [13, 15, 17]]</div><div><br></div><div>print(\"Original list:\")</div><div>print(list1)</div><div>print(\"\\nSort the list of lists by length and value:\")</div><div>print(sort_sublists(list1))</div><div><br></div>', 'def sort_sublists(input_list):\r\n    result = sorted(input_list, key=lambda l: (len(l), l))\r\n    return result\r\n\r\nlist1 = [[2], [0], [1, 3], [0, 7], [9, 11], [13, 15, 17]]\r\n\r\nprint(\"Original list:\")\r\nprint(list1)\r\nprint(\"\\nSort the list of lists by length and value:\")\r\nprint(sort_sublists(list1))', 0, 0, '2023-10-03 01:38:12', '2023-10-02 19:38:12', '2023-10-02 19:38:12', 1),
