(1076, 'Write a Python program to compute the square of the first N Fibonacci numbers, using the map function and generate a list of the numbers.', 'write-a-python-program-to-compute-the-square-of-the-first-n-fibonacci-numbers-using-the-map-function-and-generate-a-list-of-the-numbers', '<div>import itertools</div><div><br></div><div>n = 10</div><div><br></div><div>def fibonacci_nums(x=0, y=1):</div><div>&nbsp; &nbsp; yield x</div><div>&nbsp; &nbsp; while True:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; yield y</div><div>&nbsp; &nbsp; &nbsp; &nbsp; x, y = y, x + y</div><div><br></div><div>print(\"First 10 Fibonacci numbers:\")</div><div>result = list(itertools.islice(fibonacci_nums(), n))</div><div>print(result)</div><div>square = lambda x: x * x</div><div>print(\"\\nAfter squaring said numbers of the list:\")</div><div>print(list(map(square, result)))</div><div><br></div>', 'import itertools\r\n\r\nn = 10\r\n\r\ndef fibonacci_nums(x=0, y=1):\r\n    yield x\r\n    while True:\r\n        yield y\r\n        x, y = y, x + y\r\n\r\nprint(\"First 10 Fibonacci numbers:\")\r\nresult = list(itertools.islice(fibonacci_nums(), n))\r\nprint(result)\r\nsquare = lambda x: x * x\r\nprint(\"\\nAfter squaring said numbers of the list:\")\r\nprint(list(map(square, result)))', 0, 0, '2023-10-04 01:25:44', '2023-10-03 19:25:44', '2023-10-03 19:25:44', 1),
(1077, 'Write a Python program to compute the sum of elements of an array of integers. Use the map() function.', 'write-a-python-program-to-compute-the-sum-of-elements-of-an-array-of-integers-use-the-map-function', '<div>from array import array</div><div><br></div><div>def array_sum(nums_arr):</div><div>&nbsp; &nbsp; sum_n = 0</div><div>&nbsp; &nbsp; for n in nums_arr:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; sum_n += n</div><div>&nbsp; &nbsp; return sum_n</div><div><br></div><div>nums = array(\'i\', [1, 2, 3, 4, 5, -15])</div><div>print(\"Original array:\",nums)</div><div>nums_arr = list(map(int, nums))</div><div>result = array_sum(nums_arr)</div><div>print(\"Sum of all elements of the said array:\")</div><div>print(result)</div><div><br></div>', 'from array import array\r\n\r\ndef array_sum(nums_arr):\r\n    sum_n = 0\r\n    for n in nums_arr:\r\n        sum_n += n\r\n    return sum_n\r\n\r\nnums = array(\'i\', [1, 2, 3, 4, 5, -15])\r\nprint(\"Original array:\",nums)\r\nnums_arr = list(map(int, nums))\r\nresult = array_sum(nums_arr)\r\nprint(\"Sum of all elements of the said array:\")\r\nprint(result)', 0, 0, '2023-10-04 01:29:13', '2023-10-03 19:29:13', '2023-10-03 19:29:13', 1),
(1078, 'Write a Python program to find the ratio of positive numbers, negative numbers and zeroes in an array of integers.', 'write-a-python-program-to-find-the-ratio-of-positive-numbers-negative-numbers-and-zeroes-in-an-array-of-integers', '<div>from array import array</div><div><br></div><div>def plusMinus(nums):</div><div>&nbsp; &nbsp; n = len(nums)</div><div>&nbsp; &nbsp; n1 = n2 = n3 = 0</div><div><br></div><div>&nbsp; &nbsp; for x in nums:<br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; if x &gt; 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; n1 += 1</div><div>&nbsp; &nbsp; &nbsp; &nbsp; elif x &lt; 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; n2 += 1</div><div>&nbsp; &nbsp; &nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; n3 += 1</div><div><br></div><div>&nbsp; &nbsp; return round(n1/n,2), round(n2/n,2), round(n3/n,2)<br></div><div><br></div><div>nums = array(\'i\', [0, 1, 2, -1, -5, 6, 0, -3, -2, 3, 4, 6, 8])</div><div>print(\"Original array:\",nums)</div><div>nums_arr = list(map(int, nums))</div><div>result = plusMinus(nums_arr)</div><div>print(\"Ratio of positive numbers, negative numbers and zeroes:\")</div><div>print(result)</div><div><br></div><div>nums = array(\'i\', [2, 1, 2, -1, -5, 6, 4, -3, -2, 3, 4, 6, 8])</div><div>print(\"\\nOriginal array:\",nums)</div><div>nums_arr = list(map(int, nums))</div><div>result = plusMinus(nums_arr)</div><div>print(\"Ratio of positive numbers, negative numbers and zeroes:\")</div><div>print(result)</div><div><br></div>', 'from array import array\r\n\r\ndef plusMinus(nums):\r\n    n = len(nums)\r\n    n1 = n2 = n3 = 0\r\n\r\n    for x in nums:\r\n        if x > 0:\r\n            n1 += 1\r\n        elif x < 0:\r\n            n2 += 1\r\n        else:\r\n            n3 += 1\r\n\r\n    return round(n1/n,2), round(n2/n,2), round(n3/n,2)\r\n\r\nnums = array(\'i\', [0, 1, 2, -1, -5, 6, 0, -3, -2, 3, 4, 6, 8])\r\nprint(\"Original array:\",nums)\r\nnums_arr = list(map(int, nums))\r\nresult = plusMinus(nums_arr)\r\nprint(\"Ratio of positive numbers, negative numbers and zeroes:\")\r\nprint(result)\r\n\r\nnums = array(\'i\', [2, 1, 2, -1, -5, 6, 4, -3, -2, 3, 4, 6, 8])\r\nprint(\"\\nOriginal array:\",nums)\r\nnums_arr = list(map(int, nums))\r\nresult = plusMinus(nums_arr)\r\nprint(\"Ratio of positive numbers, negative numbers and zeroes:\")\r\nprint(result)', 0, 0, '2023-10-04 01:40:44', '2023-10-03 19:40:44', '2023-10-03 19:40:44', 1);
