(1079, 'Write a Python program to count the same pair in two given lists. Use map() function.', 'write-a-python-program-to-count-the-same-pair-in-two-given-lists-use-map-function', '<div>from operator import eq</div><div><br></div><div>def count_same_pair(nums1, nums2):</div><div>&nbsp; &nbsp; result = sum(map(eq, nums1, nums2))</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>nums1 = [1,2,3,4,5,6,7,8]</div><div>nums2 = [2,2,3,1,2,6,7,9]</div><div>print(\"Original lists:\")</div><div>print(nums1)</div><div>print(nums2)</div><div>print(\"\\nNumber of same pair of the said two given lists:\")</div><div>print(count_same_pair(nums1, nums2))</div><div><br></div>', 'from operator import eq\r\n\r\ndef count_same_pair(nums1, nums2):\r\n    result = sum(map(eq, nums1, nums2))\r\n    return result\r\n\r\nnums1 = [1,2,3,4,5,6,7,8]\r\nnums2 = [2,2,3,1,2,6,7,9]\r\nprint(\"Original lists:\")\r\nprint(nums1)\r\nprint(nums2)\r\nprint(\"\\nNumber of same pair of the said two given lists:\")\r\nprint(count_same_pair(nums1, nums2))', 0, 0, '2023-10-04 01:45:23', '2023-10-03 19:45:23', '2023-10-03 19:45:23', 1),
(1080, 'Write a Python program to interleave two lists into another list randomly. Use the map() function.', 'write-a-python-program-to-interleave-two-lists-into-another-list-randomly-use-the-map-function', '<div>import random</div><div><br></div><div>def randomly_interleave(nums1, nums2):</div><div>&nbsp; &nbsp; result =&nbsp; list(map(next, random.sample([iter(nums1)]*len(nums1) +&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; [iter(nums2)]*len(nums2), len(nums1)+len(nums2))))</div><div>&nbsp; &nbsp; return result</div><div><br></div><div>nums1 = [1,2,7,8,3,7]</div><div>nums2 = [4,3,8,9,4,3,8,9]</div><div>print(\"Original lists:\")</div><div>print(nums1)</div><div>print(nums2)</div><div>print(\"\\nInterleave two given list into another list randomly:\")</div><div>print(randomly_interleave(nums1, nums2))</div><div><br></div>', 'import random\r\n\r\ndef randomly_interleave(nums1, nums2):\r\n    result =  list(map(next, random.sample([iter(nums1)]*len(nums1) + \r\n                                  [iter(nums2)]*len(nums2), len(nums1)+len(nums2))))\r\n    return result\r\n\r\nnums1 = [1,2,7,8,3,7]\r\nnums2 = [4,3,8,9,4,3,8,9]\r\nprint(\"Original lists:\")\r\nprint(nums1)\r\nprint(nums2)\r\nprint(\"\\nInterleave two given list into another list randomly:\")\r\nprint(randomly_interleave(nums1, nums2))', 0, 0, '2023-10-04 01:48:15', '2023-10-03 19:48:15', '2023-10-03 19:48:15', 1),
