(1119, 'Write a Python program display a list of the dates for the 2nd Saturday of every month for a given year.', 'write-a-python-program-display-a-list-of-the-dates-for-the-2nd-saturday-of-every-month-for-a-given-year', '<div>import calendar</div><div><br></div><div># Show every month</div><div>for month in range(1, 13):</div><div>&nbsp; &nbsp; cal = calendar.monthcalendar(2020, month)</div><div>&nbsp; &nbsp; first_week&nbsp; = cal[0]</div><div>&nbsp; &nbsp; second_week = cal[1]</div><div>&nbsp; &nbsp; third_week&nbsp; = cal[2]</div><div><br></div><div>&nbsp; &nbsp; # If a Saturday presents in the first week, the second Saturday</div><div>&nbsp; &nbsp; # is in the second week.&nbsp; Otherwise, the second Saturday must</div><div>&nbsp; &nbsp; &nbsp;# be in the third week.</div><div>&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; if first_week[calendar.SATURDAY]:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; holi_day = second_week[calendar.SATURDAY]</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; holi_day = third_week[calendar.SATURDAY]</div><div><br></div><div>&nbsp; &nbsp; print(\'%3s: %2s\' % (calendar.month_abbr[month], holi_day))</div><div><br></div><div><span style=\"white-space: pre; white-space: normal;\">	</span></div>', 'import calendar\r\n\r\n# Show every month\r\nfor month in range(1, 13):\r\n    cal = calendar.monthcalendar(2020, month)\r\n    first_week  = cal[0]\r\n    second_week = cal[1]\r\n    third_week  = cal[2]\r\n\r\n    # If a Saturday presents in the first week, the second Saturday\r\n    # is in the second week.  Otherwise, the second Saturday must\r\n     # be in the third week.\r\n    \r\n    if first_week[calendar.SATURDAY]:\r\n        holi_day = second_week[calendar.SATURDAY]\r\n    else:\r\n        holi_day = third_week[calendar.SATURDAY]\r\n\r\n    print(\'%3s: %2s\' % (calendar.month_abbr[month], holi_day))', 0, 0, '2023-10-05 14:10:25', '2023-10-05 08:10:25', '2023-10-05 08:10:25', 1),
(1120, 'Write a Python program to get a list of dates between two dates.', 'write-a-python-program-to-get-a-list-of-dates-between-two-dates', '<div>from datetime import timedelta, date</div><div><br></div><div>def daterange(date1, date2):</div><div>&nbsp; &nbsp; for n in range(int ((date2 - date1).days)+1):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; yield date1 + timedelta(n)</div><div><br></div><div>start_dt = date(2015, 12, 20)</div><div>end_dt = date(2016, 1, 11)</div><div><br></div><div>for dt in daterange(start_dt, end_dt):</div><div>&nbsp; &nbsp; print(dt.strftime(\"%Y-%m-%d\"))</div><div><br></div><div><span style=\"white-space: pre; white-space: normal;\">	</span></div>', 'from datetime import timedelta, date\r\n\r\ndef daterange(date1, date2):\r\n    for n in range(int ((date2 - date1).days)+1):\r\n        yield date1 + timedelta(n)\r\n\r\nstart_dt = date(2015, 12, 20)\r\nend_dt = date(2016, 1, 11)\r\n\r\nfor dt in daterange(start_dt, end_dt):\r\n    print(dt.strftime(\"%Y-%m-%d\"))', 0, 0, '2023-10-05 14:14:57', '2023-10-05 08:14:57', '2023-10-05 08:14:57', 1),
