(1132, 'Write a Python program to convert JSON encoded data into Python objects.', 'write-a-python-program-to-convert-json-encoded-data-into-python-objects', '<div>import json</div><div><br></div><div>jobj_dict =&nbsp; \'{\"name\": \"David\", \"age\": 6, \"class\": \"I\"}\'</div><div>jobj_list =&nbsp; &nbsp;\'[\"Red\", \"Green\", \"Black\"]\'</div><div>jobj_string = \'\"Python Json\"\'</div><div>jobj_int = \'1234\'</div><div>jobj_float =&nbsp; \'21.34\'</div><div><br></div><div>python_dict =&nbsp; json.loads(jobj_dict)</div><div>python_list = json.loads(jobj_list)</div><div>python_str =&nbsp; json.loads(jobj_string)</div><div>python_int =&nbsp; &nbsp;json.loads(jobj_int)</div><div>python_float = json.loads(jobj_float)</div><div><br></div><div>print(\"Python dictionary: \", python_dict)</div><div>print(\"Python list: \", python_list)</div><div>print(\"Python string: \", python_str)</div><div>print(\"Python integer: \", python_int)</div><div>print(\"Python float: \", python_float)</div><div><br></div>', 'import json\r\n\r\njobj_dict =  \'{\"name\": \"David\", \"age\": 6, \"class\": \"I\"}\'\r\njobj_list =   \'[\"Red\", \"Green\", \"Black\"]\'\r\njobj_string = \'\"Python Json\"\'\r\njobj_int = \'1234\'\r\njobj_float =  \'21.34\'\r\n\r\npython_dict =  json.loads(jobj_dict)\r\npython_list = json.loads(jobj_list)\r\npython_str =  json.loads(jobj_string)\r\npython_int =   json.loads(jobj_int)\r\npython_float = json.loads(jobj_float)\r\n\r\nprint(\"Python dictionary: \", python_dict)\r\nprint(\"Python list: \", python_list)\r\nprint(\"Python string: \", python_str)\r\nprint(\"Python integer: \", python_int)\r\nprint(\"Python float: \", python_float)', 0, 0, '2023-10-05 15:17:22', '2023-10-05 09:17:22', '2023-10-05 09:17:22', 1),
(1133, 'Write a Python program to check whether an instance is complex or not.', 'write-a-python-program-to-check-whether-an-instance-is-complex-or-not', '<div>import json</div><div><br></div><div>def encode_complex(object):</div><div>&nbsp; &nbsp; # check using isinstance method</div><div>&nbsp; &nbsp; if isinstance(object, complex):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return [object.real, object.imag]</div><div>&nbsp; &nbsp; # raised error if object is not complex</div><div>&nbsp; &nbsp; raise TypeError(repr(object) + \" is not JSON serialized\")</div><div><br></div><div>complex_obj = json.dumps(2 + 3j, default=encode_complex)</div><div>print(complex_obj)</div><div><br></div>', 'import json\r\n\r\ndef encode_complex(object):\r\n    # check using isinstance method\r\n    if isinstance(object, complex):\r\n        return [object.real, object.imag]\r\n    # raised error if object is not complex\r\n    raise TypeError(repr(object) + \" is not JSON serialized\")\r\n\r\ncomplex_obj = json.dumps(2 + 3j, default=encode_complex)\r\nprint(complex_obj)', 0, 0, '2023-10-05 15:25:06', '2023-10-05 09:25:06', '2023-10-05 09:25:06', 1),
(1134, 'Write a Python program to check whether a JSON string contains complex object or not.', 'write-a-python-program-to-check-whether-a-json-string-contains-complex-object-or-not', '<div>import json</div><div><br></div><div>def is_complex_num(objct):</div><div>&nbsp; &nbsp; if \'__complex__\' in objct:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return complex(objct[\'real\'], objct[\'img\'])</div><div>&nbsp; &nbsp; return objct</div><div><br></div><div>complex_object =json.loads(\'{\"__complex__\": true, \"real\": 4, \"img\": 5}\', object_hook = is_complex_num)</div><div>simple_object =json.loads(\'{\"real\": 4, \"img\": 3}\', object_hook = is_complex_num)</div><div><br></div><div>print(\"Complex_object: \",complex_object)</div><div>print(\"Without complex object: \",simple_object)</div><div><br></div>', 'import json\r\n\r\ndef is_complex_num(objct):\r\n    if \'__complex__\' in objct:\r\n        return complex(objct[\'real\'], objct[\'img\'])\r\n    return objct\r\n\r\ncomplex_object =json.loads(\'{\"__complex__\": true, \"real\": 4, \"img\": 5}\', object_hook = is_complex_num)\r\nsimple_object =json.loads(\'{\"real\": 4, \"img\": 3}\', object_hook = is_complex_num)\r\n\r\nprint(\"Complex_object: \",complex_object)\r\nprint(\"Without complex object: \",simple_object)', 0, 0, '2023-10-05 15:26:43', '2023-10-05 09:26:43', '2023-10-05 09:26:43', 1),
