(104, 'Write a Python program to get the Identity, Type, and Value of an object.', 'write-a-python-program-to-get-the-identity-type-and-value-of-an-object', '<p>x = 34</p><p>print(\"\\nIdentity: \",x)</p><p>print(\"\\nType: \",type(x))</p><p>print(\"\\nValue: \",id(x))</p><div><br></div>', NULL, NULL, '2023-03-19 15:45:55', '2023-03-19 15:45:55'),
(105, 'Write a Python program to convert the bytes in a given string to a list of integers.', 'write-a-python-program-to-convert-the-bytes-in-a-given-string-to-a-list-of-integers', '<p>S = \"The quick brown fox jumps over the lazy dog.\"&nbsp;&nbsp;</p><p>print(\"Original string:\")</p><p>print(S)</p><p>nums = []</p><p>print(\"\\nConvert bytes of the said string to a list of integers:\")</p><p>for chr in S:</p><p>&nbsp; &nbsp;nums.append(ord(chr))</p><p>print(nums)</p><div><br></div>', NULL, NULL, '2023-03-19 15:47:56', '2023-03-19 15:47:56'),
(106, 'Write a Python program to check whether a string is numeric.', 'write-a-python-program-to-check-whether-a-string-is-numeric', '<p>str = \'a123\'</p><p>#str = \'123\'</p><p>try:</p><p>&nbsp; &nbsp; i = float(str)</p><p>except (ValueError, TypeError):</p><p>&nbsp; &nbsp; print(\'\\nNot numeric\')</p><p>print()</p><div><br></div>', NULL, NULL, '2023-03-19 15:49:02', '2023-03-19 15:49:02'),
