(111, 'Write a Python program to access and print a URL\'s content to the console.', 'write-a-python-program-to-access-and-print-a-urls-content-to-the-console', '<p>import requests</p><p>data = requests.get(\'https://google.com/\')</p><p>print(data.text)</p><div><br></div>', NULL, NULL, '2023-03-19 16:01:19', '2023-03-19 16:01:19'),
(112, 'Write a Python program to get system command output.', 'write-a-python-program-to-get-system-command-output', '<p>import subprocess</p><p># file and directory listing</p><p>returned_text = subprocess.check_output(\"dir\", shell=True, universal_newlines=True)</p><p>print(\"dir command to list file and directory\")</p><p>print(returned_text)</p><div><br></div>', NULL, NULL, '2023-03-19 16:02:07', '2023-03-19 16:02:07'),
(113, 'Write a Python program to extract the filename from a given path.', 'write-a-python-program-to-extract-the-filename-from-a-given-path', '<p>import os</p><p>print()</p><p>print(os.path.basename(\'/users/system1/student1/homework-1.py\'))</p><p>print()</p><div><br></div>', NULL, NULL, '2023-03-19 16:02:49', '2023-03-19 16:02:49'),
(114, 'Write a Python program to get the effective group id, effective user id, real group id, and a list of supplemental group ids associated with the current process.', 'write-a-python-program-to-get-the-effective-group-id-effective-user-id-real-group-id-and-a-list-of-supplemental-group-ids-associated-with-the-current-process', '<p>import os</p><p>print(\"\\nEffective group id: \",os.getegid())</p><p>print(\"Effective user id: \",os.geteuid())</p><p>print(\"Real group id: \",os.getgid())</p><p>print(\"List of supplemental group ids: \",os.getgroups())</p><p>print()</p><div><br></div>', NULL, NULL, '2023-03-19 16:03:33', '2023-03-19 16:03:33'),
