(355, 'Write a Python program to shuffle the following elements randomly.', 'write-a-python-program-to-shuffle-the-following-elements-randomly', '<p>import random</p><p><br></p><p>nums = [1, 2, 3, 4, 5, 6, 7]</p><p>random.shuffle(nums)</p><p>print(nums)</p><div><br></div>', NULL, NULL, '2023-04-03 22:41:11', '2023-04-03 22:41:11'),
(356, 'Write a Python program to flip a coin 1000 times and count heads and tails.', 'write-a-python-program-to-flip-a-coin-1000-times-and-count-heads-and-tails', '<p>import random</p><p>import itertools</p><p><br></p><p>results = {</p><p>&nbsp; &nbsp; \'heads\': 0,</p><p>&nbsp; &nbsp; \'tails\': 0,</p><p>}</p><p>sides = list(results.keys())</p><p><br></p><p>for i in range(10000):</p><p>&nbsp; &nbsp; results[random.choice(sides)] += 1</p><p><br></p><p>print(\'Heads:\', results[\'heads\'])</p><p>print(\'Tails:\', results[\'tails\'])</p><div><br></div>', NULL, NULL, '2023-04-03 22:41:59', '2023-04-03 22:41:59'),
(357, 'Write a Python program to print a random sample of words from the system dictionary.', 'write-a-python-program-to-print-a-random-sample-of-words-from-the-system-dictionary', '<p>import random</p><p>with open(\'/usr/share/dict/words\', \'rt\') as fh:</p><p>&nbsp; &nbsp; words = fh.readlines()</p><p>words = [w.rstrip() for w in words]</p><p>for w in random.sample(words, 7):</p><p>&nbsp; &nbsp; print(w)</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-03 22:42:48', '2023-04-03 22:42:48'),
(358, 'Write a Python program to randomly select an item from a list.', 'write-a-python-program-to-randomly-select-an-item-from-a-list', '<p>import random</p><p>color = [\'Red\', \'Green\', \'Black\', \'Orange\', \'Black\']</p><p>print(random.choice(color))</p><div><br></div>', NULL, NULL, '2023-04-03 22:43:35', '2023-04-03 22:43:35'),
(359, 'Write a Python program to calculate the absolute value of a floating point number.', 'write-a-python-program-to-calculate-the-absolute-value-of-a-floating-point-number', '<p>import math</p><p><br></p><p>print(math.fabs(-2.1))</p><p>print(math.fabs(-0.0))</p><p>print(math.fabs(10.1))</p><p>print(math.fabs(0.0))</p><div><br></div>', NULL, NULL, '2023-04-03 22:44:15', '2023-04-03 22:44:15'),
(360, 'Write a Python program to calculate the standard deviation of the following data.', 'write-a-python-program-to-calculate-the-standard-deviation-of-the-following-data', '<p>import math</p><p>import sys</p><p><br></p><p>def sd_calc(data):</p><p>&nbsp; &nbsp; n = len(data)</p><p><br></p><p>&nbsp; &nbsp; if n &lt;= 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return 0.0</p><p><br></p><p>&nbsp; &nbsp; mean, sd = avg_calc(data), 0.0</p><p><br></p><p>&nbsp; &nbsp; # calculate stan. dev.</p><p>&nbsp; &nbsp; for el in data:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; sd += (float(el) - mean)**2</p><p>&nbsp; &nbsp; sd = math.sqrt(sd / float(n-1))</p><p><br></p><p>&nbsp; &nbsp; return sd</p><p><br></p><p>def avg_calc(ls):</p><p>&nbsp; &nbsp; n, mean = len(ls), 0.0</p><p><br></p><p>&nbsp; &nbsp; if n &lt;= 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return ls[0]</p><p><br></p><p>&nbsp; &nbsp; # calculate average</p><p>&nbsp; &nbsp; for el in ls:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; mean = mean + float(el)</p><p>&nbsp; &nbsp; mean = mean / float(n)</p><p><br></p><p>&nbsp; &nbsp; return mean</p><p><br></p><p>data = [4, 2, 5, 8, 6]</p><p>print(\"Sample Data: \",data)</p><p>print(\"Standard Deviation : \",sd_calc(data))</p><div><br></div>', NULL, NULL, '2023-04-03 22:44:50', '2023-04-03 22:44:50'),
(361, 'Write a Python program to print the floating point of the mantissa, exponent pair.', 'write-a-python-program-to-print-the-floating-point-of-the-mantissa-exponent-pair', '<p>import math</p><p><br></p><p>print(\'{:^7}&nbsp; {:^8}&nbsp; {:^17}\'.format(\'Mantissa\', \'Exponent\', \'Floating point value\'))</p><p>print(\'{:-^8}&nbsp; {:-^8}&nbsp; {:-^20}\'.format(\'\', \'\', \'\'))</p><p><br></p><p>for m, e in [ (0.7, -3),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (0.3,&nbsp; 0),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (0.5,&nbsp; 3),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ]:</p><p>&nbsp; &nbsp; x = math.ldexp(m, e)</p><p>&nbsp; &nbsp; print(\'{:7.2f}&nbsp; {:7d}&nbsp; {:7.2f}\'.format(m, e, x))</p><p><br></p>', NULL, NULL, '2023-04-03 22:45:26', '2023-04-03 22:45:26'),
(362, 'Write a Python program to split the fractional and integer parts of a floating point number.', 'write-a-python-program-to-split-the-fractional-and-integer-parts-of-a-floating-point-number', '<p>import math</p><p>print(\'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(F)&nbsp; (I)\')</p><p>for i in range(6):</p><p>&nbsp; &nbsp; print(\'{}/2 = {} {}\'.format(i, i/2, math.modf(i/2.0)))</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-03 22:46:14', '2023-04-03 22:46:14'),
(363, 'Write a Python program to parse math formulas and put parentheses around multiplication and division.', 'write-a-python-program-to-parse-math-formulas-and-put-parentheses-around-multiplication-and-division', '<p>#https://gist.github.com/ohaz/ed0b14a487b0569aad2d</p><p>import ast</p><p><br></p><p>def recurse(node):</p><p>&nbsp; &nbsp; if isinstance(node, ast.BinOp):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(node.op, ast.Mult) or isinstance(node.op, ast.Div):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; print(\'(\', end=\'\')</p><p>&nbsp; &nbsp; &nbsp; &nbsp; recurse(node.left)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; recurse(node.op)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; recurse(node.right)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(node.op, ast.Mult) or isinstance(node.op, ast.Div):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; print(\')\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Add):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'+\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Sub):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'-\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Mult):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'*\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Div):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'/\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Num):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(node.n, end=\'\')</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; for child in ast.iter_child_nodes(node):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; recurse(child)</p><p><br></p><p><br></p><p>def search_expr(node):</p><p>&nbsp; &nbsp; returns = []</p><p>&nbsp; &nbsp; for child in ast.iter_child_nodes(node):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(child, ast.Expr):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return child</p><p>&nbsp; &nbsp; &nbsp; &nbsp; returns.append(search_expr(child))</p><p>&nbsp; &nbsp; for ret in returns:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(ret, ast.Expr):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return ret</p><p>&nbsp; &nbsp; return None</p><p><br></p><p><br></p><p>formula = \'4+5*7/2\'</p><p><br></p><p>a = ast.parse(formula)</p><p><br></p><p>expr = search_expr(a)</p><p>if expr is not None:</p><p>&nbsp; &nbsp; recurse(expr)</p><p>print()</p><div><br></div>', NULL, NULL, '2023-04-03 22:46:59', '2023-04-03 22:46:59'),
(364, 'Write a Python program to describe linear regression.', 'write-a-python-program-to-describe-linear-regression', '<p>#https://gist.github.com/cartr/6513044</p><p># Define the data</p><p>data = set()</p><p>count = int(input(\"Enter the number of data points: \"))</p><p>for i in range(count):</p><p>&nbsp; &nbsp; x=float(input(\"X\"+str(i+1)+\": \"))</p><p>&nbsp; &nbsp; y=float(input(\"Y\"+str(i+1)+\": \"))</p><p>&nbsp; &nbsp; data.add((x,y))</p><p><br></p><p># Find the average x and y</p><p>avgx = 0.0</p><p>avgy = 0.0</p><p>for i in data:</p><p>&nbsp; &nbsp; avgx += i[0]/len(data)</p><p>&nbsp; &nbsp; avgy += i[1]/len(data)</p><p><br></p><p># Find the sums</p><p>totalxx = 0</p><p>totalxy = 0</p><p><br></p><p>for i in data:</p><p>&nbsp; &nbsp; totalxx += (i[0]-avgx)**2</p><p>&nbsp; &nbsp; totalxy += (i[0]-avgx)*(i[1]-avgy)</p><p><br></p><p># Slope/intercept form</p><p>m = totalxy/totalxx</p><p>b = avgy-m*avgx</p><p><br></p><p>print(\"Best fit line:\")</p><p>print(\"y = \"+str(m)+\"x + \"+str(b))</p><p><br></p><p>x = float(input(\"Enter a value to calculate: \"))</p><p>print(\"y = \"+str(m*x+b))</p><div><br></div>', NULL, NULL, '2023-04-03 22:48:04', '2023-04-03 22:48:04'),
(365, 'Write a Python program to calculate a grid of hexagon coordinates of the given radius given lower-left and upper-right coordinates. The function will return a list of lists containing 6 tuples of x, y point coordinates.', 'write-a-python-program-to-calculate-a-grid-of-hexagon-coordinates-of-the-given-radius-given-lower-left-and-upper-right-coordinates-the-function-will-return-a-list-of-lists-containing-6-tuples-of-x-y-point-coordinates', '<p>#https://gist.github.com/urschrei/17cf0be92ca90a244a91</p><p>import math</p><p>def calculate_polygons(startx, starty, endx, endy, radius):</p><p>&nbsp; &nbsp; &nbsp;# calculate side length given radius&nbsp; &nbsp;</p><p>&nbsp; &nbsp; sl = (2 * radius) * math.tan(math.pi / 6)</p><p>&nbsp; &nbsp; # calculate radius for a given side-length</p><p>&nbsp; &nbsp; # (a * (math.cos(math.pi / 6) / math.sin(math.pi / 6)) / 2)</p><p>&nbsp; &nbsp; # see http://www.calculatorsoup.com/calculators/geometry-plane/polygon.php</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; # calculate coordinates of the hexagon points</p><p>&nbsp; &nbsp; # sin(30)<span style=\"white-space:pre\">	</span></p><p>&nbsp; &nbsp; p = sl * 0.5</p><p>&nbsp; &nbsp; b = sl * math.cos(math.radians(30))</p><p>&nbsp; &nbsp; w = b * 2</p><p>&nbsp; &nbsp; h = 2 * sl</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; # offset start and end coordinates by hex widths and heights to guarantee coverage&nbsp; &nbsp; &nbsp;</p><p>&nbsp; &nbsp; startx = startx - w</p><p>&nbsp; &nbsp; starty = starty - h</p><p>&nbsp; &nbsp; endx = endx + w</p><p>&nbsp; &nbsp; endy = endy + h</p><p><br></p><p>&nbsp; &nbsp; origx = startx</p><p>&nbsp; &nbsp; origy = starty</p><p><br></p><p><br></p><p>&nbsp; &nbsp; # offsets for moving along and up rows</p><p>&nbsp; &nbsp; xoffset = b</p><p>&nbsp; &nbsp; yoffset = 3 * p</p><p><br></p><p>&nbsp; &nbsp; polygons = []</p><p>&nbsp; &nbsp; row = 1</p><p>&nbsp; &nbsp; counter = 0</p><p><br></p><p>&nbsp; &nbsp; while starty &lt; endy:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if row % 2 == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startx = origx + xoffset</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startx = origx</p><p>&nbsp; &nbsp; &nbsp; &nbsp; while startx &lt; endx:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p1x = startx</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p1y = starty + p</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p2x = startx</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p2y = starty + (3 * p)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p3x = startx + b</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p3y = starty + h</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p4x = startx + w</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p4y = starty + (3 * p)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p5x = startx + w</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p5y = starty + p</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p6x = startx + b</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p6y = starty</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; poly = [</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p1x, p1y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p2x, p2y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p3x, p3y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p4x, p4y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p5x, p5y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p6x, p6y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p1x, p1y)]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; polygons.append(poly)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; counter += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startx += w</p><p>&nbsp; &nbsp; &nbsp; &nbsp; starty += yoffset</p><p>&nbsp; &nbsp; &nbsp; &nbsp; row += 1</p><p>&nbsp; &nbsp; return polygons</p><p><br></p><p>print(calculate_polygons(1,1, 4, 4, 3))</p><div><br></div>', NULL, NULL, '2023-04-03 22:50:03', '2023-04-03 22:50:03'),
(366, 'Write a Python program to create a simple math quiz.', 'write-a-python-program-to-create-a-simple-math-quiz', '<div>import random</div><div><br></div><div><br></div><div>def display_intro():</div><div>&nbsp; &nbsp; title = \"** A Simple Math Quiz **\"</div><div>&nbsp; &nbsp; print(\"*\" * len(title))</div><div>&nbsp; &nbsp; print(title)</div><div>&nbsp; &nbsp; print(\"*\" * len(title))</div><div><br></div><div><br></div><div>def display_menu():</div><div>&nbsp; &nbsp; menu_list = [\"1. Addition\", \"2. Subtraction\", \"3. Multiplication\", \"4. Integer Division\", \"5. Exit\"]</div><div>&nbsp; &nbsp; print(menu_list[0])</div><div>&nbsp; &nbsp; print(menu_list[1])</div><div>&nbsp; &nbsp; print(menu_list[2])</div><div>&nbsp; &nbsp; print(menu_list[3])</div><div>&nbsp; &nbsp; print(menu_list[4])</div><div><br></div><div><br></div><div>def display_separator():</div><div>&nbsp; &nbsp; print(\"-\" * 24)</div><div><br></div><div><br></div><div>def get_user_input():</div><div>&nbsp; &nbsp; user_input = int(input(\"Enter your choice: \"))</div><div>&nbsp; &nbsp; while user_input &gt; 5 or user_input &lt;= 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; print(\"Invalid menu option.\")</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_input = int(input(\"Please try again: \"))</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return user_input</div><div><br></div><div><br></div><div>def get_user_solution(problem):</div><div>&nbsp; &nbsp; print(\"Enter your answer\")</div><div>&nbsp; &nbsp; print(problem, end=\"\")</div><div>&nbsp; &nbsp; result = int(input(\" = \"))</div><div>&nbsp; &nbsp; return result</div><div><br></div><div><br></div><div>def check_solution(user_solution, solution, count):</div><div>&nbsp; &nbsp; if user_solution == solution:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = count + 1</div><div>&nbsp; &nbsp; &nbsp; &nbsp; print(\"Correct.\")</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; print(\"Incorrect.\")</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div><br></div><div><br></div><div>def menu_option(index, count):</div><div>&nbsp; &nbsp; number_one = random.randrange(1, 21)</div><div>&nbsp; &nbsp; number_two = random.randrange(1, 21)</div><div>&nbsp; &nbsp; if index is 1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" + \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one + number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; elif index is 2:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" - \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one - number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; elif index is 3:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" * \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one * number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" // \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one // number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div><br></div><div><br></div><div>def display_result(total, correct):</div><div>&nbsp; &nbsp; if total &gt; 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; result = correct / total</div><div>&nbsp; &nbsp; &nbsp; &nbsp; percentage = round((result * 100), 2)</div><div>&nbsp; &nbsp; if total == 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; percentage = 0</div><div>&nbsp; &nbsp; print(\"You answered\", total, \"questions with\", correct, \"correct.\")</div><div>&nbsp; &nbsp; print(\"Your score is \", percentage, \"%. Thank you.\", sep = \"\")</div><div><br></div><div><br></div><div>def main():</div><div>&nbsp; &nbsp; display_intro()</div><div>&nbsp; &nbsp; display_menu()</div><div>&nbsp; &nbsp; display_separator()</div><div><br></div><div>&nbsp; &nbsp; option = get_user_input()</div><div>&nbsp; &nbsp; total = 0</div><div>&nbsp; &nbsp; correct = 0</div><div>&nbsp; &nbsp; while option != 5:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; total = total + 1</div><div>&nbsp; &nbsp; &nbsp; &nbsp; correct = menu_option(option, correct)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; option = get_user_input()</div><div><br></div><div>&nbsp; &nbsp; print(\"Exit the quiz.\")</div><div>&nbsp; &nbsp; display_separator()</div><div>&nbsp; &nbsp; display_result(total, correct)</div><div><br></div><div>main()</div><div><br></div>', NULL, NULL, '2023-04-05 19:53:19', '2023-04-06 01:14:39'),
(367, 'Write a Python program to calculate the volume of a tetrahedron.', 'write-a-python-program-to-calculate-the-volume-of-a-tetrahedron', '<p>import math</p><p>def volume_tetrahedron(num):</p><p><span style=\"white-space:pre\">	</span>volume = (num ** 3 / (6 * math.sqrt(2)))</p><p><span style=\"white-space:pre\">	</span></p><p><span style=\"white-space:pre\">	</span>return round(volume, 2)</p><p><br></p><p>print(volume_tetrahedron(10))</p><div><br></div>', NULL, NULL, '2023-04-05 20:00:11', '2023-04-05 20:00:11'),
(368, 'Write a Python program to compute the value of e(2.718281827...) using an infinite series.', 'write-a-python-program-to-compute-the-value-of-e2718281827-using-an-infinite-series', '<p>\'\'\'&nbsp;</p><p>#https://gist.github.com/thinkphp/1528363</p><p><br></p><p>&nbsp; 1 + 1/1! + 1/2! + 1/3! + ...</p><p>&nbsp; 2 + 1/2! + 1/3!+ ...</p><p>\'\'\'</p><p>import math</p><p><br></p><p>def fact(n):</p><p>&nbsp; &nbsp; if n == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp;return 1</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp;return n*fact(n-1)</p><p><br></p><p>def e(EPS):</p><p><br></p><p>&nbsp; &nbsp; v1 = 2</p><p>&nbsp; &nbsp; v2 = v1 + float(1.0/fact(2))</p><p>&nbsp; &nbsp; i = 3&nbsp;</p><p>&nbsp; &nbsp; while math.fabs(v1-v2) &gt;= EPS:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; v1 = v2</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; v2 += float(1.0/fact(i))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i += 1</p><p>&nbsp; &nbsp; return v2&nbsp;&nbsp;</p><p><br></p><p>print(\"The mathematical constant e\")</p><p>#computes the value of e using infinite series</p><p>print(e(0.00000001))</p><p>#mathematical constant e build-in</p><p>print(math.e)</p><div><br></div>', NULL, NULL, '2023-04-05 21:26:14', '2023-04-05 21:26:14'),
(369, 'Write a Python program to create an ASCII waveform.', 'write-a-python-program-to-create-an-ascii-waveform', '<p>from time import sleep</p><p>from math import sin, cos, radians</p><p><br></p><p># increase 40 to get more wave</p><p>for n in range(1, 40):</p><p><span style=\"white-space:pre\">	</span>circle_1 = 50 * (1 + sin(radians(n*10)))</p><p><span style=\"white-space:pre\">	</span>circle_2 = 50 * (1 + cos(radians(n*10)))</p><p><span style=\"white-space:pre\">	</span>print(\"#\".center(int(circle_1)))</p><p><span style=\"white-space:pre\">	</span>print(\"*\".center(int(circle_2)))</p><p><span style=\"white-space:pre\">	</span>sleep(0.05)</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-05 21:28:23', '2023-04-05 21:28:23'),
(370, 'Write a Python program to create a dot string.', 'write-a-python-program-to-create-a-dot-string', '<p>from math import sin, cos, radians</p><p>import sys</p><p>for i in range(1000):</p><p>&nbsp; &nbsp; print(\' \'*int(10*cos(radians(i))+10) + \'.\')</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-05 21:30:15', '2023-04-05 21:30:15'),
(371, 'Write a Python program to create a Pythagorean theorem calculator.', 'write-a-python-program-to-create-a-pythagorean-theorem-calculator', '<p>from math import sqrt</p><p><br></p><p>print(\'Pythagorean theorem calculator! Calculate your triangle sides.\')</p><p>print(\'Assume the sides are a, b, c and c is the hypotenuse (the side opposite the right angle\')</p><p>formula = input(\'Which side (a, b, c) do you wish to calculate? side&gt; \')</p><p><br></p><p>if formula == \'c\':</p><p><span style=\"white-space:pre\">	</span>side_a = int(input(\'Input the length of side a: \'))</p><p><span style=\"white-space:pre\">	</span>side_b = int(input(\'Input the length of side b: \'))</p><p><br></p><p><span style=\"white-space:pre\">	</span>side_c = sqrt(side_a * side_a + side_b * side_b)</p><p><span style=\"white-space:pre\">	</span></p><p><span style=\"white-space:pre\">	</span>print(\'The length of side c is: \' )</p><p><span style=\"white-space:pre\">	</span>print(side_c)</p><p><br></p><p>elif formula == \'a\':</p><p>&nbsp; &nbsp; side_b = int(input(\'Input the length of side b: \'))</p><p>&nbsp; &nbsp; side_c = int(input(\'Input the length of side c: \'))</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; side_a = sqrt((side_c * side_c) - (side_b * side_b))</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\'The length of side a is\' )</p><p>&nbsp; &nbsp; print(side_a)</p><p><br></p><p>elif formula == \'b\':</p><p>&nbsp; &nbsp; side_a = int(input(\'Input the length of side a: \'))</p><p>&nbsp; &nbsp; side_b = int(input(\'Input the length of side c: \'))</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; side_c = sqrt(side_c * side_c - side_a * side_a)</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\'The length of side b is\')</p><p>&nbsp; &nbsp; print(side_c)</p><p><br></p><p>else:</p><p><span style=\"white-space:pre\">	</span>print(\'Please select a side between a, b, c\')</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-05 21:31:30', '2023-04-05 21:31:30'),
(372, 'Write a Python function to round up a number to specified digits.', 'write-a-python-function-to-round-up-a-number-to-specified-digits', '<p>import math</p><p><br></p><p>def roundup(a, digits=0):</p><p>&nbsp; &nbsp; n = 10**-digits</p><p>&nbsp; &nbsp; return round(math.ceil(a / n) * n, digits)</p><p><br></p><p>x = 123.01247</p><p>print(\"Original&nbsp; Number: \",x)</p><p>print(roundup(x, 0))</p><p>print(roundup(x, 1))</p><p>print(roundup(x, 2))</p><p>print(roundup(x, 3))</p><div><br></div>', NULL, NULL, '2023-04-05 21:32:26', '2023-04-05 21:32:26'),
(373, 'Write a Python program for a casino simulation.', 'write-a-python-program-for-a-casino-simulation', '<p>#https://gist.github.com/ivanGzz/243a453ba6499a49027c19cac60471f6</p><p>import random</p><p>import math</p><p><br></p><p>limit = 1000</p><p>acc = 0</p><p>results = []</p><p>exp = 1000</p><p><br></p><p><br></p><p>for i in range(exp):</p><p>&nbsp; color = 0</p><p>&nbsp; amount = 10000</p><p>&nbsp; max_amount = amount</p><p>&nbsp; bid = 1</p><p>&nbsp; count = 0</p><p>&nbsp; while count &lt; limit and amount &gt; 0 :</p><p>&nbsp; &nbsp; amount = amount - bid</p><p>&nbsp; &nbsp; next = random.randint(0, 1)</p><p>&nbsp; &nbsp; if next == color :</p><p>&nbsp; &nbsp; &nbsp; amount = amount + bid + bid</p><p>&nbsp; &nbsp; &nbsp; bid = 1</p><p>&nbsp; &nbsp; &nbsp; # color = 1 if color == 0 else 0</p><p>&nbsp; &nbsp; &nbsp; if amount &gt; max_amount:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; max_amount = amount</p><p>&nbsp; &nbsp; else :</p><p>&nbsp; &nbsp; &nbsp; bid = bid + bid</p><p>&nbsp; &nbsp; count = count + 1</p><p>&nbsp; acc = acc + max_amount</p><p>&nbsp; results.append(max_amount)</p><p>&nbsp; print(\"Exp {}\".format(i))</p><p><br></p><p>avg = acc / exp</p><p>acc = 0</p><p>for i in range(len(results)):</p><p>&nbsp; acc = acc + math.pow(results[i] - avg, 2)</p><p>std = math.sqrt(acc / exp)</p><p><br></p><p>print(\"Average max amount earned {} with standard deviation {}\".format(avg, std))</p><div><br></div>', NULL, NULL, '2023-04-05 21:33:12', '2023-04-05 21:33:12'),
(374, 'Write a Python program to reverse a range.', 'write-a-python-program-to-reverse-a-range', '<p>#https://gist.github.com/fcicq/ddb746042150b4e959e6</p><p>def reversed_range(start, stop=None, step=1):</p><p>&nbsp; if stop is None:<span style=\"white-space:pre\">	</span></p><p>&nbsp; &nbsp; return range(start - step, -step, -step)</p><p>&nbsp; else:</p><p>&nbsp; &nbsp; new_start = stop - ((stop-start-1) % step + 1)</p><p>&nbsp; &nbsp; new_end = new_start - (stop-start+step-1) // step * step</p><p>&nbsp; &nbsp; if (stop - start) % step == 0 and step &lt; 0: new_start -= step</p><p>&nbsp; &nbsp; return range(new_start, new_end, -step)</p><p><br></p><p>print(reversed_range(1, 10, 2))</p><p><br></p><p>print(reversed_range(1, 5, 1))</p><div><br></div>', NULL, NULL, '2023-04-05 21:34:18', '2023-04-05 21:34:18'),
(375, 'Write a Python program to create a range for floating numbers.', 'write-a-python-program-to-create-a-range-for-floating-numbers', '<p>#https://gist.github.com/axelpale/3e780ebdde4d99cbb69ffe8b1eada92c</p><p>def frange(x, y, jump=1.0):</p><p>&nbsp; &nbsp; i = 0.0</p><p>&nbsp; &nbsp; x = float(x)&nbsp; # Prevent yielding integers.</p><p>&nbsp; &nbsp; y = float(y)&nbsp; # Comparison converts y to float every time otherwise.</p><p>&nbsp; &nbsp; x0 = x</p><p>&nbsp; &nbsp; epsilon = jump / 2.0</p><p>&nbsp; &nbsp; yield x&nbsp; # yield always first value</p><p>&nbsp; &nbsp; while x + epsilon &lt; y:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; i += 1.0</p><p>&nbsp; &nbsp; &nbsp; &nbsp; x = x0 + i * jump</p><p>&nbsp; &nbsp; &nbsp; &nbsp; yield x</p><p><br></p><p>print(list(frange(0.0, 1.0, 0.1)))</p><div><br></div>', NULL, NULL, '2023-04-05 21:34:54', '2023-04-05 21:34:54'),
(376, 'Write a Python program to generate (given an integer n) a square matrix filled with elements from 1 to n raised to the power of 2 in spiral order.', 'write-a-python-program-to-generate-given-an-integer-n-a-square-matrix-filled-with-elements-from-1-to-n-raised-to-the-power-of-2-in-spiral-order', '<p>#https://gist.github.com/Ray1988/6806c1b85af70388a277</p><p>def generateMatrix(n):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if n&lt;=0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return []&nbsp;</p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; matrix=[row[:] for row in [[0]*n]*n]</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; row_st=0</p><p>&nbsp; &nbsp; &nbsp; &nbsp; row_ed=n-1</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; col_st=0</p><p>&nbsp; &nbsp; &nbsp; &nbsp; col_ed=n-1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; current=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; while (True):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if current&gt;n*n:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for c in range (col_st, col_ed+1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; matrix[row_st][c]=current</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; current+=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; row_st+=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for r in range (row_st, row_ed+1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; matrix[r][col_ed]=current</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; current+=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; col_ed-=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for c in range (col_ed, col_st-1, -1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; matrix[row_ed][c]=current</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; current+=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; row_ed-=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for r in range (row_ed, row_st-1, -1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; matrix[r][col_st]=current</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; current+=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; col_st+=1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return matrix</p><p><br></p><p>print(list(generateMatrix(3)))</p><div><br></div>', NULL, NULL, '2023-04-05 21:36:00', '2023-04-05 21:36:00'),
(377, 'Write a Python program to select a random date in the current year.', 'write-a-python-program-to-select-a-random-date-in-the-current-year', '<p>from datetime import date</p><p>import random</p><p><br></p><p>start_dt = date.today().replace(day=1, month=1).toordinal()</p><p>end_dt = date.today().toordinal()</p><p>random_day = date.fromordinal(random.randint(start_dt, end_dt))</p><p>print(random_day)</p><div><br></div>', NULL, NULL, '2023-04-05 21:37:46', '2023-04-05 21:37:46'),
(378, 'Write a Python program to calculate clusters using the Hierarchical Clustering method.', 'write-a-python-program-to-calculate-clusters-using-the-hierarchical-clustering-method', '<p>#https://gist.github.com/vineetrok/1391954</p><p><br></p><p>import math</p><p><br></p><p>def distance(a,b):</p><p>&nbsp; &nbsp; x=float(a[0])-float(b[0])</p><p>&nbsp; &nbsp; x=x*x</p><p>&nbsp; &nbsp; y=float(a[1])-float(b[1])</p><p>&nbsp; &nbsp; y=y*y</p><p>&nbsp; &nbsp; dist=round(math.sqrt(x+y),2)</p><p>&nbsp; &nbsp; return dist</p><p><br></p><p>def minimum(matrix):</p><p>&nbsp; &nbsp; p=[0,0]</p><p>&nbsp; &nbsp; mn=1000</p><p>&nbsp; &nbsp; for i in range(0,len(matrix)):&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; for j in range(0,len(matrix[i])):&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (matrix[i][j]&gt;0 and matrix[i][j]&lt;mn):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mn=matrix[i][j]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p[0]=i</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p[1]=j</p><p>&nbsp; &nbsp; return p&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>def newpoint(pt):</p><p>&nbsp; &nbsp; x=float(pt[0][0])+float(pt[1][0])</p><p>&nbsp; &nbsp; x=x/2</p><p>&nbsp; &nbsp; y=float(pt[0][1])+float(pt[1][1])</p><p>&nbsp; &nbsp; y=y/2</p><p>&nbsp; &nbsp; midpoint=[x,y]</p><p>&nbsp; &nbsp; return midpoint</p><p><br></p><p>if __name__ == \'__main__\':&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; n=int(input(\"Input number of points.&gt; \"))</p><p>&nbsp; &nbsp; points=list()</p><p>&nbsp; &nbsp; outline=\'[\'</p><p>&nbsp; &nbsp; i=0</p><p><br></p><p>&nbsp; &nbsp; while(i&lt;n):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s=input(\"Input point (eg. 1,1)\"+chr(65+i)+\"&gt; \")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; c=s.split(\",\")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; points.append(c)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; i=i+1</p><p><br></p><p>&nbsp; &nbsp; names={}</p><p><br></p><p>&nbsp; &nbsp; for i in range(0,len(points)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; names[str(points[i])]=chr(65+i)</p><p>&nbsp; &nbsp; l=0</p><p>&nbsp; &nbsp; while(len(points)&gt;1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; l=l+1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; matrix=list()</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'Distance matrix no. \'+str(l)+\': \')</p><p>&nbsp; &nbsp; &nbsp; &nbsp; for i in range(0,len(points)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; m=[]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for j in range(0,len(points)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; m.append(0)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for j in range(0,len(points)):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; m[j]=distance(points[i],points[j])</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; print(str(m))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; matrix.append(m)</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; m=minimum(matrix)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; pts=list()</p><p>&nbsp; &nbsp; &nbsp; &nbsp; p1=points[m[0]]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; pts.append(p1)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; points.remove(p1)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; p2=points[m[1]-1]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; pts.append(p2)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; points.remove(p2)&nbsp; &nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; midpoint=newpoint(pts)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; points.append(midpoint)&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; c1=names.pop(str(p1))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; c2=names.pop(str(p2))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; names[str(midpoint)]=\"[\"+str(c1)+str(c2)+\"]\"&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; outline=names[str(midpoint)]</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\"Cluster is :\",names[str(midpoint)])</p><div><br></div>', NULL, NULL, '2023-04-05 21:38:56', '2023-04-05 21:38:56'),
(379, 'Write a Python program to implement the Euclidean Algorithm to compute the greatest common divisor (GCD).', 'write-a-python-program-to-implement-the-euclidean-algorithm-to-compute-the-greatest-common-divisor-gcd', '<p>from math import *</p><p><br></p><p>def euclid_algo(x, y, verbose=True):</p><p><span style=\"white-space:pre\">	</span>if x &lt; y: # We want x &gt;= y</p><p><span style=\"white-space:pre\">		</span>return euclid_algo(y, x, verbose)</p><p><span style=\"white-space:pre\">	</span>print()</p><p><span style=\"white-space:pre\">	</span>while y != 0:</p><p><span style=\"white-space:pre\">		</span>if verbose: print(\'%s = %s * %s + %s\' % (x, floor(x/y), y, x % y))</p><p><span style=\"white-space:pre\">		</span>(x, y) = (y, x % y)</p><p><span style=\"white-space:pre\">	</span></p><p><span style=\"white-space:pre\">	</span>if verbose: print(\'gcd is %s\' % x)&nbsp;</p><p><span style=\"white-space:pre\">	</span>return x</p><p><br></p><p><br></p><p>euclid_algo(150, 304)</p><p>euclid_algo(1000, 10)</p><p>euclid_algo(150, 9)</p><div><br></div>', NULL, NULL, '2023-04-05 21:41:01', '2023-04-05 21:41:01'),
(380, 'Write a Python program to convert RGB color to HSV color.', 'write-a-python-program-to-convert-rgb-color-to-hsv-color', '<p>def rgb_to_hsv(r, g, b):</p><p>&nbsp; &nbsp; r, g, b = r/255.0, g/255.0, b/255.0</p><p>&nbsp; &nbsp; mx = max(r, g, b)</p><p>&nbsp; &nbsp; mn = min(r, g, b)</p><p>&nbsp; &nbsp; df = mx-mn</p><p>&nbsp; &nbsp; if mx == mn:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; h = 0</p><p>&nbsp; &nbsp; elif mx == r:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; h = (60 * ((g-b)/df) + 360) % 360</p><p>&nbsp; &nbsp; elif mx == g:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; h = (60 * ((b-r)/df) + 120) % 360</p><p>&nbsp; &nbsp; elif mx == b:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; h = (60 * ((r-g)/df) + 240) % 360</p><p>&nbsp; &nbsp; if mx == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s = 0</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s = (df/mx)*100</p><p>&nbsp; &nbsp; v = mx*100</p><p>&nbsp; &nbsp; return h, s, v</p><p><br></p><p>print(rgb_to_hsv(255, 255, 255))</p><p>print(rgb_to_hsv(0, 215, 0))</p><div><br></div>', NULL, NULL, '2023-04-05 21:41:37', '2023-04-05 21:41:37'),
(381, 'Write a Python program to find perfect squares between two given numbers.', 'write-a-python-program-to-find-perfect-squares-between-two-given-numbers', '<p>def squares(a, b):</p><p>&nbsp; &nbsp; lists=[]</p><p>&nbsp; &nbsp; # Traverse through all numbers</p><p>&nbsp; &nbsp; for i in range (a,b+1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; j = 1;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; while j*j &lt;= i:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if j*j == i:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;lists.append(i)&nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; j = j+1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; i = i+1</p><p>&nbsp; &nbsp; return lists</p><p>print(squares(1, 30))</p><div><br></div>', NULL, NULL, '2023-04-05 21:42:09', '2023-04-05 21:42:09'),
(382, 'Write a Python program to compute Euclidean distances.', 'write-a-python-program-to-compute-euclidean-distances', '<p>import math</p><p># Example points in 3-dimensional space...</p><p>x = (5, 6, 7)</p><p>y = (8, 9, 9)</p><p>distance = math.sqrt(sum([(a - b) ** 2 for a, b in zip(x, y)]))</p><p>print(\"Euclidean distance from x to y: \",distance)</p><div><br></div>', NULL, NULL, '2023-04-05 21:42:44', '2023-04-05 21:42:44'),
(383, 'Write a Python program to convert an integer to a 2 byte Hex value.', 'write-a-python-program-to-convert-an-integer-to-a-2-byte-hex-value', '<p>for i in range(1, 10):</p><p>&nbsp; &nbsp; print(i,\'--&gt;\',format(i, \'#04x\'))</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-05 21:43:09', '2023-04-05 21:43:09'),
(384, 'Write a Python program to generate a series of distinct random numbers.', 'write-a-python-program-to-generate-a-series-of-distinct-random-numbers', '<p>import random</p><p>choices = list(range(100))</p><p>random.shuffle(choices)</p><p>print(choices.pop())</p><p>while choices:</p><p>&nbsp; &nbsp; if input(\'Want another random number?(Y/N)\' ).lower() == \'n\':</p><p>&nbsp; &nbsp; &nbsp; &nbsp; break</p><p>&nbsp; &nbsp; print(choices.pop())</p><p><span style=\"white-space:pre\">	</span></p>', NULL, NULL, '2023-04-05 21:58:44', '2023-04-05 21:58:44'),
(385, 'Write a Python program to convert a given float value to a ratio.', 'write-a-python-program-to-convert-a-given-float-value-to-a-ratio', '<p>from fractions import Fraction</p><p>value = 4.2</p><p>z = Fraction(value).limit_denominator()</p><p>print(z)</p><div><br></div>', NULL, NULL, '2023-04-05 21:59:07', '2023-04-05 21:59:07');
INSERT INTO `questions_questions` (`id`, `title`, `slug`, `description`, `solution`, `deleted_at`, `created_at`, `updated_at`) VALUES
(386, 'Write a Python program to calculate the aliquot sum of a given number.', 'write-a-python-program-to-calculate-the-aliquot-sum-of-a-given-number', '<p>def aliquot_sum(input_val):</p><p>&nbsp; &nbsp; &nbsp;if not isinstance(input_val, int):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return \"Input must be an integer\"</p><p>&nbsp; &nbsp; &nbsp;if input_val &lt;= 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return \"Input must be positive\"</p><p>&nbsp; &nbsp; &nbsp;return \"Aliquot Sum\",sum(</p><p>&nbsp; &nbsp; &nbsp; &nbsp; divisor for divisor in range(1, input_val // 2 + 1) if input_val % divisor == 0</p><p>&nbsp; &nbsp; )</p><p>N = 15</p><p>print(\"\\nInput value:\",N)</p><p>print(aliquot_sum(N))</p><p>N = 12</p><p>print(\"\\nInput value:\",N)</p><p>print(aliquot_sum(N))</p><p>N = -6</p><p>print(\"\\nInput value:\",N)</p><p>print(aliquot_sum(N))</p><p>N = 12.22</p><p>print(\"\\nInput value:\",N)</p><p>print(aliquot_sum(N))</p><p>N = \'zz\'</p><p>print(\"\\nInput value:\",N)</p><p>print(aliquot_sum(N))</p><div><br></div>', NULL, NULL, '2023-04-05 22:00:52', '2023-04-05 22:00:52'),
(387, 'Write a Python program to get the nth tetrahedral number from a given integer(n) value.', 'write-a-python-program-to-get-the-nth-tetrahedral-number-from-a-given-integern-value', '<p>def test(n):</p><p>&nbsp; &nbsp; return (n * (n + 1) * (n + 2)) / 6</p><p><br></p><p>n = 1</p><p>print(\"\\nOriginal Number:\",n)</p><p>print(\"Tetrahedral number:\",test(n))</p><p>n = 2</p><p>print(\"\\nOriginal Number:\",n)</p><p>print(\"Tetrahedral number:\",test(n))</p><p>n = 6</p><p>print(\"\\nOriginal Number:\",n)</p><p>print(\"Tetrahedral number:\",test(n))</p><div><br></div>', NULL, NULL, '2023-04-05 22:01:43', '2023-04-05 22:01:43'),
(388, 'Write a Python program to get the sum of the powers of all the numbers from start to end (both inclusive).', 'write-a-python-program-to-get-the-sum-of-the-powers-of-all-the-numbers-from-start-to-end-both-inclusive', '<p>def sum_of_powers(end, power = 2, start = 1):</p><p>&nbsp; return sum([(i) ** power for i in range(start, end + 1)])</p><p><br></p><p>print(sum_of_powers(12))</p><p>print(sum_of_powers(12, 3))</p><p>print(sum_of_powers(12, 5, 7))</p><div><br></div>', NULL, NULL, '2023-04-05 22:40:17', '2023-04-05 22:40:17'),
(389, 'Write a Python program to calculate the Hamming distance between two given values.', 'write-a-python-program-to-calculate-the-hamming-distance-between-two-given-values', '<p>def hamming_distance(x, y):</p><p>&nbsp; return bin(x ^ y).count(\'1\')</p><p>x = 2&nbsp;</p><p>y = 3</p><p>print(\"Hamming distance between\",x,\"and\",y,\"is\",hamming_distance(x, y))</p><p>x = 43&nbsp;</p><p>y = 87</p><p>print(\"\\nHamming distance between\",x,\"and\",y,\"is\",hamming_distance(x, y))</p><div><br></div>', NULL, NULL, '2023-04-05 22:40:55', '2023-04-05 22:40:55'),
(390, 'Write a Python program to cap a number within the inclusive range specified by the given boundary values x and y.', 'write-a-python-program-to-cap-a-number-within-the-inclusive-range-specified-by-the-given-boundary-values-x-and-y', '<p>def clamp_number(num, a, b):</p><p>&nbsp; return max(min(num, max(a, b)), min(a, b))</p><p>&nbsp;</p><p>print(clamp_number(2, 4, 7))</p><p>print(clamp_number(1, -1, -4))</p><p>print(clamp_number(22, -10, 10))</p><p>print(clamp_number(20, 1, 10))</p><p>print(clamp_number(5, 1, 20))</p><p>print(clamp_number(1, 10, 20))</p><div><br></div>', NULL, NULL, '2023-04-05 22:41:28', '2023-04-05 22:41:28'),
(391, 'Write a Python program to check whether a given number is a Disarium number or an unhappy number.', 'write-a-python-program-to-check-whether-a-given-number-is-a-disarium-number-or-an-unhappy-number', '<p>def is_disarium(num):</p><p>&nbsp; &nbsp; temp = 0</p><p>&nbsp; &nbsp; for i in range(len(str(num))):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; temp += int(str(num)[i]) ** (i + 1)</p><p>&nbsp; &nbsp; return temp == num</p><p><br></p><p>num = 25</p><p>print(\"\\nIs\",num,\"is Disarium number?\",is_disarium(num))</p><p>num = 89</p><p>print(\"\\nIs\",num,\"is Disarium number?\",is_disarium(num))</p><p>num = 75</p><p>print(\"\\nIs\",num,\"is Disarium number?\",is_disarium(num))</p><p>num = 125</p><p>print(\"\\nIs\",num,\"is Disarium number?\",is_disarium(num))&nbsp;</p><p>num = 518</p><p>print(\"\\nIs\",num,\"is Disarium number?\",is_disarium(num))</p><div><br></div>', NULL, NULL, '2023-04-05 22:43:32', '2023-04-05 22:43:32'),
(392, 'Write a Python program to check if a given number is a repdigit number or not. If the given number is repdigit return true otherwise false.', 'write-a-python-program-to-check-if-a-given-number-is-a-repdigit-number-or-not-if-the-given-number-is-repdigit-return-true-otherwise-false', '<p>def test(n):</p><p>&nbsp; &nbsp; if n &lt; 0: return False</p><p>&nbsp; &nbsp; if n == 0: return True</p><p>&nbsp; &nbsp; return len(set(str(n))) == 1</p><p>n = 0</p><p>print(\"Original number:\", n)</p><p>print(\"Check the said number is a repdigit number or not!\")</p><p>print(test(n))</p><p>n = -1111</p><p>print(\"Original number:\", n)</p><p>print(\"Check the said number is a repdigit number or not!\")</p><p>print(test(n))</p><p>n = 1</p><p>print(\"Original number:\", n)</p><p>print(\"Check the said number is a repdigit number or not!\")</p><p>print(test(n))</p><p>n = 9999999</p><p>print(\"Original number:\", n)</p><p>print(\"Check the said number is a repdigit number or not!\")</p><p>print(test(n))&nbsp;</p>', NULL, NULL, '2023-04-05 22:44:30', '2023-04-05 22:44:30'),
(393, 'Write a Python program to check if a given number is a Harshad number or not. Return True if the number is Harshad otherwise False.', 'write-a-python-program-to-check-if-a-given-number-is-a-harshad-number-or-not-return-true-if-the-number-is-harshad-otherwise-false', '<p>def test(n):</p><p>&nbsp; &nbsp; if (n&gt;0):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; a = 0</p><p>&nbsp; &nbsp; &nbsp; &nbsp; b = n</p><p>&nbsp; &nbsp; &nbsp; &nbsp; while b &gt; 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a = a +&nbsp; b % 10</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; b = b // 10</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return not n % a</p><p>&nbsp; &nbsp; &nbsp;</p><p>n = 666</p><p>print(\"Original number:\", n)</p><p>print(\"Check the said number is a Harshad number or not!\")</p><p>print(test(n))</p><p>n = 11</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Check the said number is a Harshad number or not!\")</p><p>print(test(n))</p><p>n = -144</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Check the said number is a Harshad number or not!\")</p><p>print(test(n))</p><p>n = 200</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Check the said number is a Harshad number or not!\")</p><p>print(test(n))</p><div><br></div>', NULL, NULL, '2023-04-05 22:47:11', '2023-04-05 22:47:11'),
(394, 'Write a Python program that accepts an integer number with distinct digits and displays the next number containing only distinct digits.', 'write-a-python-program-that-accepts-an-integer-number-with-distinct-digits-and-displays-the-next-number-containing-only-distinct-digits', '<p>def test(n):</p><p><span style=\"white-space:pre\">	</span>n += 1</p><p><span style=\"white-space:pre\">	</span>if len(set(str(n))) == len(str(n)):</p><p><span style=\"white-space:pre\">		</span>return n</p><p><span style=\"white-space:pre\">	</span>return test(n)</p><p>n = 12345</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><p>n = 99999</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><p>n = 1</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><p>n = 100</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><div><br></div>', NULL, NULL, '2023-04-05 22:52:09', '2023-04-05 22:52:09'),
(395, 'Write a Python program that checks whether the absolute difference between two consecutive digits is two or not. Return true otherwise false.', 'write-a-python-program-that-checks-whether-the-absolute-difference-between-two-consecutive-digits-is-two-or-not-return-true-otherwise-false', '<p>def test(n):</p><p>&nbsp; &nbsp; return all(abs(int(x) - int(y)) == 2 for x, y in zip(str(n), str(n)[1:]))</p><p>&nbsp; &nbsp; &nbsp;</p><p>n = 666</p><p>print(\"Original number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><p>n = 3579</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><p>n = 2468</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><p>n = 20420</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><div><br></div>', NULL, NULL, '2023-04-05 22:53:44', '2023-04-05 22:53:44'),
(396, 'Write a Python program that takes an integer and rearranges the digits to create two maximum and minimum numbers.', 'write-a-python-program-that-takes-an-integer-and-rearranges-the-digits-to-create-two-maximum-and-minimum-numbers', '<p>def test(n):</p><p>&nbsp; &nbsp; temp = []</p><p>&nbsp; &nbsp; for e in str(n):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; temp.append(e)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; max_num = \"\".join(sorted(temp)[::-1])</p><p>&nbsp; &nbsp; &nbsp; &nbsp; min_num = \"\".join(sorted(temp))&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; return int(max_num), int(min_num)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>n = 1254</p><p>print(\"Original number:\", n)</p><p>print(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")</p><p>print(\"Maximum and Minimum Numbers:\",test(n))</p><p>n = 6</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")</p><p>print(\"Maximum and Minimum Numbers:\",test(n))</p><p>n = 1000</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")</p><p>print(\"Maximum and Minimum Numbers:\",test(n))</p><div><br></div>', NULL, NULL, '2023-04-05 22:54:49', '2023-04-05 22:54:49'),
(397, 'Write a Python program to calculate the sum of all prime numbers in a given list of positive integers.', 'write-a-python-program-to-calculate-the-sum-of-all-prime-numbers-in-a-given-list-of-positive-integers', '<p>def test(nums):</p><p>&nbsp; &nbsp; if len(nums) &gt; 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return sum(list(filter(lambda x: (x &gt; 1 and all(x % y != 0 for y in range(2, x))), nums)))&nbsp;</p><p>&nbsp; &nbsp; return \"Empty list!\"</p><p><br></p><p>nums = [1, 3, 4, 7, 9]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"Sum of all prime numbers in the said list of numbers:\")</p><p>print(test(nums))</p><p>nums = []</p><p>print(\"\\nOriginal list:\")</p><p>print(nums)</p><p>print(\"Sum of all prime numbers in the said list of numbers:\")</p><p>print(test(nums))</p><p>nums = [11, 37, 444]</p><p>print(\"\\nOriginal list:\")</p><p>print(nums)</p><p>print(\"Sum of all prime numbers in the said list of numbers:\")</p><p>print(test(nums))</p><div><br></div>', NULL, NULL, '2023-04-05 22:56:29', '2023-04-05 22:56:29');
