(55, 'Write a Python program to determine the profiling of Python programs.', 'write-a-python-program-to-determine-the-profiling-of-python-programs', '<p>import cProfile</p><br><p>def sum():</p><p>&nbsp; &nbsp; print(1+2)</p><br><p>cProfile.run(\'sum()\')</p>', 'import cProfile\r\n\r\ndef sum():\r\n    print(1+2)\r\n\r\ncProfile.run(\'sum()\')', 4, 0, NULL, '2023-02-26 06:28:19', '2023-06-03 06:55:08', 1),
(56, 'Write a Python program to print to STDERR.', 'write-a-python-program-to-print-to-stderr', '<p>from __future__ import print_function</p><p>import sys</p><p><br></p><p>def eprint(*args, **kwargs):</p><p>&nbsp; &nbsp; print(*args, file=sys.stderr, **kwargs)</p><p><br></p><p>eprint(\"abc\", \"efg\", \"xyz\", sep=\"--\")</p><div><br></div>', 'from __future__ import print_function\n\nimport sys\n\n\n\ndef eprint(*args, **kwargs):\n\n    print(*args, file=sys.stderr, **kwargs)\n\n\n\neprint(\"abc\", \"efg\", \"xyz\", sep=\"--\")\n\n', 2, 0, NULL, '2023-02-26 06:30:03', '2023-06-03 06:56:23', 1),
(57, 'Write a Python program to access environment variables.', 'write-a-python-program-to-access-environment-variables', '<p>import os</p><br><p>for item, value in os.environ.items():</p><p>&nbsp; &nbsp;print(\'{}: {}\'.format(item, value))</p>', 'import os\r\n\r\nfor item, value in os.environ.items():\r\n   print(\'{}: {}\'.format(item, value))', 4, 0, NULL, '2023-02-26 06:32:26', '2023-06-03 07:00:43', 1),
(58, 'Write a Python program to get the current username.', 'write-a-python-program-to-get-the-current-username', '<p>import getpass</p><p>print(getpass.getuser())</p><div><br></div>', 'import getpass\n\nprint(getpass.getuser())', 2, 0, NULL, '2023-02-26 06:42:41', '2023-06-03 07:01:50', 1),
(59, 'Write a Python program to find local IP addresses using Python\'s stdlib.', 'write-a-python-program-to-find-local-ip-addresses-using-pythons-stdlib', '<p>import socket</p><br><p>print([l for l in ([ip for ip in socket.gethostbyname_ex(socket.gethostname())[2]&nbsp;</p><p>if not ip.startswith(\"127.\")][:1], [[(s.connect((\'8.8.8.8\', 53)),&nbsp;</p><p>s.getsockname()[0], s.close()) for s in [socket.socket(socket.AF_INET,&nbsp;</p><p>socket.SOCK_DGRAM)]][0][1]]) if l][0][0])</p>', 'import socket\r\n\r\nprint([l for l in ([ip for ip in socket.gethostbyname_ex(socket.gethostname())[2] \r\nif not ip.startswith(\"127.\")][:1], [[(s.connect((\'8.8.8.8\', 53)), s.getsockname()[0], s.close()) for s in [socket.socket(socket.AF_INET, \r\nsocket.SOCK_DGRAM)]][0][1]]) if l][0][0])', 2, 0, NULL, '2023-02-26 06:43:47', '2023-06-03 07:05:24', 1),
(60, 'Write a Python program to get the height and width of the console window.', 'write-a-python-program-to-get-the-height-and-width-of-the-console-window', '<p>def terminal_size():</p><p>&nbsp; &nbsp; import fcntl, termios, struct</p><p>&nbsp; &nbsp; th, tw, hp, wp = struct.unpack(\'HHHH\',</p><p>&nbsp; &nbsp; &nbsp; &nbsp; fcntl.ioctl(0, termios.TIOCGWINSZ,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; struct.pack(\'HHHH\', 0, 0, 0, 0)))</p><p>&nbsp; &nbsp; return tw, th</p><p><br></p><p>print(\'Number of columns and Rows: \',terminal_size())</p>', 'def terminal_size():\n\n    import fcntl, termios, struct\n\n    th, tw, hp, wp = struct.unpack(\'HHHH\',\n\n        fcntl.ioctl(0, termios.TIOCGWINSZ,\n\n        struct.pack(\'HHHH\', 0, 0, 0, 0)))\n\n    return tw, th\n\n\n\nprint(\'Number of columns and Rows: \',terminal_size())', 2, 0, NULL, '2023-02-26 06:44:30', '2023-06-03 07:05:43', 1),
(61, 'Write a Python program to get the execution time of a Python method.', 'write-a-python-program-to-get-the-execution-time-of-a-python-method', '<p>import time</p><br><p>def sum_of_n_numbers(n):</p><p>&nbsp; &nbsp; start_time = time.time()</p><p>&nbsp; &nbsp; s = 0</p><p>&nbsp; &nbsp; for i in range(1,n+1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; s = s + i</p><p>&nbsp; &nbsp; end_time = time.time()</p><p>&nbsp; &nbsp; return s,end_time-start_time</p><br><p>n = 5</p><p>print(\"\\nTime to sum of 1 to \",n,\" and required time to calculate is :\",sum_of_n_numbers(n))</p>', 'import time\r\n\r\ndef sum_of_n_numbers(n):\r\n    start_time = time.time()\r\n    s = 0\r\n    for i in range(1,n+1):\r\n        s = s + i\r\n    end_time = time.time()\r\n    return s,end_time-start_time\r\n\r\nn = 5\r\nprint(\"\\nTime to sum of 1 to \",n,\" and required time to calculate is :\",sum_of_n_numbers(n))', 2, 0, NULL, '2023-02-26 06:45:19', '2023-06-03 07:10:46', 1),
(62, 'Write a Python program to sum the first n positive integers.', 'write-a-python-program-to-sum-the-first-n-positive-integers', '<p>n = int(input(\"Input a number: \"))</p><br><p>sum_num = (n * (n + 1)) / 2</p><br><p>print(\"Sum of the first\", n ,\"positive integers:\", sum_num)</p>', '#Take a number for input\r\nn = int(input( ))\r\n\r\nsum_num = (n * (n + 1)) / 2\r\n\r\nprint(\"Sum of the first\", n ,\"positive integers:\", sum_num)', 4, 1, NULL, '2023-02-26 06:46:44', '2023-06-03 07:15:04', 1),
(63, 'Write a Python program to convert height (in feet and inches) to centimeters.', 'write-a-python-program-to-convert-height-in-feet-and-inches-to-centimeters', '<p>print(\"Input your height: \")</p><br><p>h_ft = int(input(\"Feet: \"))</p><p>h_inch = int(input(\"Inches: \"))</p><br><p>h_inch += h_ft * 12</p><p>h_cm = round(h_inch * 2.54, 1)</p><br><p>print(\"Your height is : %d cm.\" % h_cm)</p>', '#Input your height\r\n\r\n#In Feet\r\nh_ft = int(input( ))\r\n#In Inches\r\nh_inch = int(input( ))\r\n\r\nh_inch += h_ft * 12\r\nh_cm = round(h_inch * 2.54, 1)\r\n\r\nprint(\"Your height is : %d cm.\" % h_cm)', 4, 1, NULL, '2023-02-26 06:47:51', '2023-06-03 07:22:05', 1),
(64, 'Write a Python program to calculate the hypotenuse of a right angled triangle.', 'write-a-python-program-to-calculate-the-hypotenuse-of-a-right-angled-triangle', '<p>from math import sqrt</p><br><p>print(\"Input lengths of shorter triangle sides:\")</p><p>a = float(input(\"a: \"))</p><p>b = float(input(\"b: \"))</p><br><p>c = sqrt(a**2 + b**2)</p><br><p>print(\"The length of the hypotenuse is:\", c )</p>', 'from math import sqrt\r\n\r\n#Input lengths of shorter triangle sides\r\na = float(input( ))\r\nb = float(input( ))\r\n\r\nc = sqrt(a**2 + b**2)\r\n\r\nprint(\"The length of the hypotenuse is:\", c )', 0, 1, NULL, '2023-02-26 06:48:33', '2023-06-03 10:10:55', 1),
(65, 'Write a Python program to convert the distance (in feet) to inches, yards, and miles.', 'write-a-python-program-to-convert-the-distance-in-feet-to-inches-yards-and-miles', '<p>d_ft = int(input(\"Input distance in feet: \"))</p><p>d_inches = d_ft * 12</p><p>d_yards = d_ft / 3.0</p><p>d_miles = d_ft / 5280.0</p><br><p>print(\"The distance in inches is %i inches.\" % d_inches)</p><p>print(\"The distance in yards is %.2f yards.\" % d_yards)</p><p>print(\"The distance in miles is %.2f miles.\" % d_miles)</p>', '#Take input distance in feet\r\nd_ft = int(input( ))\r\n\r\nd_inches = d_ft * 12\r\n\r\nd_yards = d_ft / 3.0\r\n\r\nd_miles = d_ft / 5280.0\r\n\r\nprint(\"The distance in inches is %i inches.\" % d_inches)\r\nprint(\"The distance in yards is %.2f yards.\" % d_yards)\r\nprint(\"The distance in miles is %.2f miles.\" % d_miles)', 2, 1, NULL, '2023-02-26 06:51:58', '2023-06-03 10:15:12', 1),
(66, 'Write a Python program to convert all units of time into seconds.', 'write-a-python-program-to-convert-all-units-of-time-into-seconds', '<p>days = int(input(\"Input days: \")) * 3600 * 24</p><p>hours = int(input(\"Input hours: \")) * 3600</p><p>minutes = int(input(\"Input minutes: \")) * 60</p><p>seconds = int(input(\"Input seconds: \"))</p><br><p>time = days + hours + minutes + seconds</p><br><p>print(\"The&nbsp; amounts of seconds\", time)</p>', '#Input days\r\ndays = int(input( )) * 3600 * 24\r\n#Input hours\r\nhours = int(input( )) * 3600\r\n#Input minutes\r\nminutes = int(input( )) * 60\r\n#Input seconds\r\nseconds = int(input( ))\r\n\r\ntime = days + hours + minutes + seconds\r\n\r\nprint(\"The  amounts of seconds\", time)', 4, 1, NULL, '2023-02-26 06:53:30', '2023-06-03 10:23:33', 1),
(67, 'Write a Python program to get an absolute file path.', 'write-a-python-program-to-get-an-absolute-file-path', '<p>def absolute_file_path(path_fname):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; import os</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return os.path.abspath(\'path_fname\')&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>print(\"Absolute file path: \",absolute_file_path(\"test.txt\"))</p>', 'def absolute_file_path(path_fname):\n\n        import os\n\n        return os.path.abspath(\'path_fname\')        \n\nprint(\"Absolute file path: \",absolute_file_path(\"test.txt\"))', 2, 0, NULL, '2023-02-26 06:53:57', '2023-06-03 10:24:45', 1),
(68, 'Write a Python program that retrieves the date and time of file creation and modification.', 'write-a-python-program-that-retrieves-the-date-and-time-of-file-creation-and-modification', '<p>import os.path, time</p><p>print(\"Last modified: %s\" % time.ctime(os.path.getmtime(\"test.txt\")))</p><p>print(\"Created: %s\" % time.ctime(os.path.getctime(\"test.txt\")))</p>', 'import os.path, time\r\n\r\nprint(\"Last modified: %s\" % time.ctime(os.path.getmtime(\"manage.py\")))\r\n\r\nprint(\"Created: %s\" % time.ctime(os.path.getctime(\"manage.py\")))', 4, 0, NULL, '2023-02-26 06:55:15', '2023-06-03 10:32:17', 1),
(69, 'Write a Python program that converts seconds into days, hours, minutes, and seconds.', 'write-a-python-program-that-converts-seconds-into-days-hours-minutes-and-seconds', '<p>time = float(input(\"Input time in seconds: \"))</p><br><p>day = time // (24 * 3600)</p><p>time = time % (24 * 3600)</p><p>hour = time // 3600</p><p>time %= 3600</p><p>minutes = time // 60</p><p>time %= 60</p><br><p>seconds = time</p><br><p>print(\"d:h:m:s-&gt; %d:%d:%d:%d\" % (day, hour, minutes, seconds))</p>', '#Take Input time in seconds\r\ntime = float(input( ))\r\n\r\nday = time // (24 * 3600)\r\ntime = time % (24 * 3600)\r\nhour = time // 3600\r\ntime %= 3600\r\nminutes = time // 60\r\ntime %= 60\r\n\r\nseconds = time\r\n\r\nprint(\"d:h:m:s-> %d:%d:%d:%d\" % (day, hour, minutes, seconds))', 2, 1, NULL, '2023-02-26 06:56:10', '2023-06-03 10:37:48', 1),
(70, 'Write a Python program to calculate the body mass index.', 'write-a-python-program-to-calculate-the-body-mass-index', '<p>height = float(input(\"Input your height in Feet: \"))</p><p>weight = float(input(\"Input your weight in Kilogram: \"))</p><br><p>print(\"Your body mass index is: \", round(weight / (height * height), 2))</p>', '#Input your height in Feet\r\nheight = float(input( ))\r\n#Input your weight in Kilogram\r\nweight = float(input( ))\r\n\r\nprint(\"Your body mass index is: \", round(weight / (height * height), 2))', 4, 1, NULL, '2023-02-26 06:56:53', '2023-06-03 10:43:23', 1);
