(71, 'Write a Python program to convert pressure in kilopascals to pounds per square inch,a millimeter of mercury (mmHg) and atmosphere pressure.', 'write-a-python-program-to-convert-pressure-in-kilopascals-to-pounds-per-square-incha-millimeter-of-mercury-mmhg-and-atmosphere-pressure', '<p>kpa = float(input(\"Input pressure in in kilopascals&gt; \"))</p><br><p>psi = kpa / 6.89475729</p><p>mmhg = kpa * 760 / 101.325</p><p>atm = kpa / 101.325</p><br><p>print(\"The pressure in pounds per square inch: %.2f psi\"&nbsp; % (psi))</p><p>print(\"The pressure in millimeter of mercury: %.2f mmHg\" % (mmhg))</p><p>print(\"Atmosphere pressure: %.2f atm.\" % (atm))</p>', '#Take pressure in kilopascals\r\nkpa = float(input( ))\r\n\r\npsi = kpa / 6.89475729\r\n\r\nmmhg = kpa * 760 / 101.325\r\n\r\natm = kpa / 101.325\r\n\r\nprint(\"The pressure in pounds per square inch: %.2f psi\"  % (psi))\r\nprint(\"The pressure in millimeter of mercury: %.2f mmHg\" % (mmhg))\r\nprint(\"Atmosphere pressure: %.2f atm.\" % (atm))', 2, 1, NULL, '2023-02-26 06:57:19', '2023-06-03 10:48:10', 1),
(72, 'Write a Python program to calculate sum of digits of a number.', 'write-a-python-program-to-calculate-sum-of-digits-of-a-number', '<p>num = int(input(\"Input a four digit numbers: \"))</p><br><p>x&nbsp; = num //1000</p><p>x1 = (num - x*1000)//100</p><p>x2 = (num - x*1000 - x1*100)//10</p><p>x3 = num - x*1000 - x1*100 - x2*10</p><br><p>print(\"The sum of digits in the number is\", x+x1+x2+x3)</p>', '#Take a four digit numbers\r\nnum = int(input( ))\r\n\r\nx  = num //1000\r\n\r\nx1 = (num - x*1000)//100\r\n\r\nx2 = (num - x*1000 - x1*100)//10\r\n\r\nx3 = num - x*1000 - x1*100 - x2*10\r\n\r\nprint(\"The sum of digits in the number is\", x+x1+x2+x3)', 0, 1, NULL, '2023-02-26 06:58:04', '2023-06-03 10:51:13', 1),
(73, 'Write a Python program to sort three integers without using conditional statements and loops.', 'write-a-python-program-to-sort-three-integers-without-using-conditional-statements-and-loops', '<p>x = int(input(\"Input first number: \"))</p><p>y = int(input(\"Input second number: \"))</p><p>z = int(input(\"Input third number: \"))</p><br><p>a1 = min(x, y, z)</p><p>a3 = max(x, y, z)</p><p>a2 = (x + y + z) - a1 - a3</p><br><p>print(\"Numbers in sorted order: \", a1, a2, a3)</p>', '#Take the first number\r\nx = int(input( ))\r\n#Take the second number\r\ny = int(input( ))\r\n#Take the third number\r\nz = int(input( ))\r\n\r\na1 = min(x, y, z)\r\n\r\na3 = max(x, y, z)\r\n\r\na2 = (x + y + z) - a1 - a3\r\n\r\nprint(\"Numbers in sorted order: \", a1, a2, a3)', 6, 1, NULL, '2023-02-26 06:59:41', '2023-06-04 07:43:07', 1),
(74, 'Write a Python program to sort files by date.', 'write-a-python-program-to-sort-files-by-date', '<p>import glob</p><p>import os</p><br><p>files = glob.glob(\"*.py\")</p><p>files.sort(key=os.path.getmtime)</p><p>print(\"\\n\".join(files))</p>', 'import glob\r\n\r\nimport os\r\n\r\nfiles = glob.glob(\"*.py\")\r\n\r\nfiles.sort(key=os.path.getmtime)\r\n\r\nprint(\"\\n\".join(files))', 2, 0, NULL, '2023-02-26 07:00:25', '2023-06-03 10:59:44', 1),
(75, 'Write a Python program to get a directory listing, sorted by creation date.', 'write-a-python-program-to-get-a-directory-listing-sorted-by-creation-date', '<p>from stat import S_ISREG, ST_CTIME, ST_MODE</p><br><p>import os, sys, time</p><br><p>#Relative or absolute path to the directory</p><p>dir_path = sys.argv[1] if len(sys.argv) == 2 else r\'.\'</p><br><p>#all entries in the directory w/ stats</p><p>data = (os.path.join(dir_path, fn) for fn in os.listdir(dir_path))</p><p>data = ((os.stat(path), path) for path in data)</p><br><p># regular files, insert creation date</p><p>data = ((stat[ST_CTIME], path)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;for stat, path in data if S_ISREG(stat[ST_MODE]))</p><br><p>for cdate, path in sorted(data):</p><p>&nbsp; &nbsp; print(time.ctime(cdate), os.path.basename(path))</p>', 'from stat import S_ISREG, ST_CTIME, ST_MODE\r\n\r\nimport os, sys, time\r\n\r\n#Relative or absolute path to the directory\r\ndir_path = sys.argv[1] if len(sys.argv) == 2 else r\'.\'\r\n\r\n#all entries in the directory w/ stats\r\ndata = (os.path.join(dir_path, fn) for fn in os.listdir(dir_path))\r\ndata = ((os.stat(path), path) for path in data)\r\n\r\n# regular files, insert creation date\r\ndata = ((stat[ST_CTIME], path)\r\n           for stat, path in data if S_ISREG(stat[ST_MODE]))\r\n\r\nfor cdate, path in sorted(data):\r\n    print(time.ctime(cdate), os.path.basename(path))', 2, 0, NULL, '2023-02-26 07:01:57', '2023-06-03 11:06:25', 1),
(76, 'Write a Python program to get the details of the math module.', 'write-a-python-program-to-get-the-details-of-the-math-module', '<p># Imports the math module</p><p>import math&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><br><p>#Sets everything to a list of math module</p><p>math_ls = dir(math) #&nbsp;</p><p>print(math_ls)</p><br><div>print(\"Details of math module:\\n\")</div><div>help(math)</div>', '# Imports the math module\r\nimport math            \r\n\r\n#Sets everything to a list of math module\r\nmath_ls = dir(math) # \r\n\r\nprint(math_ls)\r\n\r\nprint(\"Details of math module:\\n\")\r\nhelp(math)', 0, 0, NULL, '2023-02-26 07:22:55', '2023-06-03 11:11:31', 1),
(77, 'Write a Python program to calculate the midpoints of a line.', 'write-a-python-program-to-calculate-the-midpoints-of-a-line', '<p>x1 = float(input(\'The value of x (the first endpoint) \'))</p><p>y1 = float(input(\'The value of y (the first endpoint) \'))</p><p>x2 = float(input(\'The value of x (the second endpoint) \'))</p><p>y2 = float(input(\'The value of y (the second endpoint) \'))</p><br><p>x_m_point = (x1 + x2)/2</p><br><p>y_m_point = (y1 + y2)/2</p><br><p>print(\"The midpoint of the line is :\")</p><p>print( \"The midpoint\'s x value is: \",x_m_point)</p><p>print( \"The midpoint\'s y value is: \",y_m_point)</p>', '#Take the value of x (the first endpoint)\r\nx1 = float(input( ))\r\n#Take the value of y (the first endpoint)\r\ny1 = float(input( ))\r\n#Take the value of x (the Second endpoint)\r\nx2 = float(input( ))\r\n#The value of y (the Second endpoint)\r\ny2 = float(input( ))\r\n\r\nx_m_point = (x1 + x2)/2\r\n\r\ny_m_point = (y1 + y2)/2\r\n\r\nprint(\"The midpoint of the line is :\")\r\nprint( \"The midpoint\'s x value is: \",x_m_point)\r\nprint( \"The midpoint\'s y value is: \",y_m_point)', 2, 1, NULL, '2023-02-26 07:38:20', '2023-06-03 11:22:39', 1),
(78, 'Write a Python program to hash a word.', 'write-a-python-program-to-hash-a-word', '<p>soundex=[0,1,2,3,0,1,2,0,0,2,2,4,5,5,0,1,2,6,2,3,0,1,0,2,0,2]</p><br><p>word=input(\"Input the word be hashed: \")</p><p>word=word.upper()</p><br><p>coded=word[0]</p><br><p>for a in word[1:len(word)]:</p><p>&nbsp; &nbsp; i=65-ord(a)</p><p>&nbsp; &nbsp; coded=coded+str(soundex[i])</p><br><p>print(\"The coded word is: \"+coded)</p>', 'soundex=[0,1,2,3,0,1,2,0,0,2,2,4,5,5,0,1,2,6,2,3,0,1,0,2,0,2]\r\n\r\n#Take the word be hashed\r\nword=input( )\r\nword=word.upper()\r\n\r\ncoded=word[0]\r\n\r\nfor a in word[1:len(word)]:\r\n    i=65-ord(a)\r\n    coded=coded+str(soundex[i])\r\n\r\nprint(\"The coded word is: \"+coded)', 2, 1, NULL, '2023-02-26 05:19:22', '2023-06-03 11:30:16', 1),
