(92, 'Write a Python program to count the number of occurrences of a specific character in a string using loop.', 'write-a-python-program-to-count-the-number-of-occurrences-of-a-specific-character-in-a-string-using-loop', '<p>s = \"It does not matter how slowly you go so long as you do not stop\"</p><br><p>print(\"Original string:\")</p><p>print(s)</p><br><p>print(\"Number of occurrence of \'o\' in the said string:\")</p><br><p>ctr = 0&nbsp;</p><br><p>for c in s:</p><p>&nbsp; &nbsp;if c == \'o\':</p><p>&nbsp; &nbsp; &nbsp; &nbsp;ctr = ctr + 1</p><p>print(ctr)</p>', 's = \"It does not matter how slowly you go so long as you do not stop\"  \r\n\r\nprint(\"Original string:\")\r\nprint(s)\r\n\r\nprint(\"Number of occurrence of \'o\' in the said string:\")\r\n\r\nctr = 0 \r\n\r\nfor c in s:\r\n   if c == \'o\':\r\n       ctr = ctr + 1\r\nprint(ctr)', 4, 0, NULL, '2023-03-19 14:50:42', '2023-06-04 07:43:45', 1),
(93, 'Write a Python program to count the number of occurrences of a specific character in a string using collections.Counter().', 'write-a-python-program-to-count-the-number-of-occurrences-of-a-specific-character-in-a-string-using-collectionscounter', '<p>from collections import Counter</p><br><p>s = \"It does not matter how slowly you go so long as you do not stop\"</p><br><p>print(\"Original string:\")</p><p>print(s)</p><br><p>print(\"Number of occurrence of \'o\' in the said string:\")</p><br><p>ctr = Counter(s)</p><p>print (str(ctr[\'o\']))</p>', 'from collections import Counter\r\n\r\ns = \"It does not matter how slowly you go so long as you do not stop\"  \r\n\r\nprint(\"Original string:\")\r\nprint(s)\r\n\r\nprint(\"Number of occurrence of \'o\' in the said string:\")\r\n\r\nctr = Counter(s) \r\nprint (str(ctr[\'o\']))', 2, 0, NULL, '2023-03-19 14:51:36', '2023-06-03 19:19:15', 1),
