(95, 'Write a Python program to count the number of occurrences of a specific character in a string using Regular Expressions.', 'write-a-python-program-to-count-the-number-of-occurrences-of-a-specific-character-in-a-string-using-regular-expressions', '<p>import re</p><p>s = \"It does not matter how slowly you go so long as you do not stop.\"</p><br><p>print(\"Original string:\")</p><p>print(s)</p><br><p>print(\"Number of occurrence of \'o\' in the said string:\")</p><br><p>ctr = len(re.findall(\"o\", s))</p><p>print(ctr)</p>', 'import re\r\n\r\ns = \"It does not matter how slowly you go so long as you do not stop.\" \r\n\r\nprint(\"Original string:\")\r\nprint(s)\r\n\r\nprint(\"Number of occurrence of \'o\' in the said string:\")\r\n\r\nctr = len(re.findall(\"o\", s))\r\nprint(ctr)', 2, 0, NULL, '2023-03-19 14:53:14', '2023-06-03 19:25:45', 1),
(96, 'Write a Python program to check whether a file path is a file or a directory.', 'write-a-python-program-to-check-whether-a-file-path-is-a-file-or-a-directory', '<p>import os</p><br><p>path=\"manage.py\"</p><br><p>if os.path.isdir(path):&nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\"\\nIt is a directory\")&nbsp;&nbsp;</p><p>elif os.path.isfile(path):&nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\"\\nIt is a normal file\")&nbsp;&nbsp;</p><p>else:&nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\"It is a special file (socket, FIFO, device file)\" )</p>', 'import os  \r\n\r\npath=\"manage.py\"  \r\n\r\nif os.path.isdir(path):  \r\n    print(\"\\nIt is a directory\")  \r\nelif os.path.isfile(path):  \r\n    print(\"\\nIt is a normal file\")  \r\nelse:  \r\n    print(\"It is a special file (socket, FIFO, device file)\" )', 2, 0, NULL, '2023-03-19 14:53:50', '2023-06-03 19:31:42', 1),
