(117, 'Write a Python program to retrieve file properties.', 'write-a-python-program-to-retrieve-file-properties', '<p>import os.path</p><p>import time</p><p><br></p><p>print(\'File&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:\', __file__)</p><p>print(\'Access time&nbsp; :\', time.ctime(os.path.getatime(__file__)))</p><p>print(\'Modified time:\', time.ctime(os.path.getmtime(__file__)))</p><p>print(\'Change time&nbsp; :\', time.ctime(os.path.getctime(__file__)))</p><p>print(\'Size&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:\', os.path.getsize(__file__))</p><div><br></div>', 'import os.path\n\nimport time\n\n\n\nprint(\'File         :\', __file__)\n\nprint(\'Access time  :\', time.ctime(os.path.getatime(__file__)))\n\nprint(\'Modified time:\', time.ctime(os.path.getmtime(__file__)))\n\nprint(\'Change time  :\', time.ctime(os.path.getctime(__file__)))\n\nprint(\'Size         :\', os.path.getsize(__file__))', 0, 0, NULL, '2023-03-19 19:17:46', '2023-03-19 19:17:46', 1),
(118, 'Write a Python program to find the path to a file or directory when you encounter a path name.', 'write-a-python-program-to-find-the-path-to-a-file-or-directory-when-you-encounter-a-path-name', '<p>import os.path</p><p><br></p><p>for file in [ __file__, os.path.dirname(__file__), \'/\', \'./broken_link\']:</p><p>&nbsp; &nbsp; print(\'File&nbsp; &nbsp; &nbsp; &nbsp; :\', file)</p><p>&nbsp; &nbsp; print(\'Absolute&nbsp; &nbsp; :\', os.path.isabs(file))</p><p>&nbsp; &nbsp; print(\'Is File?&nbsp; &nbsp; :\', os.path.isfile(file))</p><p>&nbsp; &nbsp; print(\'Is Dir?&nbsp; &nbsp; &nbsp;:\', os.path.isdir(file))</p><p>&nbsp; &nbsp; print(\'Is Link?&nbsp; &nbsp; :\', os.path.islink(file))</p><p>&nbsp; &nbsp; print(\'Exists?&nbsp; &nbsp; &nbsp;:\', os.path.exists(file))</p><p>&nbsp; &nbsp; print(\'Link Exists?:\', os.path.lexists(file))</p><p><span style=\"white-space:pre\"> </span></p>', 'import os.path\n\n\n\nfor file in [ __file__, os.path.dirname(__file__), \'/\', \'./broken_link\']:\n\n    print(\'File        :\', file)\n\n    print(\'Absolute    :\', os.path.isabs(file))\n\n    print(\'Is File?    :\', os.path.isfile(file))\n\n    print(\'Is Dir?     :\', os.path.isdir(file))\n\n    print(\'Is Link?    :\', os.path.islink(file))\n\n    print(\'Exists?     :\', os.path.exists(file))\n\n    print(\'Link Exists?:\', os.path.lexists(file))', 0, 0, NULL, '2023-03-19 19:18:34', '2023-03-19 19:18:34', 1),
(119, 'Write a Python program to check if a number is positive, negative or zero.', 'write-a-python-program-to-check-if-a-number-is-positive-negative-or-zero', '<p>num = float(input(\"Input a number: \"))</p><p>if num &gt; 0:</p><p>&nbsp; &nbsp;print(\"It is positive number\")</p><p>elif num == 0:</p><p>&nbsp; &nbsp;print(\"It is Zero\")</p><p>else:</p><p>&nbsp; &nbsp;print(\"It is a negative number\")</p><p>&nbsp; &nbsp;</p>', 'num = float(input(\"Input a number: \"))\n\nif num > 0:\n\n   print(\"It is positive number\")\n\nelif num == 0:\n\n   print(\"It is Zero\")\n\nelse:\n\n   print(\"It is a negative number\")', 0, 1, NULL, '2023-03-19 19:19:25', '2023-03-19 19:19:25', 1),
(120, 'Write a Python program to get numbers divisible by fifteen from a list using an anonymous function.', 'write-a-python-program-to-get-numbers-divisible-by-fifteen-from-a-list-using-an-anonymous-function', '<p>num_list = [45, 55, 60, 37, 100, 105, 220]</p><p>print(\"Original list:\",num_list)</p><p># use anonymous function to filter</p><p>result = list(filter(lambda x: (x % 15 == 0), num_list))</p><p>print(\"Numbers of the said list divisible by 15 are:\",result)</p><div><br></div>', 'num_list = [45, 55, 60, 37, 100, 105, 220]\n\nprint(\"Original list:\",num_list)\n\n# use anonymous function to filter\n\nresult = list(filter(lambda x: (x % 15 == 0), num_list))\n\nprint(\"Numbers of the said list divisible by 15 are:\",result)\n\n', 0, 0, NULL, '2023-03-19 19:21:10', '2023-03-19 19:21:10', 1),
(121, 'Write a Python program to make file lists from the current directory using a wildcard.', 'write-a-python-program-to-make-file-lists-from-the-current-directory-using-a-wildcard', '<p>import glob</p><p>file_list = glob.glob(\'*.*\')</p><p>print(file_list)</p><p>#Specific files</p><p>print(glob.glob(\'*.py\'))</p><p>print(glob.glob(\'./[0-9].*\'))</p><div><br></div>', 'import glob\n\nfile_list = glob.glob(\'*.*\')\n\nprint(file_list)\n\n#Specific files\n\nprint(glob.glob(\'*.py\'))\n\nprint(glob.glob(\'./[0-9].*\'))\n\n', 0, 0, NULL, '2023-03-19 19:21:45', '2023-03-19 19:21:45', 1),
(122, 'Write a Python program to remove the first item from a specified list.', 'write-a-python-program-to-remove-the-first-item-from-a-specified-list', '<p>color = [\"Red\", \"Black\", \"Green\", \"White\", \"Orange\"]</p><p>print(\"Original list elements:\")</p><p>print(color)</p><p>del color[0]</p><p>print(\"After removing the first color:\")</p><p>print(color)</p>', 'color = [\"Red\", \"Black\", \"Green\", \"White\", \"Orange\"]\n\nprint(\"Original list elements:\")\n\nprint(color)\n\ndel color[0]\n\nprint(\"After removing the first color:\")\n\nprint(color)', 0, 0, NULL, '2023-03-19 19:22:22', '2023-03-19 19:22:22', 1),
(123, 'Write a Python program that inputs a number and generates an error message if it is not a number.', 'write-a-python-program-that-inputs-a-number-and-generates-an-error-message-if-it-is-not-a-number', '<p>while True:</p><p>&nbsp; &nbsp; try:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; a = int(input(\"Input a number: \"))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; break</p><p>&nbsp; &nbsp; except ValueError:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\"\\nThis is not a number. Try again...\")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print()</p><p><span style=\"white-space:pre\"> </span></p>', 'while True:\n\n    try:\n\n        a = int(input(\"Input a number: \"))\n\n        break\n\n    except ValueError:\n\n        print(\"\\nThis is not a number. Try again...\")\n\n        print()\n\n ', 0, 1, NULL, '2023-03-19 19:39:55', '2023-03-19 19:39:55', 1),
(124, 'Write a Python program to filter positive numbers from a list.', 'write-a-python-program-to-filter-positive-numbers-from-a-list', '<p>nums = [34, 1, 0, -23, 12, -88]</p><p>print(\"Original numbers in the list: \",nums)</p><p>print(\"Positive numbers in the said list: \")</p><p>for pos_nums in nums:</p><p>&nbsp; &nbsp;if pos_nums &gt; 0:</p><p>&nbsp; &nbsp; &nbsp; print(pos_nums, end = \" \")</p><div><br></div>', 'nums = [34, 1, 0, -23, 12, -88]\n\nprint(\"Original numbers in the list: \",nums)\n\nprint(\"Positive numbers in the said list: \")\n\nfor pos_nums in nums:\n\n   if pos_nums > 0:\n\n      print(pos_nums, end = \" \")\n\n', 0, 0, NULL, '2023-03-19 19:40:40', '2023-03-19 19:40:40', 1),
(125, 'Write a Python program to compute the product of a list of integers (without using for loop).', 'write-a-python-program-to-compute-the-product-of-a-list-of-integers-without-using-for-loop', '<p>import math</p><p>&nbsp;# Python version 3.9.</p><p>nums = [10, 20, 30,]</p><p>print(\"Original list numbers:\")</p><p>print(nums)</p><p>nums_product = math.prod(nums)</p><p>print(\"\\nProduct of the said numbers (without using for loop):\",nums_product)</p><div><br></div>', 'import math\n\n # Python version 3.9.\n\nnums = [10, 20, 30,]\n\nprint(\"Original list numbers:\")\n\nprint(nums)\n\nnums_product = math.prod(nums)\n\nprint(\"\\nProduct of the said numbers (without using for loop):\",nums_product)\n\n', 2, 0, NULL, '2023-03-19 19:41:44', '2023-06-10 22:12:17', 1),
