(222, 'Write a Python program to check whether a number is \"happy\" or not.', 'write-a-python-program-to-check-whether-a-number-is-\"happy\"-or-not.', '<div>def is_Happy_num(n):</div><div>&nbsp; past = set()</div><div>&nbsp; while n != 1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; n = sum(int(i)**2 for i in str(n))</div><div>&nbsp; &nbsp; &nbsp; &nbsp; if n in past:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return False</div><div>&nbsp; &nbsp; &nbsp; &nbsp; past.add(n)</div><div>&nbsp; return True</div><div>print(is_Happy_num(7))</div><div>print(is_Happy_num(932))</div><div>print(is_Happy_num(6))</div>', 'def is_Happy_num(n):\n  past = set()\n  while n != 1:\n        n = sum(int(i)**2 for i in str(n))\n        if n in past:\n            return False\n        past.add(n)\n  return True\nprint(is_Happy_num(7))\nprint(is_Happy_num(932))\nprint(is_Happy_num(6))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:07:51', 1),
(223, 'Write a Python program to find and print the first 10 happy numbers.', 'write-a-python-program-to-find-and-print-the-first-10-happy-numbers.', '<div>def happy_numbers(n):</div><div>&nbsp; &nbsp; past = set()<span style=\"white-space:pre\">			</span></div><div>&nbsp; &nbsp; while n != 1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; n = sum(int(i)**2 for i in str(n))</div><div>&nbsp; &nbsp; &nbsp; &nbsp; if n in past:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return False</div><div>&nbsp; &nbsp; &nbsp; &nbsp; past.add(n)</div><div>&nbsp; &nbsp; return True</div><div>print([x for x in range(500) if happy_numbers(x)][:10])</div>', 'def happy_numbers(n):\n    past = set()			\n    while n != 1:\n        n = sum(int(i)**2 for i in str(n))\n        if n in past:\n            return False\n        past.add(n)\n    return True\nprint([x for x in range(500) if happy_numbers(x)][:10])', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:08:20', 1),
(224, 'Write a Python program that counts the number of prime numbers that are less than a given non-negative number.', 'write-a-python-program-that-counts-the-number-of-prime-numbers-that-are-less-than-a-given-non-negative-number.', '<div>def count_Primes_nums(n):</div><div>&nbsp; &nbsp; ctr = 0</div><div>&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; for num in range(n):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; if num &lt;= 1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; continue</div><div>&nbsp; &nbsp; &nbsp; &nbsp; for i in range(2, num):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (num % i) == 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; break</div><div>&nbsp; &nbsp; &nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ctr += 1</div><div><br></div><div>&nbsp; &nbsp; return ctr</div><div><br></div><div>print(count_Primes_nums(10))</div><div>print(count_Primes_nums(100))</div>', 'def count_Primes_nums(n):\n    ctr = 0\n    \n    for num in range(n):\n        if num <= 1:\n            continue\n        for i in range(2, num):\n            if (num % i) == 0:\n                break\n        else:\n            ctr += 1\n\n    return ctr\n\nprint(count_Primes_nums(10))\nprint(count_Primes_nums(100))', 2, 0, NULL, '2023-03-26 06:49:32', '2023-05-25 09:38:42', 1),
(225, 'Write a Python program to check if two given strings are isomorphic to each other or not.', 'write-a-python-program-to-check-if-two-given-strings-are-isomorphic-to-each-other-or-not.', '<div>def isIsomorphic(str1, str2):&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; dict_str1 = {}</div><div>&nbsp; &nbsp; dict_str2 = {}</div><div>&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; for i, value in enumerate(str1):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; dict_str1[value] = dict_str1.get(value, []) + [i]</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; for j, value in enumerate(str2):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; dict_str2[value] = dict_str2.get(value, []) + [j]</div><div>&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; if sorted(dict_str1.values()) == sorted(dict_str2.values()):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return True</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return False</div><div><br></div><div>print(isIsomorphic(\"foo\", \"bar\"));&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div><div>print(isIsomorphic(\"bar\", \"foo\"));&nbsp; &nbsp; &nbsp;&nbsp;</div><div>print(isIsomorphic(\"paper\", \"title\"));&nbsp; &nbsp;</div><div>print(isIsomorphic(\"title\", \"paper\"));</div><div>print(isIsomorphic(\"apple\", \"orange\"));</div><div>print(isIsomorphic(\"aa\", \"ab\"));</div><div>print(isIsomorphic(\"ab\", \"aa\"));</div>', 'def isIsomorphic(str1, str2):          \n    dict_str1 = {}\n    dict_str2 = {}\n    \n    for i, value in enumerate(str1):\n        dict_str1[value] = dict_str1.get(value, []) + [i]\n            \n    for j, value in enumerate(str2):\n        dict_str2[value] = dict_str2.get(value, []) + [j]\n    \n    if sorted(dict_str1.values()) == sorted(dict_str2.values()):\n        return True\n    else:\n        return False\n\nprint(isIsomorphic(\"foo\", \"bar\"));         \nprint(isIsomorphic(\"bar\", \"foo\"));      \nprint(isIsomorphic(\"paper\", \"title\"));   \nprint(isIsomorphic(\"title\", \"paper\"));\nprint(isIsomorphic(\"apple\", \"orange\"));\nprint(isIsomorphic(\"aa\", \"ab\"));\nprint(isIsomorphic(\"ab\", \"aa\"));', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:09:54', 1),
(226, 'Write a Python program to find the longest common prefix string among a given array of strings. Return false if there is no common prefix.', 'write-a-python-program-to-find-the-longest-common-prefix-string-among-a-given-array-of-strings.-return-false-if-there-is-no-common-prefix.', '<div>def longest_Common_Prefix(str1):</div><div>&nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; if not str1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return \"\"</div><div><br></div><div>&nbsp; &nbsp; short_str = min(str1,key=len)</div><div><br></div><div>&nbsp; &nbsp; for i, char in enumerate(short_str):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; for other in str1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if other[i] != char:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return short_str[:i]</div><div><br></div><div>&nbsp; &nbsp; return short_str&nbsp;</div><div><br></div><div>print(longest_Common_Prefix([\"abcdefgh\",\"abcefgh\"]))</div><div>print(longest_Common_Prefix([\"w3r\",\"w3resource\"]))</div><div>print(longest_Common_Prefix([\"Python\",\"PHP\", \"Perl\"]))</div><div>print(longest_Common_Prefix([\"Python\",\"PHP\", \"Java\"]))</div>', 'def longest_Common_Prefix(str1):\n    \n    if not str1:\n        return \"\"\n\n    short_str = min(str1,key=len)\n\n    for i, char in enumerate(short_str):\n        for other in str1:\n            if other[i] != char:\n                return short_str[:i]\n\n    return short_str \n\nprint(longest_Common_Prefix([\"abcdefgh\",\"abcefgh\"]))\nprint(longest_Common_Prefix([\"w3r\",\"w3resource\"]))\nprint(longest_Common_Prefix([\"Python\",\"PHP\", \"Perl\"]))\nprint(longest_Common_Prefix([\"Python\",\"PHP\", \"Java\"]))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:10:34', 1),
(227, 'Write a Python program to reverse only the vowels of a given string.', 'write-a-python-program-to-reverse-only-the-vowels-of-a-given-string.', '<div>def reverse_vowels(str1):</div><div><span style=\"white-space:pre\">	</span>vowels = \"\"</div><div><span style=\"white-space:pre\">	</span>for char in str1:</div><div><span style=\"white-space:pre\">		</span>if char in \"aeiouAEIOU\":</div><div><span style=\"white-space:pre\">			</span>vowels += char</div><div><span style=\"white-space:pre\">	</span>result_string = \"\"</div><div><span style=\"white-space:pre\">	</span>for char in str1:</div><div><span style=\"white-space:pre\">		</span>if char in \"aeiouAEIOU\":</div><div><span style=\"white-space:pre\">			</span>result_string += vowels[-1]</div><div><span style=\"white-space:pre\">			</span>vowels = vowels[:-1]</div><div><span style=\"white-space:pre\">		</span>else:</div><div><span style=\"white-space:pre\">			</span>result_string += char</div><div><span style=\"white-space:pre\">	</span>return result_string</div><div>print(reverse_vowels(\"w3resource\"))</div><div>print(reverse_vowels(\"Python\"))</div><div>print(reverse_vowels(\"Perl\"))</div><div>print(reverse_vowels(\"USA\"))</div>', 'def reverse_vowels(str1):\n	vowels = \"\"\n	for char in str1:\n		if char in \"aeiouAEIOU\":\n			vowels += char\n	result_string = \"\"\n	for char in str1:\n		if char in \"aeiouAEIOU\":\n			result_string += vowels[-1]\n			vowels = vowels[:-1]\n		else:\n			result_string += char\n	return result_string\nprint(reverse_vowels(\"w3resource\"))\nprint(reverse_vowels(\"Python\"))\nprint(reverse_vowels(\"Perl\"))\nprint(reverse_vowels(\"USA\"))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:11:20', 1),
(228, 'Write a Python program to check whether a given integer is a palindrome or not.', 'write-a-python-program-to-check-whether-a-given-integer-is-a-palindrome-or-not.', '<div>def is_Palindrome(n):</div><div>&nbsp; &nbsp; return str(n) == str(n)[::-1]</div><div>print(is_Palindrome(100))</div><div>print(is_Palindrome(252))</div><div>print(is_Palindrome(-838))</div>', 'def is_Palindrome(n):\n    return str(n) == str(n)[::-1]\nprint(is_Palindrome(100))\nprint(is_Palindrome(252))\nprint(is_Palindrome(-838))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:11:51', 1),
(229, 'Write a Python program that removes duplicate elements from a given array of numbers so that each element appears only once and returns the new length of the array.', 'write-a-python-program-that-removes-duplicate-elements-from-a-given-array-of-numbers-so-that-each-element-appears-only-once-and-returns-the-new-length-of-the-array.', '<div>def remove_duplicates(nums):</div><div>&nbsp; &nbsp; for i in range (len(nums)-1, 0, -1):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; if nums[i] == nums[i-1]:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; del nums[i-1]</div><div>&nbsp; &nbsp; return len(nums)</div><div><br></div><div>print(remove_duplicates([0,0,1,1,2,2,3,3,4,4,4]))</div><div>print(remove_duplicates([1, 2, 2, 3, 4, 4]))</div>', 'def remove_duplicates(nums):\n    for i in range (len(nums)-1, 0, -1):\n        if nums[i] == nums[i-1]:\n            del nums[i-1]\n    return len(nums)\n\nprint(remove_duplicates([0,0,1,1,2,2,3,3,4,4,4]))\nprint(remove_duplicates([1, 2, 2, 3, 4, 4]))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:12:22', 1),
(230, 'Write a Python program to calculate the maximum profit from selling and buying values of stock. An array of numbers represent the stock prices in chronological order.', 'write-a-python-program-to-calculate-the-maximum-profit-from-selling-and-buying-values-of-stock.-an-array-of-numbers-represent-the-stock-prices-in-chronological-order.', '<div>def buy_and_sell(stock_price):</div><div>&nbsp; &nbsp; max_profit_val, current_max_val = 0, 0&nbsp;</div><div>&nbsp; &nbsp; for price in reversed(stock_price):&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; current_max_val = max(current_max_val, price)&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; potential_profit = current_max_val - price&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</div><div>&nbsp; &nbsp; &nbsp; &nbsp; max_profit_val = max(potential_profit, max_profit_val)</div><div><br></div><div>&nbsp; &nbsp; return max_profit_val</div><div><br></div><div>print(buy_and_sell([8, 10, 7, 5, 7, 15]))</div><div>print(buy_and_sell([1, 2, 8, 1]))</div><div>print(buy_and_sell([]))</div>', 'def buy_and_sell(stock_price):\n    max_profit_val, current_max_val = 0, 0 \n    for price in reversed(stock_price):                       \n        current_max_val = max(current_max_val, price)          \n        potential_profit = current_max_val - price          \n        max_profit_val = max(potential_profit, max_profit_val)\n\n    return max_profit_val\n\nprint(buy_and_sell([8, 10, 7, 5, 7, 15]))\nprint(buy_and_sell([1, 2, 8, 1]))\nprint(buy_and_sell([]))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:13:10', 1),
(231, 'Write a Python program to remove all instances of a given value from a given array of integers and find the length of the newly created array.', 'write-a-python-program-to-remove-all-instances-of-a-given-value-from-a-given-array-of-integers-and-find-the-length-of-the-newly-created-array.', '<div>def remove_element(array_nums, val):</div><div>&nbsp; &nbsp; i = 0</div><div>&nbsp; &nbsp; while i &lt; len(array_nums):</div><div>&nbsp; &nbsp; &nbsp; &nbsp; if array_nums[i] == val:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; array_nums.remove(array_nums[i])</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i += 1</div><div><br></div><div>&nbsp; &nbsp; return len(array_nums)</div><div>print(remove_element([1, 2, 3, 4, 5, 6, 7, 5], 5))</div><div>print(remove_element([10,10,10,10,10], 10))&nbsp;</div><div>print(remove_element([10,10,10,10,10], 20))&nbsp;</div><div>print(remove_element([], 1))</div>', 'def remove_element(array_nums, val):\n    i = 0\n    while i < len(array_nums):\n        if array_nums[i] == val:\n            array_nums.remove(array_nums[i])\n\n        else:\n            i += 1\n\n    return len(array_nums)\nprint(remove_element([1, 2, 3, 4, 5, 6, 7, 5], 5))\nprint(remove_element([10,10,10,10,10], 10)) \nprint(remove_element([10,10,10,10,10], 20)) \nprint(remove_element([], 1))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:13:37', 1),
(232, 'Write a Python program to find the starting and ending position of a given value in a given array of integers, sorted in ascending order.', 'write-a-python-program-to-find-the-starting-and-ending-position-of-a-given-value-in-a-given-array-of-integers,-sorted-in-ascending-order.', '<div>def search_Range(array_nums, target_val):</div><div>&nbsp; result_arra = []</div><div>&nbsp; start_pos = 0</div><div>&nbsp; end_pos = 0</div><div>&nbsp; for i in range(len(array_nums)):</div><div>&nbsp; &nbsp; &nbsp; if target_val == array_nums[i] and start_pos == -1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; start_pos = i</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; end_pos = i</div><div>&nbsp; &nbsp; &nbsp; elif target_val == array_nums[i] and start_pos != -1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; end_pos = i</div><div>&nbsp; result_arra.append(start_pos)</div><div>&nbsp; result_arra.append(end_pos)</div><div>&nbsp; return result_arra</div><div>print(search_Range([5, 7, 7, 8, 8, 8], 8))</div><div>print(search_Range([1, 3, 6, 9, 13, 14], 4))</div><div>print(search_Range([5, 7, 7, 8, 10], 8))</div>', 'def search_Range(array_nums, target_val):\n  result_arra = []\n  start_pos = 0\n  end_pos = 0\n  for i in range(len(array_nums)):\n      if target_val == array_nums[i] and start_pos == -1:\n          start_pos = i\n          end_pos = i\n      elif target_val == array_nums[i] and start_pos != -1:\n          end_pos = i\n  result_arra.append(start_pos)\n  result_arra.append(end_pos)\n  return result_arra\nprint(search_Range([5, 7, 7, 8, 8, 8], 8))\nprint(search_Range([1, 3, 6, 9, 13, 14], 4))\nprint(search_Range([5, 7, 7, 8, 10], 8))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:15:46', 1),
