(239, 'Write a Python program to count the number of equal numbers from three given integers.', 'write-a-python-program-to-count-the-number-of-equal-numbers-from-three-given-integers.', '<div>def test_three_equal(x, y, z):</div><div>&nbsp; result= set([x, y, z])</div><div>&nbsp; if len(result)==3:</div><div>&nbsp; &nbsp; return 0</div><div>&nbsp; else:</div><div>&nbsp; &nbsp; return (4 - len(result))</div><div><br></div><div>print(test_three_equal(1, 1, 1))</div><div>print(test_three_equal(1, 2, 2))</div><div>print(test_three_equal(-1, -2, -3))</div><div>print(test_three_equal(-1, -1, -1))</div>', 'def test_three_equal(x, y, z):\n  result= set([x, y, z])\n  if len(result)==3:\n    return 0\n  else:\n    return (4 - len(result))\n\nprint(test_three_equal(1, 1, 1))\nprint(test_three_equal(1, 2, 2))\nprint(test_three_equal(-1, -2, -3))\nprint(test_three_equal(-1, -1, -1))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:20:50', 1),
(240, 'Write a Python program to check whether a given employee code is exactly 8 digits or 12 digits. Return True if the employee code is valid and False if it\'s not.', 'write-a-python-program-to-check-whether-a-given-employee-code-is-exactly-8-digits-or-12-digits.-return-true-if-the-employee-code-is-valid-and-false-if-it\'s-not.', '<div>def is_valid_emp_code(emp_code):</div><div>&nbsp; return len(emp_code) in [8, 12] and emp_code.isdigit()</div><div>print(is_valid_emp_code(\'12345678\'))</div><div>print(is_valid_emp_code(\'1234567j\'))</div><div>print(is_valid_emp_code(\'12345678j\'))</div><div>print(is_valid_emp_code(\'123456789123\'))</div><div>print(is_valid_emp_code(\'123456abcdef\'))</div>', 'def is_valid_emp_code(emp_code):\n  return len(emp_code) in [8, 12] and emp_code.isdigit()\nprint(is_valid_emp_code(\'12345678\'))\nprint(is_valid_emp_code(\'1234567j\'))\nprint(is_valid_emp_code(\'12345678j\'))\nprint(is_valid_emp_code(\'123456789123\'))\nprint(is_valid_emp_code(\'123456abcdef\'))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:21:24', 1),
