(253, 'Write a Python program to compute the sum of all items in a given array of integers where each integer is multiplied by its index. Return 0 if there is no number.', 'write-a-python-program-to-compute-the-sum-of-all-items-in-a-given-array-of-integers-where-each-integer-is-multiplied-by-its-index.-return-0-if-there-is-no-number.', '<div>def sum_index_multiplier(nums):</div><div><span style=\"white-space:pre\">	</span>return sum(j*i for i, j in enumerate(nums))</div><div><br></div><div>print(sum_index_multiplier([1,2,3,4]))</div><div>print(sum_index_multiplier([-1,-2,-3,-4]))</div><div>print(sum_index_multiplier([]))</div>', 'def sum_index_multiplier(nums):\n	return sum(j*i for i, j in enumerate(nums))\n\nprint(sum_index_multiplier([1,2,3,4]))\nprint(sum_index_multiplier([-1,-2,-3,-4]))\nprint(sum_index_multiplier([]))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:34:15', 1),
(254, 'Write a Python program to find the name of the oldest student in a given dictionary containing the names and ages of a group of students.', 'write-a-python-program-to-find-the-name-of-the-oldest-student-in-a-given-dictionary-containing-the-names-and-ages-of-a-group-of-students.', '<div>def oldest_student(students):</div><div><span style=\"white-space:pre\">	</span>return max(students, key=students.get)</div><div><br></div><div>print(oldest_student({\"Bernita Ahner\": 12, \"Kristie Marsico\": 11, \"Sara Pardee\": 14, \"Fallon Fabiano\": 11, \"Nidia Dominique\": 15}))&nbsp;</div><div>print(oldest_student({\"Nilda Woodside\": 12, \"Jackelyn Pineda\": 12.2, \"Sofia Park\": 12.4, \"Joannie Archibald\": 12.6, \"Becki Saunder\": 12.7}))</div>', 'def oldest_student(students):\n	return max(students, key=students.get)\n\nprint(oldest_student({\"Bernita Ahner\": 12, \"Kristie Marsico\": 11, \"Sara Pardee\": 14, \"Fallon Fabiano\": 11, \"Nidia Dominique\": 15})) \nprint(oldest_student({\"Nilda Woodside\": 12, \"Jackelyn Pineda\": 12.2, \"Sofia Park\": 12.4, \"Joannie Archibald\": 12.6, \"Becki Saunder\": 12.7}))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:37:03', 1),
(255, 'Write a Python program to create a string with no duplicate consecutive letters from a given string.', 'write-a-python-program-to-create-a-string-with-no-duplicate-consecutive-letters-from-a-given-string.', '<div>def no_consecutive_letters (txt):</div><div>&nbsp; &nbsp; return txt[0] + \'\'.join(txt[i] for i in range(1,len(txt)) if txt[i] != txt[i-1])</div><div><br></div><div>print(no_consecutive_letters(\"PPYYYTTHON\"))</div><div>print(no_consecutive_letters(\"PPyyythonnn\"))</div><div>print(no_consecutive_letters(\"Java\"))</div><div>print(no_consecutive_letters(\"PPPHHHPPP\"))</div>', 'def no_consecutive_letters (txt):\n    return txt[0] + \'\'.join(txt[i] for i in range(1,len(txt)) if txt[i] != txt[i-1])\n\nprint(no_consecutive_letters(\"PPYYYTTHON\"))\nprint(no_consecutive_letters(\"PPyyythonnn\"))\nprint(no_consecutive_letters(\"Java\"))\nprint(no_consecutive_letters(\"PPPHHHPPP\"))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-04-02 18:38:12', 1),
(256, 'Write a Python program to check whether two given lines are parallel or not.', 'write-a-python-program-to-check-whether-two-given-lines-are-parallel-or-not.', '<div>def parallel_lines(line1, line2):</div><div>&nbsp; return line1[0]/line1[1] == line2[0]/line2[1]</div><div>#2x + 3y = 4, 2x + 3y = 8</div><div>print(parallel_lines([2,3,4], [2,3,8]))</div><div>#2x + 3y = 4, 4x - 3y = 8</div><div>print(parallel_lines([2,3,4], [4,-3,8]))</div>', 'def parallel_lines(line1, line2):\n  return line1[0]/line1[1] == line2[0]/line2[1]\n#2x + 3y = 4, 2x + 3y = 8\nprint(parallel_lines([2,3,4], [2,3,8]))\n#2x + 3y = 4, 4x - 3y = 8\nprint(parallel_lines([2,3,4], [4,-3,8]))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-05-23 06:46:06', 1),
(257, 'Write a Python program to find a number in a given matrix that is maximum in its column and minimum in its row.', 'write-a-python-program-to-find-a-number-in-a-given-matrix-that-is-maximum-in-its-column-and-minimum-in-its-row.', '<div># Number in a Matrix: Maximum in its column and minimum in its row.</div><div>def matrix_number(matrix):</div><div>&nbsp; &nbsp;result = set(map(min, matrix)) &amp; set(map(max, zip(*matrix)))</div><div>&nbsp; &nbsp;return list(result)</div><div>m1 = [[1,2], [2,3]]</div><div>print(\"Original matrix:\",m1)</div><div>print(\"Number in the said matrix which is maximum in its column and minimum in its row:\")</div><div>print(matrix_number(m1))</div><div>m1 = [[1,2,3], [3,4,5]]</div><div>print(\"\\nOriginal matrix:\",m1)</div><div>print(\"Number in the said matrix which is maximum in its column and minimum in its row:\")</div><div>print(matrix_number(m1))</div><div>m1 = [[7,5,6], [3,4,4], [6,5,7]]</div><div>print(\"\\nOriginal matrix:\",m1)</div><div>print(\"Number in the said matrix which is maximum in its column and minimum in its row:\")</div><div>print(matrix_number(m1))</div>', '# Number in a Matrix: Maximum in its column and minimum in its row.\ndef matrix_number(matrix):\n   result = set(map(min, matrix)) & set(map(max, zip(*matrix)))\n   return list(result)\nm1 = [[1,2], [2,3]]\nprint(\"Original matrix:\",m1)\nprint(\"Number in the said matrix which is maximum in its column and minimum in its row:\")\nprint(matrix_number(m1))\nm1 = [[1,2,3], [3,4,5]]\nprint(\"\\nOriginal matrix:\",m1)\nprint(\"Number in the said matrix which is maximum in its column and minimum in its row:\")\nprint(matrix_number(m1))\nm1 = [[7,5,6], [3,4,4], [6,5,7]]\nprint(\"\\nOriginal matrix:\",m1)\nprint(\"Number in the said matrix which is maximum in its column and minimum in its row:\")\nprint(matrix_number(m1))', 0, 0, NULL, '2023-03-26 06:49:32', '2023-05-22 21:55:19', 1),
(258, 'Write a Python program to check whether a given sequence is linear, quadratic or cubic.', 'write-a-python-program-to-check-whether-a-given-sequence-is-linear,-quadratic-or-cubic.', '<div>def Seq_Linear_Quadratic_Cubic(seq_nums):</div><div>&nbsp; seq_nums = [seq_nums[x] - seq_nums[x-1] for x in range(1, len(seq_nums))]</div><div>&nbsp; if len(set(seq_nums)) == 1: return \"Linear Sequence\"</div><div>&nbsp; seq_nums = [seq_nums[x] - seq_nums[x-1] for x in range(1, len(seq_nums))]</div><div>&nbsp; if len(set(seq_nums)) == 1: return \"Quadratic Sequence\"</div><div>&nbsp; seq_nums = [seq_nums[x] - seq_nums[x-1] for x in range(1, len(seq_nums))]</div><div>&nbsp; if len(set(seq_nums)) == 1: return \"Cubic Sequence\"</div><div><br></div><div>nums = [0,2,4,6,8,10]</div><div>print(\"Original Sequence:\",nums)</div><div>print(\"Check the said sequence is Linear, Quadratic or Cubic?\")</div><div>print(Seq_Linear_Quadratic_Cubic(nums))</div><div>nums = [1,4,9,16,25]</div><div>print(\"\\nOriginal Sequence:\",nums)</div><div>print(\"Check the said sequence is Linear, Quadratic or Cubic?\")</div><div>print(Seq_Linear_Quadratic_Cubic(nums))</div><div>nums = [0,12,10,0,-12,-20]</div><div>print(\"\\nOriginal Sequence:\",nums)</div><div>print(\"Check the said sequence is Linear, Quadratic or Cubic?\")</div><div>print(Seq_Linear_Quadratic_Cubic(nums))</div><div>nums = [1,2,3,4,5]</div><div>print(\"\\nOriginal Sequence:\",nums)</div><div>print(\"Check the said sequence is Linear, Quadratic or Cubic?\")</div><div>print(Seq_Linear_Quadratic_Cubic(nums))</div>', 'def Seq_Linear_Quadratic_Cubic(seq_nums):\n  seq_nums = [seq_nums[x] - seq_nums[x-1] for x in range(1, len(seq_nums))]\n  if len(set(seq_nums)) == 1: return \"Linear Sequence\"\n  seq_nums = [seq_nums[x] - seq_nums[x-1] for x in range(1, len(seq_nums))]\n  if len(set(seq_nums)) == 1: return \"Quadratic Sequence\"\n  seq_nums = [seq_nums[x] - seq_nums[x-1] for x in range(1, len(seq_nums))]\n  if len(set(seq_nums)) == 1: retu