(326, 'Write a Python program to find the next and previous palindromes of a specified number.', 'write-a-python-program-to-find-the-next-and-previous-palindromes-of-a-specified-number', '<p>def Previous_Palindrome(num):</p><p>&nbsp; &nbsp; for x in range(num-1,0,-1):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if str(x) == str(x)[::-1]:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return x</p><p>print(Previous_Palindrome(99));</p><p>print(Previous_Palindrome(1221));</p><div><br></div>', 'def Previous_Palindrome(num):\n\n    for x in range(num-1,0,-1):\n\n        if str(x) == str(x)[::-1]:\n\n            return x\n\nprint(Previous_Palindrome(99));\n\nprint(Previous_Palindrome(1221));', 0, 0, NULL, '2023-04-03 21:12:31', '2023-04-03 21:12:31', 1),
(327, 'Write a Python program to convert a float to ratio.', 'write-a-python-program-to-convert-a-float-to-ratio', '<p>from fractions import Fraction</p><p>value = 4.2</p><p>print(Fraction(value).limit_denominator())</p><div><br></div>', 'from fractions import Fraction\n\nvalue = 4.2\n\nprint(Fraction(value).limit_denominator())\n\n', 0, 0, NULL, '2023-04-03 21:14:11', '2023-04-03 21:14:11', 1),
(328, 'Write a Python program for the nth Catalan numbers.', 'write-a-python-program-for-the-nth-catalan-numbers', '<p>def catalan_number(num):</p><p>&nbsp; &nbsp; if num &lt;=1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return 1</p><p>&nbsp; &nbsp;</p><p>&nbsp; &nbsp; res_num = 0</p><p>&nbsp; &nbsp; for i in range(num):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; res_num += catalan_number(i) * catalan_number(num-i-1)</p><p>&nbsp; &nbsp; return res_num</p><p>&nbsp;</p><p>for n in range(10):</p><p>&nbsp; &nbsp; print(catalan_number(n))</p><p><span style=\"white-space:pre\">	</span></p>', 'def catalan_number(num):\n\n    if num <=1:\n\n         return 1\n\n   \n\n    res_num = 0\n\n    for i in range(num):\n\n        res_num += catalan_number(i) * catalan_number(num-i-1)\n\n    return res_num\n\n \n\nfor n in range(10):\n\n    print(catalan_number(n))\n\n	', 0, 0, NULL, '2023-04-03 21:14:48', '2023-04-03 21:14:48', 1),
(329, 'Write a Python program to display numbers separated by commas as thousands.', 'write-a-python-program-to-display-numbers-separated-by-commas-as-thousands', '<p>print(\"{:,}\".format(1000000))</p><p>print(\"{:,}\".format(10000))</p><div><br></div>', 'print(\"{:,}\".format(1000000))\n\nprint(\"{:,}\".format(10000))\n\n', 0, 0, NULL, '2023-04-03 21:15:29', '2023-04-03 21:15:29', 1),
(330, 'Write a Python program to calculate distance between two points using latitude and longitude.', 'write-a-python-program-to-calculate-distance-between-two-points-using-latitude-and-longitude', '<p>from math import radians, sin, cos, acos</p><p><br></p><p>print(\"Input coordinates of two points:\")</p><p>slat = radians(float(input(\"Starting latitude: \")))</p><p>slon = radians(float(input(\"Ending longitude: \")))</p><p>elat = radians(float(input(\"Starting latitude: \")))</p><p>elon = radians(float(input(\"Ending longitude: \")))</p><p><br></p><p>dist = 6371.01 * acos(sin(slat)*sin(elat) + cos(slat)*cos(elat)*cos(slon - elon))</p><p>print(\"The distance is %.2fkm.\" % dist)</p><div><br></div>', 'from math import radians, sin, cos, acos\n\n\n\nprint(\"Input coordinates of two points:\")\n\nslat = radians(float(input(\"Starting latitude: \")))\n\nslon = radians(float(input(\"Ending longitude: \")))\n\nelat = radians(float(input(\"Starting latitude: \")))\n\nelon = radians(float(input(\"Ending longitude: \")))\n\n\n\ndist = 6371.01 * acos(sin(slat)*sin(elat) + cos(slat)*cos(elat)*cos(slon - elon))\n\nprint(\"The distance is %.2fkm.\" % dist)', 0, 1, NULL, '2023-04-03 21:19:16', '2023-04-03 21:19:16', 1),
(331, 'Write a Python program to calculate the area of a regular polygon.', 'write-a-python-program-to-calculate-the-area-of-a-regular-polygon', '<p>from math import tan, pi</p><p>n_sides = int(input(\"Input number of sides: \"))</p><p>s_length = float(input(\"Input the length of a side: \"))</p><p>p_area = n_sides * (s_length ** 2) / (4 * tan(pi / n_sides))</p><p>print(\"The area of the polygon is: \",p_area)</p><div><br></div>', 'from math import tan, pi\n\nn_sides = int(input(\"Input number of sides: \"))\n\ns_length = float(input(\"Input the length of a side: \"))\n\np_area = n_sides * (s_length ** 2) / (4 * tan(pi / n_sides))\n\nprint(\"The area of the polygon is: \",p_area)\n\n', 0, 1, NULL, '2023-04-03 21:20:05', '2023-04-03 21:20:05', 1),
(332, 'Write a Python program to calculate the wind chill index.', 'write-a-python-program-to-calculate-the-wind-chill-index', '<p>import math</p><p>v = float(input(\"Input wind speed in kilometers/hour: \"))</p><p>t = float(input(\"Input air temperature in degrees Celsius: \"))</p><p>wci = 13.12 + 0.6215*t -&nbsp; 11.37*math.pow(v, 0.16) + 0.3965*t*math.pow(v, 0.16)</p><p>print(\"The wind chill index is\", int(round(wci, 0)))</p><div><br></div>', 'import math\n\nv = float(input(\"Input wind speed in kilometers/hour: \"))\n\nt = float(input(\"Input air temperature in degrees Celsius: \"))\n\nwci = 13.12 + 0.6215*t -  11.37*math.pow(v, 0.16) + 0.3965*t*math.pow(v, 0.16)\n\nprint(\"The wind chill index is\", int(round(wci, 0)))\n\n', 0, 1, NULL, '2023-04-03 21:20:41', '2023-04-03 21:20:41', 1),
(333, 'Write a Python program to find the roots of a quadratic function.', 'write-a-python-program-to-find-the-roots-of-a-quadratic-function', '<p>from math import sqrt</p><p><br></p><p>print(\"Quadratic function : (a * x^2) + b*x + c\")</p><p>a = float(input(\"a: \"))</p><p>b = float(input(\"b: \"))</p><p>c = float(input(\"c: \"))</p><p><br></p><p>r = b**2 - 4*a*c</p><p><br></p><p>if r &gt; 0:</p><p>&nbsp; &nbsp; num_roots = 2</p><p>&nbsp; &nbsp; x1 = (((-b) + sqrt(r))/(2*a))&nbsp; &nbsp; &nbsp;</p><p>&nbsp; &nbsp; x2 = (((-b) - sqrt(r))/(2*a))</p><p>&nbsp; &nbsp; print(\"There are 2 roots: %f and %f\" % (x1, x2))</p><p>elif r == 0:</p><p>&nbsp; &nbsp; num_roots = 1</p><p>&nbsp; &nbsp; x = (-b) / 2*a</p><p>&nbsp; &nbsp; print(\"There is one root: \", x)</p><p>else:</p><p>&nbsp; &nbsp; num_roots = 0</p><p>&nbsp; &nbsp; print(\"No roots, discriminant &lt; 0.\")</p><p>&nbsp; &nbsp; exit()</p><p><span style=\"white-space:pre\">	</span></p>', 'from math import sqrt\n\n\n\nprint(\"Quadratic function : (a * x^2) + b*x + c\")\n\na = float(input(\"a: \"))\n\nb = float(input(\"b: \"))\n\nc = float(input(\"c: \"))\n\n\n\nr = b**2 - 4*a*c\n\n\n\nif r > 0:\n\n    num_roots = 2\n\n    x1 = (((-b) + sqrt(r))/(2*a))     \n\n    x2 = (((-b) - sqrt(r))/(2*a))\n\n    print(\"There are 2 roots: %f and %f\" % (x1, x2))\n\nelif r == 0:\n\n    num_roots = 1\n\n    x = (-b) / 2*a\n\n    print(\"There is one root: \", x)\n\nelse:\n\n    num_roots = 0\n\n    print(\"No roots, discriminant < 0.\")\n\n    exit()', 0, 1, NULL, '2023-04-03 21:21:18', '2023-04-03 21:21:18', 1),
(334, 'Write a Python program to convert a decimal number to a binary number.', 'write-a-python-program-to-convert-a-decimal-number-to-a-binary-number', '<p>b_num = list(input(\"Input a binary number: \"))</p><p>value = 0</p><p><br></p><p>for i in range(len(b_num)):</p><p><span style=\"white-space:pre\">	</span>digit = b_num.pop()</p><p><span style=\"white-space:pre\">	</span>if digit == \'1\':</p><p><span style=\"white-space:pre\">		</span>value = value + pow(2, i)</p><p>print(\"The decimal value of the number is\", value)</p><div><br></div>', 'b_num = list(input(\"Input a binary number: \"))\n\nvalue = 0\n\n\n\nfor i in range(len(b_num)):\n\n	digit = b_num.pop()\n\n	if digit == \'1\':\n\n		value = value + pow(2, i)\n\nprint(\"The decimal value of the number is\", value)', 0, 1, NULL, '2023-04-03 21:22:00', '2023-04-03 21:22:00', 1),
(335, 'Write a Python program to print a complex number and its real and imaginary parts.', 'write-a-python-program-to-print-a-complex-number-and-its-real-and-imaginary-parts', '<p>#Initialize a complex number</p><p>cn = complex(2,3)</p><p>print(\"Complex Number: \",cn)</p><p>print(\"Complex Number - Real part: \",cn.real)</p><p>print(\"Complex Number - Imaginary part: \",cn.imag)</p><div><br></div>', '#Initialize a complex number\n\ncn = complex(2,3)\n\nprint(\"Complex Number: \",cn)\n\nprint(\"Complex Number - Real part: \",cn.real)\n\nprint(\"Complex Number - Imaginary part: \",cn.imag)', 0, 0, NULL, '2023-04-03 21:23:10', '2023-04-03 21:23:10', 1),
(336, 'Write a Python program to add, subtract, multiply, and divide two complex numbers.', 'write-a-python-program-to-add-subtract-multiply-and-divide-two-complex-numbers', '<p>print(\"Addition of two complex numbers : \",(4+3j)+(3-7j))</p><p>print(\"Subtraction of two complex numbers : \",(4+3j)-(3-7j))</p><p>print(\"Multiplication of two complex numbers : \",(4+3j)*(3-7j))</p><p>print(\"Division of two complex numbers : \",(4+3j)/(3-7j))</p><div><br></div>', 'print(\"Addition of two complex numbers : \",(4+3j)+(3-7j))\n\nprint(\"Subtraction of two complex numbers : \",(4+3j)-(3-7j))\n\nprint(\"Multiplication of two complex numbers : \",(4+3j)*(3-7j))\n\nprint(\"Division of two complex numbers : \",(4+3j)/(3-7j))\n\n', 0, 0, NULL, '2023-04-03 21:23:49', '2023-04-03 21:23:49', 1),
(337, 'Write a Python program to get the length and the angle of a complex number.', 'write-a-python-program-to-get-the-length-and-the-angle-of-a-complex-number', '<p>import cmath</p><p>cn = complex(3,4)</p><p>#length of a complex number.&nbsp;</p><p>print(\"Length of a complex number: \", abs(cn))</p><p># gets angle. return in radians, between&nbsp; [-π, π]</p><p>print(\"Complex number Angle: \",cmath.phase(0+1j) )</p><div><br></div>', 'import cmath\n\ncn = complex(3,4)\n\n#length of a complex number. \n\nprint(\"Length of a complex number: \", abs(cn))\n\n# gets angle. return in radians, between  [-π, π]\n\nprint(\"Complex number Angle: \",cmath.phase(0+1j) )', 0, 0, NULL, '2023-04-03 21:24:28', '2023-04-03 21:24:28', 1),
(338, 'Write a Python program to convert Polar coordinates to rectangular coordinates.', 'write-a-python-program-to-convert-polar-coordinates-to-rectangular-coordinates', '<p>import cmath</p><p>cn = complex(3,4)</p><p># get polar coordinates</p><p>print(\"Polar Coordinates: \",cmath.polar(cn))</p><p><br></p><p># polar to rectangular. Format for input is (length, ‹angle in radians›).</p><p>#Returns a complex number</p><p>cn1 = cmath.rect(2, cmath.pi)</p><p>print(\"Polar to rectangular: \",cn1)</p><div><br></div>', 'import cmath\n\ncn = complex(3,4)\n\n# get polar coordinates\n\nprint(\"Polar Coordinates: \",cmath.polar(cn))\n\n\n\n# polar to rectangular. Format for input is (length, ‹angle in radians›).\n\n#Returns a complex number\n\ncn1 = cmath.rect(2, cmath.pi)\n\nprint(\"Polar to rectangular: \",cn1)\n\n', 0, 1, NULL, '2023-04-03 21:25:22', '2023-04-03 21:25:22', 1),
(339, 'Write a Python program to find the maximum and minimum numbers from the specified decimal numbers.', 'write-a-python-program-to-find-the-maximum-and-minimum-numbers-from-the-specified-decimal-numbers', '<p>from decimal import *</p><p>data = list(map(Decimal, \'2.45 2.69 2.45 3.45 2.00 0.04 7.25\'.split()))</p><p>print(\"Maximum: \", max(data))</p><p>print(\"Minimum: \", min(data))</p><div><br></div>', 'from decimal import *\n\ndata = list(map(Decimal, \'2.45 2.69 2.45 3.45 2.00 0.04 7.25\'.split()))\n\nprint(\"Maximum: \", max(data))\n\nprint(\"Minimum: \", min(data))', 0, 0, NULL, '2023-04-03 21:26:10', '2023-04-03 21:26:10', 1),
(340, 'Write a Python program to find the sum of the following decimal numbers and display the numbers in sorted order.', 'write-a-python-program-to-find-the-sum-of-the-following-decimal-numbers-and-display-the-numbers-in-sorted-order', '<p>from decimal import *</p><p>data = list(map(Decimal, \'2.45 2.69 2.45 3.45 2.00 0.04 7.25\'.split()))</p><p>print(\"Sum: \", sum(data))</p><p>print(\"Sorted order: \", sorted(data))</p><div><br></div>', 'from decimal import *\n\ndata = list(map(Decimal, \'2.45 2.69 2.45 3.45 2.00 0.04 7.25\'.split()))\n\nprint(\"Sum: \", sum(data))\n\nprint(\"Sorted order: \", sorted(data))', 0, 0, NULL, '2023-04-03 21:26:38', '2023-04-03 21:26:38', 1),
(341, 'Write a Python program to get the square root and exponential of a given decimal number.', 'write-a-python-program-to-get-the-square-root-and-exponential-of-a-given-decimal-number', '<p>from decimal import *</p><p>x = Decimal(\'1.44\')</p><p>print(\"Square root of \",x, \" is :\", x.sqrt())</p><p>print(\"exponential of \",x, \" is :\", x.exp())</p><div><br></div>', 'from decimal import *\n\nx = Decimal(\'1.44\')\n\nprint(\"Square root of \",x, \" is :\", x.sqrt())\n\nprint(\"exponential of \",x, \" is :\", x.exp())', 0, 0, NULL, '2023-04-03 21:27:06', '2023-04-03 21:27:06', 1),
(342, 'Write a Python program to retrieve the current global context (public properties) for all decimal.', 'write-a-python-program-to-retrieve-the-current-global-context-public-properties-for-all-decimal', '<p>import decimal</p><p>context = decimal.getcontext()</p><p>print(\'Emax&nbsp; &nbsp; &nbsp;=\', context.Emax)</p><p>print(\'Emin&nbsp; &nbsp; &nbsp;=\', context.Emin)</p><p>print(\'capitals =\', context.capitals)</p><p>print(\'prec&nbsp; &nbsp; &nbsp;=\', context.prec)</p><p>print(\'rounding =\', context.rounding)</p><p>print(\'flags&nbsp; &nbsp; =\')</p><p>for x, y in context.flags.items():</p><p>&nbsp; &nbsp; print(\'&nbsp; {}: {}\'.format(x, y))</p><p>print(\'traps&nbsp; &nbsp; =\')</p><p>for x, y in context.traps.items():</p><p>&nbsp; &nbsp; print(\'&nbsp; {}: {}\'.format(x, y))</p><div><br></div>', 'import decimal\n\ncontext = decimal.getcontext()\n\nprint(\'Emax     =\', context.Emax)\n\nprint(\'Emin     =\', context.Emin)\n\nprint(\'capitals =\', context.capitals)\n\nprint(\'prec     =\', context.prec)\n\nprint(\'rounding =\', context.rounding)\n\nprint(\'flags    =\')\n\nfor x, y in context.flags.items():\n\n    print(\'  {}: {}\'.format(x, y))\n\nprint(\'traps    =\')\n\nfor x, y in context.traps.items():\n\n    print(\'  {}: {}\'.format(x, y))', 0, 0, NULL, '2023-04-03 21:27:30', '2023-04-03 21:27:30', 1),
(343, 'Write a Python program to round a specified decimal by setting precision (between 1 and 4).', 'write-a-python-program-to-round-a-specified-decimal-by-setting-precision-between-1-and-4', '<p>import decimal</p><p><br></p><p>d = decimal.Decimal(\'00.26598\')</p><p>print(\"Original Number : \",d)</p><p>for i in range(1, 5):</p><p>&nbsp; &nbsp; decimal.getcontext().prec = i</p><p>&nbsp; &nbsp; print(\"Precision-\",i, \':\', d * 1)</p><div><br></div>', 'import decimal\n\n\n\nd = decimal.Decimal(\'00.26598\')\n\nprint(\"Original Number : \",d)\n\nfor i in range(1, 5):\n\n    decimal.getcontext().prec = i\n\n    print(\"Precision-\",i, \':\', d * 1)', 0, 0, NULL, '2023-04-03 21:27:54', '2023-04-03 21:27:54', 1),
(344, 'Write a Python program to round a specified number upwards towards infinity and down towards negative infinity with precision 4.', 'write-a-python-program-to-round-a-specified-number-upwards-towards-infinity-and-down-towards-negative-infinity-with-precision-4', '<p>import decimal</p><p>context = decimal.getcontext()</p><p>value = decimal.Decimal(1) / decimal.Decimal(17)</p><p>print(\"1/17 = \",value)</p><p>context.prec = 4</p><p>print(\"Precision: \",4)</p><p><br></p><p>context.rounding = getattr(decimal, \'ROUND_CEILING\')</p><p>value = decimal.Decimal(1) / decimal.Decimal(17)</p><p>print(\"Round upwards towards infinity: \",value)</p><p><br></p><p>context.rounding = getattr(decimal, \'ROUND_FLOOR\')</p><p>value = decimal.Decimal(1) / decimal.Decimal(17)</p><p>print(\"Round down towards negative infinity: \",value)</p><div><br></div>', 'import decimal\n\ncontext = decimal.getcontext()\n\nvalue = decimal.Decimal(1) / decimal.Decimal(17)\n\nprint(\"1/17 = \",value)\n\ncontext.prec = 4\n\nprint(\"Precision: \",4)\n\n\n\ncontext.rounding = getattr(decimal, \'ROUND_CEILING\')\n\nvalue = decimal.Decimal(1) / decimal.Decimal(17)\n\nprint(\"Round upwards towards infinity: \",value)\n\n\n\ncontext.rounding = getattr(decimal, \'ROUND_FLOOR\')\n\nvalue = decimal.Decimal(1) / decimal.Decimal(17)\n\nprint(\"Round down towards negative infinity: \",value)', 0, 0, NULL, '2023-04-03 21:28:50', '2023-04-03 21:28:50', 1),
(345, 'Write a Python program to get the local and default precision.', 'write-a-python-program-to-get-the-local-and-default-precision', '<p>import decimal</p><p>with decimal.localcontext() as context:</p><p>&nbsp; &nbsp; context.prec = 2</p><p>&nbsp; &nbsp; print(\'Local precision:\', context.prec)</p><p>&nbsp; &nbsp; print(\'22/7 =\', (decimal.Decimal(\'22\') / 7))</p><p><br></p><p>print()</p><p>print(\'Default precision:\', decimal.getcontext().prec)</p><p>print(\'22 /7 =\', (decimal.Decimal(\'22\') / 7))</p><div><br></div>', 'import decimal\n\nwith decimal.localcontext() as context:\n\n    context.prec = 2\n\n    print(\'Local precision:\', context.prec)\n\n    print(\'22/7 =\', (decimal.Decimal(\'22\') / 7))\n\n\n\nprint()\n\nprint(\'Default precision:\', decimal.getcontext().prec)\n\nprint(\'22 /7 =\', (decimal.Decimal(\'22\') / 7))\n\n', 0, 0, NULL, '2023-04-03 21:29:15', '2023-04-03 21:29:15', 1),
(346, 'Write a Python program to display fraction instances of the string representation of a number.', 'write-a-python-program-to-display-fraction-instances-of-the-string-representation-of-a-number', '<p>import fractions</p><p><br></p><p>for s in [\'0.7\', \'2.5\', \'9.32\', \'7e-1\']:</p><p>&nbsp; &nbsp; f = fractions.Fraction(s)</p><p>&nbsp; &nbsp; print(\'{0:&gt;4} = {1}\'.format(s, f))</p><div><br></div>', 'import fractions\n\n\n\nfor s in [\'0.7\', \'2.5\', \'9.32\', \'7e-1\']:\n\n    f = fractions.Fraction(s)\n\n    print(\'{0:>4} = {1}\'.format(s, f))', 0, 0, NULL, '2023-04-03 21:29:35', '2023-04-03 21:29:35', 1),
(347, 'Write a Python program to create fraction instances of float numbers.', 'write-a-python-program-to-create-fraction-instances-of-float-numbers', '<p>import fractions</p><p>for n in [0.2, 0.7, 6.5, 6.0]:</p><p>&nbsp; &nbsp; print(\'{} = {}\'.format(n, fractions.Fraction(n)))</p><p><span style=\"white-space:pre\">	</span></p>', 'import fractions\n\nfor n in [0.2, 0.7, 6.5, 6.0]:\n\n    print(\'{} = {}\'.format(n, fractions.Fraction(n)))', 0, 0, NULL, '2023-04-03 21:30:18', '2023-04-03 21:30:18', 1),
(348, 'Write a Python program to create fraction instances of decimal numbers.', 'write-a-python-program-to-create-fraction-instances-of-decimal-numbers', '<p>import decimal</p><p>import fractions</p><p>values = [</p><p>&nbsp; &nbsp; decimal.Decimal(\'0.2\'),</p><p>&nbsp; &nbsp; decimal.Decimal(\'0.7\'),</p><p>&nbsp; &nbsp; decimal.Decimal(\'2.5\'),</p><p>&nbsp; &nbsp; decimal.Decimal(\'3.0\'),</p><p>]</p><p><br></p><p>for d in values:</p><p>&nbsp; &nbsp; print(\'{} = {}\'.format(d, fractions.Fraction(d)))</p><p><span style=\"white-space:pre\">	</span></p>', 'import decimal\n\nimport fractions\n\nvalues = [\n\n    decimal.Decimal(\'0.2\'),\n\n    decimal.Decimal(\'0.7\'),\n\n    decimal.Decimal(\'2.5\'),\n\n    decimal.Decimal(\'3.0\'),\n\n]\n\n\n\nfor d in values:\n\n    print(\'{} = {}\'.format(d, fractions.Fraction(d)))', 0, 0, NULL, '2023-04-03 21:30:42', '2023-04-03 21:30:42', 1),
(349, 'Write a Python program to add, subtract, multiply and divide two fractions.', 'write-a-python-program-to-add-subtract-multiply-and-divide-two-fractions', '<p>import fractions</p><p><br></p><p>f1 = fractions.Fraction(2, 3)</p><p>f2 = fractions.Fraction(3, 7)</p><p><br></p><p>print(\'{} + {} = {}\'.format(f1, f2, f1 + f2))</p><p>print(\'{} - {} = {}\'.format(f1, f2, f1 - f2))</p><p>print(\'{} * {} = {}\'.format(f1, f2, f1 * f2))</p><p>print(\'{} / {} = {}\'.format(f1, f2, f1 / f2))</p><div><br></div>', 'import fractions\n\n\n\nf1 = fractions.Fraction(2, 3)\n\nf2 = fractions.Fraction(3, 7)\n\n\n\nprint(\'{} + {} = {}\'.format(f1, f2, f1 + f2))\n\nprint(\'{} - {} = {}\'.format(f1, f2, f1 - f2))\n\nprint(\'{} * {} = {}\'.format(f1, f2, f1 * f2))\n\nprint(\'{} / {} = {}\'.format(f1, f2, f1 / f2))\n\n', 0, 0, NULL, '2023-04-03 21:31:20', '2023-04-03 21:31:20', 1),
(350, 'Write a Python program to convert a floating point number (PI) to an approximate rational value on the various denominators.\nNote: max_denominator = 1000000', 'write-a-python-program-to-convert-a-floating-point-number-pi-to-an-approximate-rational-value-on-the-various-denominators-note-max-denominator-1000000', '<p>import fractions</p><p>import math</p><p><br></p><p>print(\'PI&nbsp; &nbsp; &nbsp; &nbsp;=\', math.pi)</p><p><br></p><p>f_pi = fractions.Fraction(str(math.pi))</p><p>print(\'No limit =\', f_pi)</p><p><br></p><p>for d in [1, 5,&nbsp; 50, 90, 100, 500, 1000000]:</p><p>&nbsp; &nbsp; limited = f_pi.limit_denominator(d)</p><p>&nbsp; &nbsp; print(\'{0:8} = {1}\'.format(d, limited))</p><div><br></div>', 'import fractions\n\nimport math\n\n\n\nprint(\'PI       =\', math.pi)\n\n\n\nf_pi = fractions.Fraction(str(math.pi))\n\nprint(\'No limit =\', f_pi)\n\n\n\nfor d in [1, 5,  50, 90, 100, 500, 1000000]:\n\n    limited = f_pi.limit_denominator(d)\n\n    print(\'{0:8} = {1}\'.format(d, limited))', 0, 0, NULL, '2023-04-03 21:32:11', '2023-04-03 21:32:11', 1),
(351, 'Write a Python program to generate random floating numbers in a specific numerical range.', 'write-a-python-program-to-generate-random-floating-numbers-in-a-specific-numerical-range', '<p>import random</p><p>for x in range(6):</p><p>&nbsp; &nbsp; print(\'{:04.3f}\'.format(random.uniform(x, 100)), end=\' \')</p><div><br></div>', 'import random\n\nfor x in range(6):\n\n    print(\'{:04.3f}\'.format(random.uniform(x, 100)), end=\' \')', 0, 0, NULL, '2023-04-03 21:32:32', '2023-04-03 21:32:32', 1),
