(355, 'Write a Python program to shuffle the following elements randomly.', 'write-a-python-program-to-shuffle-the-following-elements-randomly', '<p>import random</p><p><br></p><p>nums = [1, 2, 3, 4, 5, 6, 7]</p><p>random.shuffle(nums)</p><p>print(nums)</p><div><br></div>', 'import random\n\n\n\nnums = [1, 2, 3, 4, 5, 6, 7]\n\nrandom.shuffle(nums)\n\nprint(nums)', 0, 0, NULL, '2023-04-03 22:41:11', '2023-04-03 22:41:11', 1),
(356, 'Write a Python program to flip a coin 1000 times and count heads and tails.', 'write-a-python-program-to-flip-a-coin-1000-times-and-count-heads-and-tails', '<p>import random</p><p>import itertools</p><p><br></p><p>results = {</p><p>&nbsp; &nbsp; \'heads\': 0,</p><p>&nbsp; &nbsp; \'tails\': 0,</p><p>}</p><p>sides = list(results.keys())</p><p><br></p><p>for i in range(10000):</p><p>&nbsp; &nbsp; results[random.choice(sides)] += 1</p><p><br></p><p>print(\'Heads:\', results[\'heads\'])</p><p>print(\'Tails:\', results[\'tails\'])</p><div><br></div>', 'import random\n\nimport itertools\n\n\n\nresults = {\n\n    \'heads\': 0,\n\n    \'tails\': 0,\n\n}\n\nsides = list(results.keys())\n\n\n\nfor i in range(10000):\n\n    results[random.choice(sides)] += 1\n\n\n\nprint(\'Heads:\', results[\'heads\'])\n\nprint(\'Tails:\', results[\'tails\'])', 0, 0, NULL, '2023-04-03 22:41:59', '2023-04-03 22:41:59', 1),
(357, 'Write a Python program to print a random sample of words from the system dictionary.', 'write-a-python-program-to-print-a-random-sample-of-words-from-the-system-dictionary', '<p>import random</p><p>with open(\'/usr/share/dict/words\', \'rt\') as fh:</p><p>&nbsp; &nbsp; words = fh.readlines()</p><p>words = [w.rstrip() for w in words]</p><p>for w in random.sample(words, 7):</p><p>&nbsp; &nbsp; print(w)</p><p><span style=\"white-space:pre\">	</span></p>', 'import random\n\nwith open(\'/usr/share/dict/words\', \'rt\') as fh:\n\n    words = fh.readlines()\n\nwords = [w.rstrip() for w in words]\n\nfor w in random.sample(words, 7):\n\n    print(w)', 0, 0, NULL, '2023-04-03 22:42:48', '2023-04-03 22:42:48', 1),
(358, 'Write a Python program to randomly select an item from a list.', 'write-a-python-program-to-randomly-select-an-item-from-a-list', '<p>import random</p><p>color = [\'Red\', \'Green\', \'Black\', \'Orange\', \'Black\']</p><p>print(random.choice(color))</p><div><br></div>', 'import random\n\ncolor = [\'Red\', \'Green\', \'Black\', \'Orange\', \'Black\']\n\nprint(random.choice(color))\n\n', 0, 0, NULL, '2023-04-03 22:43:35', '2023-04-03 22:43:35', 1),
(359, 'Write a Python program to calculate the absolute value of a floating point number.', 'write-a-python-program-to-calculate-the-absolute-value-of-a-floating-point-number', '<p>import math</p><p><br></p><p>print(math.fabs(-2.1))</p><p>print(math.fabs(-0.0))</p><p>print(math.fabs(10.1))</p><p>print(math.fabs(0.0))</p><div><br></div>', 'import math\n\n\n\nprint(math.fabs(-2.1))\n\nprint(math.fabs(-0.0))\n\nprint(math.fabs(10.1))\n\nprint(math.fabs(0.0))\n\n', 0, 0, NULL, '2023-04-03 22:44:15', '2023-04-03 22:44:15', 1),
(360, 'Write a Python program to calculate the standard deviation of the following data.', 'write-a-python-program-to-calculate-the-standard-deviation-of-the-following-data', '<p>import math</p><p>import sys</p><p><br></p><p>def sd_calc(data):</p><p>&nbsp; &nbsp; n = len(data)</p><p><br></p><p>&nbsp; &nbsp; if n &lt;= 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return 0.0</p><p><br></p><p>&nbsp; &nbsp; mean, sd = avg_calc(data), 0.0</p><p><br></p><p>&nbsp; &nbsp; # calculate stan. dev.</p><p>&nbsp; &nbsp; for el in data:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; sd += (float(el) - mean)**2</p><p>&nbsp; &nbsp; sd = math.sqrt(sd / float(n-1))</p><p><br></p><p>&nbsp; &nbsp; return sd</p><p><br></p><p>def avg_calc(ls):</p><p>&nbsp; &nbsp; n, mean = len(ls), 0.0</p><p><br></p><p>&nbsp; &nbsp; if n &lt;= 1:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return ls[0]</p><p><br></p><p>&nbsp; &nbsp; # calculate average</p><p>&nbsp; &nbsp; for el in ls:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; mean = mean + float(el)</p><p>&nbsp; &nbsp; mean = mean / float(n)</p><p><br></p><p>&nbsp; &nbsp; return mean</p><p><br></p><p>data = [4, 2, 5, 8, 6]</p><p>print(\"Sample Data: \",data)</p><p>print(\"Standard Deviation : \",sd_calc(data))</p><div><br></div>', 'import math\n\nimport sys\n\n\n\ndef sd_calc(data):\n\n    n = len(data)\n\n\n\n    if n <= 1:\n\n        return 0.0\n\n\n\n    mean, sd = avg_calc(data), 0.0\n\n\n\n    # calculate stan. dev.\n\n    for el in data:\n\n        sd += (float(el) - mean)**2\n\n    sd = math.sqrt(sd / float(n-1))\n\n\n\n    return sd\n\n\n\ndef avg_calc(ls):\n\n    n, mean = len(ls), 0.0\n\n\n\n    if n <= 1:\n\n        return ls[0]\n\n\n\n    # calculate average\n\n    for el in ls:\n\n        mean = mean + float(el)\n\n    mean = mean / float(n)\n\n\n\n    return mean\n\n\n\ndata = [4, 2, 5, 8, 6]\n\nprint(\"Sample Data: \",data)\n\nprint(\"Standard Deviation : \",sd_calc(data))\n\n', 0, 0, NULL, '2023-04-03 22:44:50', '2023-04-03 22:44:50', 1),
(361, 'Write a Python program to print the floating point of the mantissa, exponent pair.', 'write-a-python-program-to-print-the-floating-point-of-the-mantissa-exponent-pair', '<p>import math</p><p><br></p><p>print(\'{:^7}&nbsp; {:^8}&nbsp; {:^17}\'.format(\'Mantissa\', \'Exponent\', \'Floating point value\'))</p><p>print(\'{:-^8}&nbsp; {:-^8}&nbsp; {:-^20}\'.format(\'\', \'\', \'\'))</p><p><br></p><p>for m, e in [ (0.7, -3),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (0.3,&nbsp; 0),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (0.5,&nbsp; 3),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ]:</p><p>&nbsp; &nbsp; x = math.ldexp(m, e)</p><p>&nbsp; &nbsp; print(\'{:7.2f}&nbsp; {:7d}&nbsp; {:7.2f}\'.format(m, e, x))</p><p><br></p>', 'import math\n\n\n\nprint(\'{:^7}  {:^8}  {:^17}\'.format(\'Mantissa\', \'Exponent\', \'Floating point value\'))\n\nprint(\'{:-^8}  {:-^8}  {:-^20}\'.format(\'\', \'\', \'\'))\n\n\n\nfor m, e in [ (0.7, -3),\n\n              (0.3,  0),\n\n              (0.5,  3),\n\n              ]:\n\n    x = math.ldexp(m, e)\n\n    print(\'{:7.2f}  {:7d}  {:7.2f}\'.format(m, e, x))\n\n', 0, 0, NULL, '2023-04-03 22:45:26', '2023-04-03 22:45:26', 1),
(362, 'Write a Python program to split the fractional and integer parts of a floating point number.', 'write-a-python-program-to-split-the-fractional-and-integer-parts-of-a-floating-point-number', '<p>import math</p><p>print(\'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;(F)&nbsp; (I)\')</p><p>for i in range(6):</p><p>&nbsp; &nbsp; print(\'{}/2 = {} {}\'.format(i, i/2, math.modf(i/2.0)))</p><p><span style=\"white-space:pre\">	</span></p>', 'import math\n\nprint(\'           (F)  (I)\')\n\nfor i in range(6):\n\n    print(\'{}/2 = {} {}\'.format(i, i/2, math.modf(i/2.0)))\n\n	', 0, 0, NULL, '2023-04-03 22:46:14', '2023-04-03 22:46:14', 1),
(363, 'Write a Python program to parse math formulas and put parentheses around multiplication and division.', 'write-a-python-program-to-parse-math-formulas-and-put-parentheses-around-multiplication-and-division', '<p>#https://gist.github.com/ohaz/ed0b14a487b0569aad2d</p><p>import ast</p><p><br></p><p>def recurse(node):</p><p>&nbsp; &nbsp; if isinstance(node, ast.BinOp):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(node.op, ast.Mult) or isinstance(node.op, ast.Div):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; print(\'(\', end=\'\')</p><p>&nbsp; &nbsp; &nbsp; &nbsp; recurse(node.left)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; recurse(node.op)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; recurse(node.right)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(node.op, ast.Mult) or isinstance(node.op, ast.Div):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; print(\')\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Add):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'+\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Sub):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'-\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Mult):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'*\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Div):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(\'/\', end=\'\')</p><p>&nbsp; &nbsp; elif isinstance(node, ast.Num):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; print(node.n, end=\'\')</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; for child in ast.iter_child_nodes(node):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; recurse(child)</p><p><br></p><p><br></p><p>def search_expr(node):</p><p>&nbsp; &nbsp; returns = []</p><p>&nbsp; &nbsp; for child in ast.iter_child_nodes(node):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(child, ast.Expr):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return child</p><p>&nbsp; &nbsp; &nbsp; &nbsp; returns.append(search_expr(child))</p><p>&nbsp; &nbsp; for ret in returns:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if isinstance(ret, ast.Expr):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return ret</p><p>&nbsp; &nbsp; return None</p><p><br></p><p><br></p><p>formula = \'4+5*7/2\'</p><p><br></p><p>a = ast.parse(formula)</p><p><br></p><p>expr = search_expr(a)</p><p>if expr is not None:</p><p>&nbsp; &nbsp; recurse(expr)</p><p>print()</p><div><br></div>', '#https://gist.github.com/ohaz/ed0b14a487b0569aad2d\n\nimport ast\n\n\n\ndef recurse(node):\n\n    if isinstance(node, ast.BinOp):\n\n        if isinstance(node.op, ast.Mult) or isinstance(node.op, ast.Div):\n\n            print(\'(\', end=\'\')\n\n        recurse(node.left)\n\n        recurse(node.op)\n\n        recurse(node.right)\n\n        if isinstance(node.op, ast.Mult) or isinstance(node.op, ast.Div):\n\n            print(\')\', end=\'\')\n\n    elif isinstance(node, ast.Add):\n\n        print(\'+\', end=\'\')\n\n    elif isinstance(node, ast.Sub):\n\n        print(\'-\', end=\'\')\n\n    elif isinstance(node, ast.Mult):\n\n        print(\'*\', end=\'\')\n\n    elif isinstance(node, ast.Div):\n\n        print(\'/\', end=\'\')\n\n    elif isinstance(node, ast.Num):\n\n        print(node.n, end=\'\')\n\n    else:\n\n        for child in ast.iter_child_nodes(node):\n\n            recurse(child)\n\n\n\n\n\ndef search_expr(node):\n\n    returns = []\n\n    for child in ast.iter_child_nodes(node):\n\n        if isinstance(child, ast.Expr):\n\n            return child\n\n        returns.append(search_expr(child))\n\n    for ret in returns:\n\n        if isinstance(ret, ast.Expr):\n\n            return ret\n\n    return None\n\n\n\n\n\nformula = \'4+5*7/2\'\n\n\n\na = ast.parse(formula)\n\n\n\nexpr = search_expr(a)\n\nif expr is not None:\n\n    recurse(expr)\n\nprint()', 0, 0, NULL, '2023-04-03 22:46:59', '2023-04-03 22:46:59', 1),
(364, 'Write a Python program to describe linear regression.', 'write-a-python-program-to-describe-linear-regression', '<p>#https://gist.github.com/cartr/6513044</p><p># Define the data</p><p>data = set()</p><p>count = int(input(\"Enter the number of data points: \"))</p><p>for i in range(count):</p><p>&nbsp; &nbsp; x=float(input(\"X\"+str(i+1)+\": \"))</p><p>&nbsp; &nbsp; y=float(input(\"Y\"+str(i+1)+\": \"))</p><p>&nbsp; &nbsp; data.add((x,y))</p><p><br></p><p># Find the average x and y</p><p>avgx = 0.0</p><p>avgy = 0.0</p><p>for i in data:</p><p>&nbsp; &nbsp; avgx += i[0]/len(data)</p><p>&nbsp; &nbsp; avgy += i[1]/len(data)</p><p><br></p><p># Find the sums</p><p>totalxx = 0</p><p>totalxy = 0</p><p><br></p><p>for i in data:</p><p>&nbsp; &nbsp; totalxx += (i[0]-avgx)**2</p><p>&nbsp; &nbsp; totalxy += (i[0]-avgx)*(i[1]-avgy)</p><p><br></p><p># Slope/intercept form</p><p>m = totalxy/totalxx</p><p>b = avgy-m*avgx</p><p><br></p><p>print(\"Best fit line:\")</p><p>print(\"y = \"+str(m)+\"x + \"+str(b))</p><p><br></p><p>x = float(input(\"Enter a value to calculate: \"))</p><p>print(\"y = \"+str(m*x+b))</p><div><br></div>', '#https://gist.github.com/cartr/6513044\n\n# Define the data\n\ndata = set()\n\ncount = int(input(\"Enter the number of data points: \"))\n\nfor i in range(count):\n\n    x=float(input(\"X\"+str(i+1)+\": \"))\n\n    y=float(input(\"Y\"+str(i+1)+\": \"))\n\n    data.add((x,y))\n\n\n\n# Find the average x and y\n\navgx = 0.0\n\navgy = 0.0\n\nfor i in data:\n\n    avgx += i[0]/len(data)\n\n    avgy += i[1]/len(data)\n\n\n\n# Find the sums\n\ntotalxx = 0\n\ntotalxy = 0\n\n\n\nfor i in data:\n\n    totalxx += (i[0]-avgx)**2\n\n    totalxy += (i[0]-avgx)*(i[1]-avgy)\n\n\n\n# Slope/intercept form\n\nm = totalxy/totalxx\n\nb = avgy-m*avgx\n\n\n\nprint(\"Best fit line:\")\n\nprint(\"y = \"+str(m)+\"x + \"+str(b))\n\n\n\nx = float(input(\"Enter a value to calculate: \"))\n\nprint(\"y = \"+str(m*x+b))', 0, 1, NULL, '2023-04-03 22:48:04', '2023-04-03 22:48:04', 1),
(365, 'Write a Python program to calculate a grid of hexagon coordinates of the given radius given lower-left and upper-right coordinates. The function will return a list of lists containing 6 tuples of x, y point coordinates.', 'write-a-python-program-to-calculate-a-grid-of-hexagon-coordinates-of-the-given-radius-given-lower-left-and-upper-right-coordinates-the-function-will-return-a-list-of-lists-containing-6-tuples-of-x-y-point-coordinates', '<p>#https://gist.github.com/urschrei/17cf0be92ca90a244a91</p><p>import math</p><p>def calculate_polygons(startx, starty, endx, endy, radius):</p><p>&nbsp; &nbsp; &nbsp;# calculate side length given radius&nbsp; &nbsp;</p><p>&nbsp; &nbsp; sl = (2 * radius) * math.tan(math.pi / 6)</p><p>&nbsp; &nbsp; # calculate radius for a given side-length</p><p>&nbsp; &nbsp; # (a * (math.cos(math.pi / 6) / math.sin(math.pi / 6)) / 2)</p><p>&nbsp; &nbsp; # see http://www.calculatorsoup.com/calculators/geometry-plane/polygon.php</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; # calculate coordinates of the hexagon points</p><p>&nbsp; &nbsp; # sin(30)<span style=\"white-space:pre\">	</span></p><p>&nbsp; &nbsp; p = sl * 0.5</p><p>&nbsp; &nbsp; b = sl * math.cos(math.radians(30))</p><p>&nbsp; &nbsp; w = b * 2</p><p>&nbsp; &nbsp; h = 2 * sl</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; # offset start and end coordinates by hex widths and heights to guarantee coverage&nbsp; &nbsp; &nbsp;</p><p>&nbsp; &nbsp; startx = startx - w</p><p>&nbsp; &nbsp; starty = starty - h</p><p>&nbsp; &nbsp; endx = endx + w</p><p>&nbsp; &nbsp; endy = endy + h</p><p><br></p><p>&nbsp; &nbsp; origx = startx</p><p>&nbsp; &nbsp; origy = starty</p><p><br></p><p><br></p><p>&nbsp; &nbsp; # offsets for moving along and up rows</p><p>&nbsp; &nbsp; xoffset = b</p><p>&nbsp; &nbsp; yoffset = 3 * p</p><p><br></p><p>&nbsp; &nbsp; polygons = []</p><p>&nbsp; &nbsp; row = 1</p><p>&nbsp; &nbsp; counter = 0</p><p><br></p><p>&nbsp; &nbsp; while starty &lt; endy:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if row % 2 == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startx = origx + xoffset</p><p>&nbsp; &nbsp; &nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startx = origx</p><p>&nbsp; &nbsp; &nbsp; &nbsp; while startx &lt; endx:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p1x = startx</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p1y = starty + p</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p2x = startx</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p2y = starty + (3 * p)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p3x = startx + b</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p3y = starty + h</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p4x = startx + w</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p4y = starty + (3 * p)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p5x = startx + w</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p5y = starty + p</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p6x = startx + b</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; p6y = starty</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; poly = [</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p1x, p1y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p2x, p2y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p3x, p3y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p4x, p4y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p5x, p5y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p6x, p6y),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (p1x, p1y)]</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; polygons.append(poly)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; counter += 1</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; startx += w</p><p>&nbsp; &nbsp; &nbsp; &nbsp; starty += yoffset</p><p>&nbsp; &nbsp; &nbsp; &nbsp; row += 1</p><p>&nbsp; &nbsp; return polygons</p><p><br></p><p>print(calculate_polygons(1,1, 4, 4, 3))</p><div><br></div>', '#https://gist.github.com/urschrei/17cf0be92ca90a244a91\n\nimport math\n\ndef calculate_polygons(startx, starty, endx, endy, radius):\n\n     # calculate side length given radius   \n\n    sl = (2 * radius) * math.tan(math.pi / 6)\n\n    # calculate radius for a given side-length\n\n    # (a * (math.cos(math.pi / 6) / math.sin(math.pi / 6)) / 2)\n\n    # see http://www.calculatorsoup.com/calculators/geometry-plane/polygon.php\n\n    \n\n    # calculate coordinates of the hexagon points\n\n    # sin(30)	\n\n    p = sl * 0.5\n\n    b = sl * math.cos(math.radians(30))\n\n    w = b * 2\n\n    h = 2 * sl\n\n    \n\n    # offset start and end coordinates by hex widths and heights to guarantee coverage     \n\n    startx = startx - w\n\n    starty = starty - h\n\n    endx = endx + w\n\n    endy = endy + h\n\n\n\n    origx = startx\n\n    origy = starty\n\n\n\n\n\n    # offsets for moving along and up rows\n\n    xoffset = b\n\n    yoffset = 3 * p\n\n\n\n    polygons = []\n\n    row = 1\n\n    counter = 0\n\n\n\n    while starty < endy:\n\n        if row % 2 == 0:\n\n            startx = origx + xoffset\n\n        else:\n\n            startx = origx\n\n        while startx < endx:\n\n            p1x = startx\n\n            p1y = starty + p\n\n            p2x = startx\n\n            p2y = starty + (3 * p)\n\n            p3x = startx + b\n\n            p3y = starty + h\n\n            p4x = startx + w\n\n            p4y = starty + (3 * p)\n\n            p5x = startx + w\n\n            p5y = starty + p\n\n            p6x = startx + b\n\n            p6y = starty\n\n            poly = [\n\n                (p1x, p1y),\n\n                (p2x, p2y),\n\n                (p3x, p3y),\n\n                (p4x, p4y),\n\n                (p5x, p5y),\n\n                (p6x, p6y),\n\n                (p1x, p1y)]\n\n            polygons.append(poly)\n\n            counter += 1\n\n            startx += w\n\n        starty += yoffset\n\n        row += 1\n\n    return polygons\n\n\n\nprint(calculate_polygons(1,1, 4, 4, 3))\n\n', 0, 0, NULL, '2023-04-03 22:50:03', '2023-04-03 22:50:03', 1);
