(366, 'Write a Python program to create a simple math quiz.', 'write-a-python-program-to-create-a-simple-math-quiz', '<div>import random</div><div><br></div><div><br></div><div>def display_intro():</div><div>&nbsp; &nbsp; title = \"** A Simple Math Quiz **\"</div><div>&nbsp; &nbsp; print(\"*\" * len(title))</div><div>&nbsp; &nbsp; print(title)</div><div>&nbsp; &nbsp; print(\"*\" * len(title))</div><div><br></div><div><br></div><div>def display_menu():</div><div>&nbsp; &nbsp; menu_list = [\"1. Addition\", \"2. Subtraction\", \"3. Multiplication\", \"4. Integer Division\", \"5. Exit\"]</div><div>&nbsp; &nbsp; print(menu_list[0])</div><div>&nbsp; &nbsp; print(menu_list[1])</div><div>&nbsp; &nbsp; print(menu_list[2])</div><div>&nbsp; &nbsp; print(menu_list[3])</div><div>&nbsp; &nbsp; print(menu_list[4])</div><div><br></div><div><br></div><div>def display_separator():</div><div>&nbsp; &nbsp; print(\"-\" * 24)</div><div><br></div><div><br></div><div>def get_user_input():</div><div>&nbsp; &nbsp; user_input = int(input(\"Enter your choice: \"))</div><div>&nbsp; &nbsp; while user_input &gt; 5 or user_input &lt;= 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; print(\"Invalid menu option.\")</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_input = int(input(\"Please try again: \"))</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return user_input</div><div><br></div><div><br></div><div>def get_user_solution(problem):</div><div>&nbsp; &nbsp; print(\"Enter your answer\")</div><div>&nbsp; &nbsp; print(problem, end=\"\")</div><div>&nbsp; &nbsp; result = int(input(\" = \"))</div><div>&nbsp; &nbsp; return result</div><div><br></div><div><br></div><div>def check_solution(user_solution, solution, count):</div><div>&nbsp; &nbsp; if user_solution == solution:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = count + 1</div><div>&nbsp; &nbsp; &nbsp; &nbsp; print(\"Correct.\")</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; print(\"Incorrect.\")</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div><br></div><div><br></div><div>def menu_option(index, count):</div><div>&nbsp; &nbsp; number_one = random.randrange(1, 21)</div><div>&nbsp; &nbsp; number_two = random.randrange(1, 21)</div><div>&nbsp; &nbsp; if index is 1:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" + \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one + number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; elif index is 2:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" - \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one - number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; elif index is 3:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" * \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one * number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div>&nbsp; &nbsp; else:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; problem = str(number_one) + \" // \" + str(number_two)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; solution = number_one // number_two</div><div>&nbsp; &nbsp; &nbsp; &nbsp; user_solution = get_user_solution(problem)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; count = check_solution(user_solution, solution, count)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; return count</div><div><br></div><div><br></div><div>def display_result(total, correct):</div><div>&nbsp; &nbsp; if total &gt; 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; result = correct / total</div><div>&nbsp; &nbsp; &nbsp; &nbsp; percentage = round((result * 100), 2)</div><div>&nbsp; &nbsp; if total == 0:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; percentage = 0</div><div>&nbsp; &nbsp; print(\"You answered\", total, \"questions with\", correct, \"correct.\")</div><div>&nbsp; &nbsp; print(\"Your score is \", percentage, \"%. Thank you.\", sep = \"\")</div><div><br></div><div><br></div><div>def main():</div><div>&nbsp; &nbsp; display_intro()</div><div>&nbsp; &nbsp; display_menu()</div><div>&nbsp; &nbsp; display_separator()</div><div><br></div><div>&nbsp; &nbsp; option = get_user_input()</div><div>&nbsp; &nbsp; total = 0</div><div>&nbsp; &nbsp; correct = 0</div><div>&nbsp; &nbsp; while option != 5:</div><div>&nbsp; &nbsp; &nbsp; &nbsp; total = total + 1</div><div>&nbsp; &nbsp; &nbsp; &nbsp; correct = menu_option(option, correct)</div><div>&nbsp; &nbsp; &nbsp; &nbsp; option = get_user_input()</div><div><br></div><div>&nbsp; &nbsp; print(\"Exit the quiz.\")</div><div>&nbsp; &nbsp; display_separator()</div><div>&nbsp; &nbsp; display_result(total, correct)</div><div><br></div><div>main()</div><div><br></div>', 'import random\n\n\ndef display_intro():\n    title = \"** A Simple Math Quiz **\"\n    print(\"*\" * len(title))\n    print(title)\n    print(\"*\" * len(title))\n\n\ndef display_menu():\n    menu_list = [\"1. Addition\", \"2. Subtraction\", \"3. Multiplication\", \"4. Integer Division\", \"5. Exit\"]\n    print(menu_list[0])\n    print(menu_list[1])\n    print(menu_list[2])\n    print(menu_list[3])\n    print(menu_list[4])\n\n\ndef display_separator():\n    print(\"-\" * 24)\n\n\ndef get_user_input():\n    user_input = int(input(\"Enter your choice: \"))\n    while user_input > 5 or user_input <= 0:\n        print(\"Invalid menu option.\")\n        user_input = int(input(\"Please try again: \"))\n    else:\n        return user_input\n\n\ndef get_user_solution(problem):\n    print(\"Enter your answer\")\n    print(problem, end=\"\")\n    result = int(input(\" = \"))\n    return result\n\n\ndef check_solution(user_solution, solution, count):\n    if user_solution == solution:\n        count = count + 1\n        print(\"Correct.\")\n        return count\n    else:\n        print(\"Incorrect.\")\n        return count\n\n\ndef menu_option(index, count):\n    number_one = random.randrange(1, 21)\n    number_two = random.randrange(1, 21)\n    if index is 1:\n        problem = str(number_one) + \" + \" + str(number_two)\n        solution = number_one + number_two\n        user_solution = get_user_solution(problem)\n        count = check_solution(user_solution, solution, count)\n        return count\n    elif index is 2:\n        problem = str(number_one) + \" - \" + str(number_two)\n        solution = number_one - number_two\n        user_solution = get_user_solution(problem)\n        count = check_solution(user_solution, solution, count)\n        return count\n    elif index is 3:\n        problem = str(number_one) + \" * \" + str(number_two)\n        solution = number_one * number_two\n        user_solution = get_user_solution(problem)\n        count = check_solution(user_solution, solution, count)\n        return count\n    else:\n        problem = str(number_one) + \" // \" + str(number_two)\n        solution = number_one // number_two\n        user_solution = get_user_solution(problem)\n        count = check_solution(user_solution, solution, count)\n        return count\n\n\ndef display_result(total, correct):\n    if total > 0:\n        result = correct / total\n        percentage = round((result * 100), 2)\n    if total == 0:\n        percentage = 0\n    print(\"You answered\", total, \"questions with\", correct, \"correct.\")\n    print(\"Your score is \", percentage, \"%. Thank you.\", sep = \"\")\n\n\ndef main():\n    display_intro()\n    display_menu()\n    display_separator()\n\n    option = get_user_input()\n    total = 0\n    correct = 0\n    while option != 5:\n        total = total + 1\n        correct = menu_option(option, correct)\n        option = get_user_input()\n\n    print(\"Exit the quiz.\")\n    display_separator()\n    display_result(total, correct)\n\nmain()\n', 0, 1, NULL, '2023-04-05 19:53:19', '2023-04-06 01:14:39', 1),
(367, 'Write a Python program to calculate the volume of a tetrahedron.', 'write-a-python-program-to-calculate-the-volume-of-a-tetrahedron', '<p>import math</p><p>def volume_tetrahedron(num):</p><p><span style=\"white-space:pre\">	</span>volume = (num ** 3 / (6 * math.sqrt(2)))</p><p><span style=\"white-space:pre\">	</span></p><p><span style=\"white-space:pre\">	</span>return round(volume, 2)</p><p><br></p><p>print(volume_tetrahedron(10))</p><div><br></div>', 'import math\n\ndef volume_tetrahedron(num):\n\n	volume = (num ** 3 / (6 * math.sqrt(2)))\n\n	\n\n	return round(volume, 2)\n\n\n\nprint(volume_tetrahedron(10))\n\n', 0, 0, NULL, '2023-04-05 20:00:11', '2023-04-05 20:00:11', 1),
(368, 'Write a Python program to compute the value of e(2.718281827...) using an infinite series.', 'write-a-python-program-to-compute-the-value-of-e2718281827-using-an-infinite-series', '<p>\'\'\'&nbsp;</p><p>#https://gist.github.com/thinkphp/1528363</p><p><br></p><p>&nbsp; 1 + 1/1! + 1/2! + 1/3! + ...</p><p>&nbsp; 2 + 1/2! + 1/3!+ ...</p><p>\'\'\'</p><p>import math</p><p><br></p><p>def fact(n):</p><p>&nbsp; &nbsp; if n == 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp;return 1</p><p>&nbsp; &nbsp; else:</p><p>&nbsp; &nbsp; &nbsp; &nbsp;return n*fact(n-1)</p><p><br></p><p>def e(EPS):</p><p><br></p><p>&nbsp; &nbsp; v1 = 2</p><p>&nbsp; &nbsp; v2 = v1 + float(1.0/fact(2))</p><p>&nbsp; &nbsp; i = 3&nbsp;</p><p>&nbsp; &nbsp; while math.fabs(v1-v2) &gt;= EPS:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; v1 = v2</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; v2 += float(1.0/fact(i))</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i += 1</p><p>&nbsp; &nbsp; return v2&nbsp;&nbsp;</p><p><br></p><p>print(\"The mathematical constant e\")</p><p>#computes the value of e using infinite series</p><p>print(e(0.00000001))</p><p>#mathematical constant e build-in</p><p>print(math.e)</p><div><br></div>', '\'\'\' \n\n#https://gist.github.com/thinkphp/1528363\n\n\n\n  1 + 1/1! + 1/2! + 1/3! + ...\n\n  2 + 1/2! + 1/3!+ ...\n\n\'\'\'\n\nimport math\n\n\n\ndef fact(n):\n\n    if n == 0:\n\n       return 1\n\n    else:\n\n       return n*fact(n-1)\n\n\n\ndef e(EPS):\n\n\n\n    v1 = 2\n\n    v2 = v1 + float(1.0/fact(2))\n\n    i = 3 \n\n    while math.fabs(v1-v2) >= EPS:\n\n          v1 = v2\n\n          v2 += float(1.0/fact(i))\n\n          i += 1\n\n    return v2  \n\n\n\nprint(\"The mathematical constant e\")\n\n#computes the value of e using infinite series\n\nprint(e(0.00000001))\n\n#mathematical constant e build-in\n\nprint(math.e)', 0, 0, NULL, '2023-04-05 21:26:14', '2023-04-05 21:26:14', 1),
(369, 'Write a Python program to create an ASCII waveform.', 'write-a-python-program-to-create-an-ascii-waveform', '<p>from time import sleep</p><p>from math import sin, cos, radians</p><p><br></p><p># increase 40 to get more wave</p><p>for n in range(1, 40):</p><p><span style=\"white-space:pre\">	</span>circle_1 = 50 * (1 + sin(radians(n*10)))</p><p><span style=\"white-space:pre\">	</span>circle_2 = 50 * (1 + cos(radians(n*10)))</p><p><span style=\"white-space:pre\">	</span>print(\"#\".center(int(circle_1)))</p><p><span style=\"white-space:pre\">	</span>print(\"*\".center(int(circle_2)))</p><p><span style=\"white-space:pre\">	</span>sleep(0.05)</p><p><span style=\"white-space:pre\">	</span></p>', 'from time import sleep\n\nfrom math import sin, cos, radians\n\n\n\n# increase 40 to get more wave\n\nfor n in range(1, 40):\n\n	circle_1 = 50 * (1 + sin(radians(n*10)))\n\n	circle_2 = 50 * (1 + cos(radians(n*10)))\n\n	print(\"#\".center(int(circle_1)))\n\n	print(\"*\".center(int(circle_2)))\n\n	sleep(0.05)\n\n	', 0, 0, NULL, '2023-04-05 21:28:23', '2023-04-05 21:28:23', 1),
