(371, 'Write a Python program to create a Pythagorean theorem calculator.', 'write-a-python-program-to-create-a-pythagorean-theorem-calculator', '<p>from math import sqrt</p><p><br></p><p>print(\'Pythagorean theorem calculator! Calculate your triangle sides.\')</p><p>print(\'Assume the sides are a, b, c and c is the hypotenuse (the side opposite the right angle\')</p><p>formula = input(\'Which side (a, b, c) do you wish to calculate? side&gt; \')</p><p><br></p><p>if formula == \'c\':</p><p><span style=\"white-space:pre\">	</span>side_a = int(input(\'Input the length of side a: \'))</p><p><span style=\"white-space:pre\">	</span>side_b = int(input(\'Input the length of side b: \'))</p><p><br></p><p><span style=\"white-space:pre\">	</span>side_c = sqrt(side_a * side_a + side_b * side_b)</p><p><span style=\"white-space:pre\">	</span></p><p><span style=\"white-space:pre\">	</span>print(\'The length of side c is: \' )</p><p><span style=\"white-space:pre\">	</span>print(side_c)</p><p><br></p><p>elif formula == \'a\':</p><p>&nbsp; &nbsp; side_b = int(input(\'Input the length of side b: \'))</p><p>&nbsp; &nbsp; side_c = int(input(\'Input the length of side c: \'))</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; side_a = sqrt((side_c * side_c) - (side_b * side_b))</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\'The length of side a is\' )</p><p>&nbsp; &nbsp; print(side_a)</p><p><br></p><p>elif formula == \'b\':</p><p>&nbsp; &nbsp; side_a = int(input(\'Input the length of side a: \'))</p><p>&nbsp; &nbsp; side_b = int(input(\'Input the length of side c: \'))</p><p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; side_c = sqrt(side_c * side_c - side_a * side_a)</p><p>&nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; print(\'The length of side b is\')</p><p>&nbsp; &nbsp; print(side_c)</p><p><br></p><p>else:</p><p><span style=\"white-space:pre\">	</span>print(\'Please select a side between a, b, c\')</p><p><span style=\"white-space:pre\">	</span></p>', 'from math import sqrt\n\n\n\nprint(\'Pythagorean theorem calculator! Calculate your triangle sides.\')\n\nprint(\'Assume the sides are a, b, c and c is the hypotenuse (the side opposite the right angle\')\n\nformula = input(\'Which side (a, b, c) do you wish to calculate? side> \')\n\n\n\nif formula == \'c\':\n\n	side_a = int(input(\'Input the length of side a: \'))\n\n	side_b = int(input(\'Input the length of side b: \'))\n\n\n\n	side_c = sqrt(side_a * side_a + side_b * side_b)\n\n	\n\n	print(\'The length of side c is: \' )\n\n	print(side_c)\n\n\n\nelif formula == \'a\':\n\n    side_b = int(input(\'Input the length of side b: \'))\n\n    side_c = int(input(\'Input the length of side c: \'))\n\n    \n\n    side_a = sqrt((side_c * side_c) - (side_b * side_b))\n\n    \n\n    print(\'The length of side a is\' )\n\n    print(side_a)\n\n\n\nelif formula == \'b\':\n\n    side_a = int(input(\'Input the length of side a: \'))\n\n    side_b = int(input(\'Input the length of side c: \'))\n\n        \n\n    side_c = sqrt(side_c * side_c - side_a * side_a)\n\n    \n\n    print(\'The length of side b is\')\n\n    print(side_c)\n\n\n\nelse:\n\n	print(\'Please select a side between a, b, c\')\n\n	', 0, 1, NULL, '2023-04-05 21:31:30', '2023-04-05 21:31:30', 1),
(372, 'Write a Python function to round up a number to specified digits.', 'write-a-python-function-to-round-up-a-number-to-specified-digits', '<p>import math</p><p><br></p><p>def roundup(a, digits=0):</p><p>&nbsp; &nbsp; n = 10**-digits</p><p>&nbsp; &nbsp; return round(math.ceil(a / n) * n, digits)</p><p><br></p><p>x = 123.01247</p><p>print(\"Original&nbsp; Number: \",x)</p><p>print(roundup(x, 0))</p><p>print(roundup(x, 1))</p><p>print(roundup(x, 2))</p><p>print(roundup(x, 3))</p><div><br></div>', 'import math\n\n\n\ndef roundup(a, digits=0):\n\n    n = 10**-digits\n\n    return round(math.ceil(a / n) * n, digits)\n\n\n\nx = 123.01247\n\nprint(\"Original  Number: \",x)\n\nprint(roundup(x, 0))\n\nprint(roundup(x, 1))\n\nprint(roundup(x, 2))\n\nprint(roundup(x, 3))\n\n', 0, 0, NULL, '2023-04-05 21:32:26', '2023-04-05 21:32:26', 1),
