(374, 'Write a Python program to reverse a range.', 'write-a-python-program-to-reverse-a-range', '<p>#https://gist.github.com/fcicq/ddb746042150b4e959e6</p><p>def reversed_range(start, stop=None, step=1):</p><p>&nbsp; if stop is None:<span style=\"white-space:pre\">	</span></p><p>&nbsp; &nbsp; return range(start - step, -step, -step)</p><p>&nbsp; else:</p><p>&nbsp; &nbsp; new_start = stop - ((stop-start-1) % step + 1)</p><p>&nbsp; &nbsp; new_end = new_start - (stop-start+step-1) // step * step</p><p>&nbsp; &nbsp; if (stop - start) % step == 0 and step &lt; 0: new_start -= step</p><p>&nbsp; &nbsp; return range(new_start, new_end, -step)</p><p><br></p><p>print(reversed_range(1, 10, 2))</p><p><br></p><p>print(reversed_range(1, 5, 1))</p><div><br></div>', '#https://gist.github.com/fcicq/ddb746042150b4e959e6\n\ndef reversed_range(start, stop=None, step=1):\n\n  if stop is None:	\n\n    return range(start - step, -step, -step)\n\n  else:\n\n    new_start = stop - ((stop-start-1) % step + 1)\n\n    new_end = new_start - (stop-start+step-1) // step * step\n\n    if (stop - start) % step == 0 and step < 0: new_start -= step\n\n    return range(new_start, new_end, -step)\n\n\n\nprint(reversed_range(1, 10, 2))\n\n\n\nprint(reversed_range(1, 5, 1))', 0, 0, NULL, '2023-04-05 21:34:18', '2023-04-05 21:34:18', 1),
(375, 'Write a Python program to create a range for floating numbers.', 'write-a-python-program-to-create-a-range-for-floating-numbers', '<p>#https://gist.github.com/axelpale/3e780ebdde4d99cbb69ffe8b1eada92c</p><p>def frange(x, y, jump=1.0):</p><p>&nbsp; &nbsp; i = 0.0</p><p>&nbsp; &nbsp; x = float(x)&nbsp; # Prevent yielding integers.</p><p>&nbsp; &nbsp; y = float(y)&nbsp; # Comparison converts y to float every time otherwise.</p><p>&nbsp; &nbsp; x0 = x</p><p>&nbsp; &nbsp; epsilon = jump / 2.0</p><p>&nbsp; &nbsp; yield x&nbsp; # yield always first value</p><p>&nbsp; &nbsp; while x + epsilon &lt; y:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; i += 1.0</p><p>&nbsp; &nbsp; &nbsp; &nbsp; x = x0 + i * jump</p><p>&nbsp; &nbsp; &nbsp; &nbsp; yield x</p><p><br></p><p>print(list(frange(0.0, 1.0, 0.1)))</p><div><br></div>', '#https://gist.github.com/axelpale/3e780ebdde4d99cbb69ffe8b1eada92c\n\ndef frange(x, y, jump=1.0):\n\n    i = 0.0\n\n    x = float(x)  # Prevent yielding integers.\n\n    y = float(y)  # Comparison converts y to float every time otherwise.\n\n    x0 = x\n\n    epsilon = jump / 2.0\n\n    yield x  # yield always first value\n\n    while x + epsilon < y:\n\n        i += 1.0\n\n        x = x0 + i * jump\n\n        yield x\n\n\n\nprint(list(frange(0.0, 1.0, 0.1)))\n\n', 0, 0, NULL, '2023-04-05 21:34:54', '2023-04-05 21:34:54', 1),
