(394, 'Write a Python program that accepts an integer number with distinct digits and displays the next number containing only distinct digits.', 'write-a-python-program-that-accepts-an-integer-number-with-distinct-digits-and-displays-the-next-number-containing-only-distinct-digits', '<p>def test(n):</p><p><span style=\"white-space:pre\">	</span>n += 1</p><p><span style=\"white-space:pre\">	</span>if len(set(str(n))) == len(str(n)):</p><p><span style=\"white-space:pre\">		</span>return n</p><p><span style=\"white-space:pre\">	</span>return test(n)</p><p>n = 12345</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><p>n = 99999</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><p>n = 1</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><p>n = 100</p><p>print(\"Original number:\", n)</p><p>print(\"Next number containing only distinct digits!\")&nbsp;</p><p>print(test(n))</p><div><br></div>', 'def test(n):\n\n	n += 1\n\n	if len(set(str(n))) == len(str(n)):\n\n		return n\n\n	return test(n)\n\nn = 12345\n\nprint(\"Original number:\", n)\n\nprint(\"Next number containing only distinct digits!\") \n\nprint(test(n))\n\nn = 99999\n\nprint(\"Original number:\", n)\n\nprint(\"Next number containing only distinct digits!\") \n\nprint(test(n))\n\nn = 1\n\nprint(\"Original number:\", n)\n\nprint(\"Next number containing only distinct digits!\") \n\nprint(test(n))\n\nn = 100\n\nprint(\"Original number:\", n)\n\nprint(\"Next number containing only distinct digits!\") \n\nprint(test(n))\n\n', 0, 0, NULL, '2023-04-05 22:52:09', '2023-04-05 22:52:09', 1),
(395, 'Write a Python program that checks whether the absolute difference between two consecutive digits is two or not. Return true otherwise false.', 'write-a-python-program-that-checks-whether-the-absolute-difference-between-two-consecutive-digits-is-two-or-not-return-true-otherwise-false', '<p>def test(n):</p><p>&nbsp; &nbsp; return all(abs(int(x) - int(y)) == 2 for x, y in zip(str(n), str(n)[1:]))</p><p>&nbsp; &nbsp; &nbsp;</p><p>n = 666</p><p>print(\"Original number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><p>n = 3579</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><p>n = 2468</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><p>n = 20420</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")</p><p>print(test(n))</p><div><br></div>', 'def test(n):\n\n    return all(abs(int(x) - int(y)) == 2 for x, y in zip(str(n), str(n)[1:]))\n\n     \n\nn = 666\n\nprint(\"Original number:\", n)\n\nprint(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")\n\nprint(test(n))\n\nn = 3579\n\nprint(\"\\nOriginal number:\", n)\n\nprint(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")\n\nprint(test(n))\n\nn = 2468\n\nprint(\"\\nOriginal number:\", n)\n\nprint(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")\n\nprint(test(n))\n\nn = 20420\n\nprint(\"\\nOriginal number:\", n)\n\nprint(\"Is absolute difference between two consecutive digits of the said number is 2 or not?\")\n\nprint(test(n))\n\n', 0, 0, NULL, '2023-04-05 22:53:44', '2023-04-05 22:53:44', 1),
(396, 'Write a Python program that takes an integer and rearranges the digits to create two maximum and minimum numbers.', 'write-a-python-program-that-takes-an-integer-and-rearranges-the-digits-to-create-two-maximum-and-minimum-numbers', '<p>def test(n):</p><p>&nbsp; &nbsp; temp = []</p><p>&nbsp; &nbsp; for e in str(n):</p><p>&nbsp; &nbsp; &nbsp; &nbsp; temp.append(e)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; max_num = \"\".join(sorted(temp)[::-1])</p><p>&nbsp; &nbsp; &nbsp; &nbsp; min_num = \"\".join(sorted(temp))&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; return int(max_num), int(min_num)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>n = 1254</p><p>print(\"Original number:\", n)</p><p>print(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")</p><p>print(\"Maximum and Minimum Numbers:\",test(n))</p><p>n = 6</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")</p><p>print(\"Maximum and Minimum Numbers:\",test(n))</p><p>n = 1000</p><p>print(\"\\nOriginal number:\", n)</p><p>print(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")</p><p>print(\"Maximum and Minimum Numbers:\",test(n))</p><div><br></div>', 'def test(n):\n\n    temp = []\n\n    for e in str(n):\n\n        temp.append(e)\n\n        max_num = \"\".join(sorted(temp)[::-1])\n\n        min_num = \"\".join(sorted(temp))        \n\n    return int(max_num), int(min_num)\n\n                                      \n\nn = 1254\n\nprint(\"Original number:\", n)\n\nprint(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")\n\nprint(\"Maximum and Minimum Numbers:\",test(n))\n\nn = 6\n\nprint(\"\\nOriginal number:\", n)\n\nprint(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")\n\nprint(\"Maximum and Minimum Numbers:\",test(n))\n\nn = 1000\n\nprint(\"\\nOriginal number:\", n)\n\nprint(\"Rearrange the digits of the said number to get Maximum and Minimum numbers:\")\n\nprint(\"Maximum and Minimum Numbers:\",test(n))\n\n', 0, 0, NULL, '2023-04-05 22:54:49', '2023-04-05 22:54:49', 1),
(397, 'Write a Python program to calculate the sum of all prime numbers in a given list of positive integers.', 'write-a-python-program-to-calculate-the-sum-of-all-prime-numbers-in-a-given-list-of-positive-integers', '<p>def test(nums):</p><p>&nbsp; &nbsp; if len(nums) &gt; 0:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; return sum(list(filter(lambda x: (x &gt; 1 and all(x % y != 0 for y in range(2, x))), nums)))&nbsp;</p><p>&nbsp; &nbsp; return \"Empty list!\"</p><p><br></p><p>nums = [1, 3, 4, 7, 9]</p><p>print(\"Original list:\")</p><p>print(nums)</p><p>print(\"Sum of all prime numbers in the said list of numbers:\")</p><p>print(test(nums))</p><p>nums = []</p><p>print(\"\\nOriginal list:\")</p><p>print(nums)</p><p>print(\"Sum of all prime numbers in the said list of numbers:\")</p><p>print(test(nums))</p><p>nums = [11, 37, 444]</p><p>print(\"\\nOriginal list:\")</p><p>print(nums)</p><p>print(\"Sum of all prime numbers in the said list of numbers:\")</p><p>print(test(nums))</p>', 'def test(nums):\n\n    if len(nums) > 0:\n\n        return sum(list(filter(lambda x: (x > 1 and all(x % y != 0 for y in range(2, x))), nums))) \n\n    return \"Empty list!\"\n\n\n\nnums = [1, 3, 4, 7, 9]\n\nprint(\"Original list:\")\n\nprint(nums)\n\nprint(\"Sum of all prime numbers in the said list of numbers:\")\n\nprint(test(nums))\n\nnums = []\n\nprint(\"\\nOriginal list:\")\n\nprint(nums)\n\nprint(\"Sum of all prime numbers in the said list of numbers:\")\n\nprint(test(nums))\n\nnums = [11, 37, 444]\n\nprint(\"\\nOriginal list:\")\n\nprint(nums)\n\nprint(\"Sum of all prime numbers in the said list of numbers:\")\n\nprint(test(nums))', 0, 0, NULL, '2023-04-05 22:56:29', '2023-05-24 09:56:01', 1),
